import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as g,c as o,a as s,b as t,f as i,d as l,e}from"./app-a01d9b9c.js";const n={},h=t("p",null,[t("strong",null,"石頭山"),i("，海拔214公尺，位於新竹縣新埔鎮內立里，山頂有土地調查局圖根點基石，樹林間無展望。")],-1),c=t("p",null,[t("strong",null,"水坑廣山"),i("，海拔235公尺，亦位於新竹縣新埔鎮內立里，山頂有土地調查局圖根點基石，另有頭前溪保護區界石，無展望。")],-1),d=e('<p>登山日期： 2010/01/30</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>台68線快速道路下二重埔交流道往芎林方向，過芎林富林路直走文德路(115縣道)往新埔，遇關埔路(竹16鄉道，有紅綠燈)後右轉往關西，約700公尺後於關埔路203巷右轉(路口有寶石托兒所招牌)，至三元宮前廣場停車。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→三元宮(取右健康步道，陡上水泥路)<br> →路障(水泥路變得更陡更滑)<br> →石頭山登山口(水泥路高點，續行應為健康步道，取左上切泥土路，樹上有路條及指示牌)<br> →私人別墅住宅(借道住宅內草皮，至水塔處再切回山徑)<br> →高壓電塔(電塔過後為較寬好走的保線路)<br> →叉路(左為保線路，取右入竹林，芒草夾道)<br> →鐵絲網(鐵絲網外為水泥路)<br> →接水泥路(鐵絲網結束)<br> →叉路(左下為寬廣枕木步道，不知往何處，續直行)<br> →叉路(直下為水泥路，循路條取右入樹林)<br> →駁坎遺跡<br> →空曠斜坡地(續往前行便可發現路條)<br> →<strong>石頭山</strong>(無展望，續行下切，土質鬆滑需小心)<br> →民宅(內立xxx號，忘了，無人居住)<br> →接內立產業道路(大魯閣幹#52電桿)<br> →回到關埔路(取左)<br> →回到三元宮</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>登山時間：約2小時40分鐘</p><p>高低落差：約130公尺</p></div>',7),b={class:"hint-container note"},m=t("p",{class:"hint-container-title"},"後記",-1),_={href:"http://www.keepon.com.tw/ActiveSite/Article/One.asp?ArticleID=30948",target:"_blank",rel:"noopener noreferrer"},u=t("p",null,"如果只為一探石頭山基點，可開車由關埔路直接上內立產業道路(路口有「劉三觀光果園」招牌)，於大魯閣幹#52電桿三叉路右轉至民宅附近停車，約十幾分鐘可到達。",-1),j=e('<h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>關埔路203巷<br><img src="https://1013399.github.io/image-6/319/144779711_l.jpg" alt="關埔路203巷" loading="lazy"></p><p>三元宮(旁邊即為寶石托兒所)<br><img src="https://1013399.github.io/image-6/319/144779715_l.jpg" alt="三元宮 寶石托兒所" loading="lazy"></p><p>路障<br><img src="https://1013399.github.io/image-6/319/144779725_l.jpg" alt="石頭山" loading="lazy"></p><p>石頭山登山口<br><img src="https://1013399.github.io/image-6/319/144779756_l.jpg" alt="石頭山登山口" loading="lazy"></p><p>依藍天隊路條行<br><img src="https://1013399.github.io/image-6/319/144779770_l.jpg" alt="石頭山" loading="lazy"></p><p>地上藤蔓多<br><img src="https://1013399.github.io/image-6/319/144779782_l.jpg" alt="石頭山" loading="lazy"></p><p>私人別墅住宅<br><img src="https://1013399.github.io/image-6/319/144779832_l.jpg" alt="石頭山" loading="lazy"></p><p>借道住宅內草皮，至水塔處再切回山徑<br><img src="https://1013399.github.io/image-6/319/144779838_l.jpg" alt="石頭山" loading="lazy"></p><p>高壓電塔<br><img src="https://1013399.github.io/image-6/319/144779873_l.jpg" alt="石頭山" loading="lazy"></p><p>寬廣好走的保線路<br><img src="https://1013399.github.io/image-6/319/144779882_l.jpg" alt="石頭山" loading="lazy"></p><p>叉路(左為保線路，取右入竹林)<br><img src="https://1013399.github.io/image-6/319/144779888_l.jpg" alt="石頭山" loading="lazy"></p><p>芒草夾道<br><img src="https://1013399.github.io/image-6/319/144779897_l.jpg" alt="石頭山 芒草" loading="lazy"></p><p>鐵絲網(鐵絲網外為水泥路)<br><img src="https://1013399.github.io/image-6/319/144779960_l.jpg" alt="石頭山" loading="lazy"></p><p>叉路左下寬廣枕木步道，不知往何處<br><img src="https://1013399.github.io/image-6/319/144779967_l.jpg" alt="石頭山" loading="lazy"></p><p>叉路(直下為水泥路，循路條取右入樹林)<br><img src="https://1013399.github.io/image-6/319/144779975_l.jpg" alt="石頭山" loading="lazy"></p><p>駁坎遺跡<br><img src="https://1013399.github.io/image-6/319/144779983_l.jpg" alt="石頭山 駁坎遺跡" loading="lazy"></p><p>空曠斜坡地<br><img src="https://1013399.github.io/image-6/319/144780083_l.jpg" alt="石頭山" loading="lazy"></p><p>循路條再切入樹林<br><img src="https://1013399.github.io/image-6/319/144780148_l.jpg" alt="石頭山" loading="lazy"></p><p>石頭山土地調查局圖根點<br><img src="https://1013399.github.io/image-6/319/144780153_l.jpg" alt="石頭山" loading="lazy"></p><p>民宅(內立xxx號，忘了，無人居住)<br><img src="https://1013399.github.io/image-6/319/144780157_l.jpg" alt="石頭山" loading="lazy"></p><p>內立產業道路路旁櫻花盛開<br><img src="https://1013399.github.io/image-6/319/144780165_l.jpg" alt="石頭山 櫻花盛開" loading="lazy"></p><p>再拍一張櫻花照<br><img src="https://1013399.github.io/image-6/319/144780252_l.jpg" alt="石頭山 櫻花盛開" loading="lazy"></p><p>回到關埔路<br><img src="https://1013399.github.io/image-6/319/144779704_l.jpg" alt="石頭山" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',25),y={href:"http://www.keepon.com.tw/ActiveSite/Article/One.asp?ArticleID=6516",target:"_blank",rel:"noopener noreferrer"},z=t("br",null,null,-1),f=t("img",{src:"https://1013399.github.io/image-6/319/144780312_l.jpg",alt:"石頭山 參考地圖",loading:"lazy"},null,-1);function x(k,v){const a=p("ExternalLinkIcon");return g(),o("div",null,[h,c,s(" more "),d,t("div",b,[m,t("p",null,[i("石頭山及水坑廣山算是新埔冷門郊山，從登山補給站得知，藍天隊於不久前，花了兩個工作天辛苦踏查砍草開路("),t("a",_,[i("原文連結"),l(a)]),i(")，並已補綁登山路條，心想趁草未長之際前往走走，未料山路崎嶇、藤蔓絆腳，小心走來非常辛苦，本想石頭山連走水坑廣山，從石頭山下切至產業道路後，便不想再走了，改循內立產業道路回三元宮，繞個O型。")]),u]),j,t("p",null,[i("原圖連結→"),t("a",y,[i("藍天圖集(四049)"),l(a)]),z,f])])}const N=r(n,[["render",x],["__file","post-319-2010-02-02.html.vue"]]);export{N as default};
