import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as r,a as o,b as i,f as a,d as e,e as s}from"./app-a01d9b9c.js";const h={},b=i("p",null,[i("strong",null,"金敏子山"),a("，又稱白石鵠山，海拔630公尺，位於台北縣三峽鎮金圳里，山頂有標示牌但無基石，視野極佳。")],-1),n=i("p",null,[i("strong",null,"詩朗山"),a("，又稱西崙山，海拔667公尺，位於台北縣三峽鎮金圳里，竹林中有台灣省政府圖根補點基石，無視野。")],-1),m=s('<p><strong>塞口坑山</strong>，海拔755公尺，位於台北縣三峽鎮金圳里，山頂有標示牌但無基石，視野佳。</p><p><strong>內金敏山</strong>，又稱大豹山，海拔594公尺，位於台北縣三峽鎮金圳里，山頂有總督府圖根補點基石，視野稍可。</p><p>登山日期： 2010/01/17</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>北二高下三鶯交流道往三峽方向，走台3線至大埔(25.5K)後左轉開往三民方向(台7乙線)，途經五寮尖登山口、湊合橋(左北114鄉道往滿月圓)，至台7乙4.5K處左轉過金圳橋進入金敏產業道路，約1.4km後(途經插角國小金敏分校)過橋三叉路轉彎處附近停車，右有「大豹幹16-14」電桿。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→停車處(取右水泥路上行)<br> →叉路(取右水泥路，旁有方形大水塔)<br> →金敏子山登山口(產道轉彎處，有路條，一開始就陡上)<br> →廢棄老屋<br> →岩壁<br> →金敏子山下方巨岩壁(左轉沿著岩壁下方繞行)<br> →岩壁亂石區(岩壁有侵蝕現象，下方有小香爐)<br> →稜線叉路(左往觀景台，雜草多、不前往，取右往金敏子山)<br> →竹林產道叉路(產道往右下行往五寮，產道往左上行往詩朗山，先取右山徑往金敏子山)<br> →叉路(左右殊途同歸，取右行視野較佳)<br> →金敏子山(視野極佳，眺望五寮尖岩稜、詩朗山、塞口坑山等，原路折返)<br> →回到竹林產道叉路(續直走產道往詩朗山)<br> →詩朗山登山口(距竹林產道叉路約30公尺，取左入竹林)<br> →詩朗山(竹林中無視野)<br> →孟宗竹林空地(巧遇北縣野外育樂協會，一行二十餘人在此休息午餐)<br> →倒竹擋路(繞道而行，途中越過數個小山頭)<br> →鞍部叉路(右往金平山，續直行)<br> →塞口坑山(視野不錯，也可眺望五寮尖岩稜，以望遠鏡可見有人正在爬岩稜)<br> →第一段垂降坡(拉繩攀樹而下)<br> →鞍部(左轉)<br> →第二段垂降坡<br> →急陡坡<br> →空地叉路(右下往插角，左下為下山方向，先直上往內金敏山)<br> →內金敏山(原路折返)<br> →回到空地叉路(取右下山)<br> →芒草區(草比人高)<br> →孟宗竹林區(發現路旁有一顆「文七一八」基石)<br> →出竹林(左有菜園，取右接產業道路，之後產道遇有叉路，大都取直行)<br> →金敏169之3號民宅(有狗狂吠，不予理會)<br> →金敏157號民宅<br> →叉路(右往金龍橋接北114鄉道，直行回停車處)<br> →回到停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>登山時間：約6.5小時(含休息、午餐)</p><p>高低落差：約560公尺</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>三叉路轉彎停車處<br><img src="https://1013399.github.io/image-6/320/143740634_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山 停車處" loading="lazy"></p><p>往登山口產道上仰望金敏子山<br><img src="https://1013399.github.io/image-6/320/143740637_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>往登山口產道上仰望詩朗山<br><img src="https://1013399.github.io/image-6/320/143740641_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>金敏子山登山口<br><img src="https://1013399.github.io/image-6/320/143740645_l.jpg" alt="金敏子山登山口" loading="lazy"></p><p>廢棄老屋<br><img src="https://1013399.github.io/image-6/320/143740647_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>一路陡上<br><img src="https://1013399.github.io/image-6/320/143740663_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>開始出現岩壁<br><img src="https://1013399.github.io/image-6/320/143740667_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>金敏子山下方巨岩壁<br><img src="https://1013399.github.io/image-6/320/143740671_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>岩壁侵蝕現象，下方有小香爐<br><img src="https://1013399.github.io/image-6/320/143740673_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>巨岩壁還真長<br><img src="https://1013399.github.io/image-6/320/143740676_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>稜線叉路(左往觀景台，取右往金敏子山)<br><img src="https://1013399.github.io/image-6/320/143740679_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>竹林產道叉路<br><img src="https://1013399.github.io/image-6/320/143740681_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>金敏子山<br><img src="https://1013399.github.io/image-6/320/143740684_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>金敏子山眺望五寮尖<br><img src="https://1013399.github.io/image-6/320/143740686_l.jpg" alt="五寮尖" loading="lazy"></p><p>詩朗山登山口<br><img src="https://1013399.github.io/image-6/320/143740692_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山 詩朗山登山口" loading="lazy"></p><p>詩朗山<br><img src="https://1013399.github.io/image-6/320/143740696_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>孟宗竹林空地(北縣野外育樂協會在此休息午餐)<br><img src="https://1013399.github.io/image-6/320/143740700_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>倒竹擋路<br><img src="https://1013399.github.io/image-6/320/143740703_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>塞口坑山<br><img src="https://1013399.github.io/image-6/320/143740706_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>急陡坡<br><img src="https://1013399.github.io/image-6/320/143740708_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>急陡坡(往回拍攝)<br><img src="https://1013399.github.io/image-6/320/143740711_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>空地叉路(直上往內金敏山)<br><img src="https://1013399.github.io/image-6/320/143740714_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>往內金敏山指示約5分鐘<br><img src="https://1013399.github.io/image-6/320/143740716_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>內金敏山總督府圖根補點基石<br><img src="https://1013399.github.io/image-6/320/143740719_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>內金敏山<br><img src="https://1013399.github.io/image-6/320/143740722_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>孟宗竹林區<br><img src="https://1013399.github.io/image-6/320/143740730_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>文七一八基石<br><img src="https://1013399.github.io/image-6/320/143740791_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>出竹林，取右接產業道路<br><img src="https://1013399.github.io/image-6/320/143740798_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>回望塞口坑山<br><img src="https://1013399.github.io/image-6/320/143740827_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>金敏169之3號<br><img src="https://1013399.github.io/image-6/320/143740839_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>產道鐵門<br><img src="https://1013399.github.io/image-6/320/143740850_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>金敏157號<br><img src="https://1013399.github.io/image-6/320/143740923_l.jpg" alt="金敏157號" loading="lazy"></p><p>產道叉路(取直行)<br><img src="https://1013399.github.io/image-6/320/143740931_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><p>產道叉路(取直行)<br><img src="https://1013399.github.io/image-6/320/143740632_l.jpg" alt="金敏子山、詩朗山、塞口坑山、內金敏山" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',45),c={href:"http://ms1.ctjh.tpc.edu.tw/~uank3/070211chat.htm",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-6/320/143741318_l.jpg",alt:"金敏子山、詩朗山、塞口坑山、內金敏山 參考地圖",loading:"lazy"},null,-1);function u(j,y){const t=l("ExternalLinkIcon");return p(),r("div",null,[b,n,o(" more "),m,i("p",null,[a("原圖連結→"),i("a",c,[a("江翠登山社(圓開的個人登山旅遊活動)"),e(t)]),d,_])])}const x=g(h,[["render",u],["__file","post-320-2010-01-20.html.vue"]]);export{x as default};
