import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as e,c as p,a as g,b as a,f as i,d as o,e as n}from"./app-7f62a6ff.js";const s={},h=a("p",null,[a("strong",null,"三段崎古道"),i("，位於新竹縣新埔鎮寶石里與芎林鄉下山村之間，為昔日農民運送柑橘等農作物所走出來的一條道路，入口為新埔鎮寶石里的三段崎站站牌附近(115縣道32.5K，文德路二段339巷)，另一端入口為芎林鄉下山村39號金鑑堂(竹22鄉道2K附近)，有三段崎涼亭及古道標示牌。")],-1),b=n('<p>2007/10/20曾走過三段崎古道，未拍照也未記錄，走完文林古道後，便順著123縣道往竹北方向，再次造訪三段崎古道，以留下記錄。</p><p><strong>石頭坑山</strong>，海拔237公尺，位於新竹縣芎林鄉三段崎古道，有兩顆基石但在不同處，第一顆為三等三角點六號基石，第二顆為二等三角點#1029基石(旁還有台灣省政府頭前溪水源保護區界石)，兩處相距約200公尺，且樹林中無視野。</p><p>登山日期： 2010/01/09</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>123縣道芎林往竹北方向，見到「歡迎光臨上山社區」標示後(旁有「百年古道(三段崎)」指標)，右轉竹22鄉道，約行駛2K即可看見「三段崎涼亭」(此處也有「百年古道(三段崎)」指標)，停車於路旁較寬處。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→三段崎涼亭<br> →叉路(直往私人果園，取左續行)<br> →十字叉路(直往私人果園，右為三段崎古道，先取左往觀景涼亭)<br> →觀景涼亭(從三段崎涼亭至十字路口為陡上產業道路)<br> →回十字叉路(直行三段崎古道，先為石版路後轉為鵝卵石步道)<br> →A叉路(直行為三段崎古道，取右往石頭坑山)<br> →石頭坑山(三等三角點六號基石，再往前行)<br> →石頭坑山(二等三角點#1029基石，原路折返)<br> →回到A叉路(取右續行)<br> →B叉路(取右續行三段崎古道，起初不知左往何處，事後才知可環狀由左繞回)<br> →小蓄水池<br> →小木橋<br> →芎林上山64號民宅(已無人居住)<br> →接產業道路(左往芎林，右往新埔另一入口，往右走約10公尺發現文山步道入口)<br> →文山步道入口(試走一段後折回)<br> →回產業道路(又發現左側竹林有路條明顯路徑，依方向判斷應可接回三段崎古道，決定走走)<br> →回到B叉路(原來可以環狀繞回，但此路徑較多拉繩陡坡，取右續回程)<br> →回到A叉路(續直行)<br> →回到觀景涼亭十字叉路(取左下產業道路)<br> →回到停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>登山時間：約1.5小時(含休息)</p><p>高低落差：約160公尺</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>三段崎涼亭<br><img src="https://1013399.github.io/image-6/323/143157257_l.jpg" alt="三段崎涼亭" loading="lazy"></p><p>陡上產業道路<br><img src="https://1013399.github.io/image-6/323/143157314_l.jpg" alt="三段崎古道、石頭坑山" loading="lazy"></p><p>叉路取左(有指示)<br><img src="https://1013399.github.io/image-6/323/143157323_l.jpg" alt="三段崎古道、石頭坑山" loading="lazy"></p><p>觀景涼亭十字叉路<br><img src="https://1013399.github.io/image-6/323/143157335_l.jpg" alt="三段崎古道、石頭坑山" loading="lazy"></p><p>觀景涼亭<br><img src="https://1013399.github.io/image-6/323/143157343_l.jpg" alt="三段崎古道、石頭坑山" loading="lazy"></p><p>觀景涼亭展望<br><img src="https://1013399.github.io/image-6/323/143157346_l.jpg" alt="三段崎古道、石頭坑山" loading="lazy"></p><p>古道竹林路<br><img src="https://1013399.github.io/image-6/323/143157435_l.jpg" alt="三段崎古道 竹林路" loading="lazy"></p><p>A叉路(取右往石頭坑山)<br><img src="https://1013399.github.io/image-6/323/143157446_l.jpg" alt="三段崎古道、石頭坑山" loading="lazy"></p><p>石頭坑山三等三角點六號<br><img src="https://1013399.github.io/image-6/323/143157491_l.jpg" alt="石頭坑山三等三角點六號" loading="lazy"></p><p>石頭坑山二等三角點#1029<br><img src="https://1013399.github.io/image-6/323/143157497_l.jpg" alt="石頭坑山二等三角點#1029" loading="lazy"></p><p>台灣省政府頭前溪水源保護區界石<br><img src="https://1013399.github.io/image-6/323/143157500_l.jpg" alt="台灣省政府頭前溪水源保護區界石" loading="lazy"></p><p>B叉路(取右續行，事後才知可環狀由左繞回)<br><img src="https://1013399.github.io/image-6/323/143157504_l.jpg" alt="三段崎古道、石頭坑山" loading="lazy"></p><p>小蓄水池<br><img src="https://1013399.github.io/image-6/323/143157509_l.jpg" alt="三段崎古道、石頭坑山" loading="lazy"></p><p>小木橋<br><img src="https://1013399.github.io/image-6/323/143157515_l.jpg" alt="三段崎古道、石頭坑山" loading="lazy"></p><p>芎林上山64號民宅<br><img src="https://1013399.github.io/image-6/323/143157522_l.jpg" alt="三段崎古道、石頭坑山" loading="lazy"></p><p>接產業道路<br><img src="https://1013399.github.io/image-6/323/143157524_l.jpg" alt="三段崎古道、石頭坑山" loading="lazy"></p><p>文山步道入口<br><img src="https://1013399.github.io/image-6/323/143157532_l.jpg" alt="三段崎古道、石頭坑山" loading="lazy"></p><p>左側竹林明顯路徑(有路條)<br><img src="https://1013399.github.io/image-6/323/143157543_l.jpg" alt="三段崎古道、石頭坑山" loading="lazy"></p><p>拉繩陡坡<br><img src="https://1013399.github.io/image-6/323/143157547_l.jpg" alt="三段崎古道、石頭坑山" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',30),c={href:"http://www.yougoipay.com/kenny/w597/index.htm",target:"_blank",rel:"noopener noreferrer"},d=a("figure",null,[a("img",{src:"https://1013399.github.io/image-6/323/143157587_l.jpg",alt:"三段崎古道、石頭坑山 參考地圖",tabindex:"0",loading:"lazy"}),a("figcaption",null,"三段崎古道、石頭坑山 參考地圖")],-1);function m(_,u){const t=l("ExternalLinkIcon");return e(),p("div",null,[h,g(" more "),b,a("p",null,[i("(依"),a("a",c,[i("蕭郎-獨步山林間"),o(t)]),i("修改，點圖進相簿可放大)")]),d])}const z=r(s,[["render",m],["__file","post-323-2010-01-12.html.vue"]]);export{z as default};
