import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as r,a as o,b as t,f as a,d as e,e as s}from"./app-2f66486a.js";const n={},h=t("p",null,[t("strong",null,"文林古道"),a("，位於新竹縣芎林鄉文林閣後方的紙寮窩山區，古道全長約3公里，2007/10/14曾經造訪過，但未拍照，聽說最近剛整修完成，決定再來走走。全程較陡需拉繩路段，已舖設木棧道取代，讓步道走來更為舒適，且沿途每200公尺設立里程木樁(鄧雨賢紀念公園後方產業道路起至文山路322巷產業道路，全長2439公尺)，並於里程木樁約1200公尺處(往新埔叉路)附近，設置一座休憩涼亭，這條古道實為親子假日同遊的好去處。")],-1),b=s('<p><strong>糖塔山</strong>，海拔135公尺，又稱紙寮窩山，位於文林古道路旁有顆國王宮界石。</p><p><strong>高梘頭山</strong>，海拔175公尺，又稱九芎林角山，山頂有聯勤製圖隊的三等三角點內補#025基石。</p><p>登山日期： 2010/01/09</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>北二高下竹林交流道後右轉沿【120】往芎林，遇【115】縣道右轉往新埔(左往【台68】快速道路)，遇【123】縣道右轉文山路，至【123:3.2K】處即可看見「文林自然生態古道」的指標，左前方為芎林國小，右後方為派出所，登山口位於文山路322巷內，可於文山路旁停車(芎林國小入口旁圖書館有小型停車場也可停車)。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→文山路322巷<br> →文林古道入口(路口新設有紙寮窩登山口指示牌及文林古道導覽圖)<br> →叉路(新設指示牌，左往唐塔山，取直文林古道)<br> →叉路(左下方有一涼亭，前往一探)<br> →文山亭(拍張照後折回續行文林古道)<br> →<strong>糖塔山</strong>(紙寮窩山，國王宮界石就在路旁)<br> →叉路(右往蓄水池，拍張照後折回續行文林古道)<br> →涼亭叉路(直往新埔，取右過涼亭)<br> →紙寮窩叉路(右下回文林古道登山口，取左上)<br> →<strong>高梘頭山</strong><br> →接產業道路(里程0K處，路口也新設有文林古道導覽圖，取左續行)<br> →社區住宅<br> →鄧雨賢紀念公園<br> →接文山路(取右回)<br> →文昌廟(文林閣)<br> →回到停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>登山時間：約1.5小時(含休息)</p><p>高低落差：約80公尺</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>文山路322巷<br><img src="https://1013399.github.io/image-6/324/143156095_l.jpg" alt="文山路322巷" loading="lazy"></p><p>文林古道入口<br><img src="https://1013399.github.io/image-6/324/143156103_l.jpg" alt="文林古道入口" loading="lazy"></p><p>新設文林古道導覽圖<br><img src="https://1013399.github.io/image-6/324/143156108_l.jpg" alt="文林古道導覽圖" loading="lazy"></p><p>新設里程木樁<br><img src="https://1013399.github.io/image-6/324/143156115_l.jpg" alt="文林古道、糖塔山、高梘頭山" loading="lazy"></p><p>叉路(左往唐塔山，取直文林古道)<br><img src="https://1013399.github.io/image-6/324/143156123_l.jpg" alt="文林古道、糖塔山、高梘頭山" loading="lazy"></p><p>叉路(左下方有一涼亭)<br><img src="https://1013399.github.io/image-6/324/143156145_l.jpg" alt="文林古道、糖塔山、高梘頭山" loading="lazy"></p><p>文山亭<br><img src="https://1013399.github.io/image-6/324/143156131_l.jpg" alt="文山亭" loading="lazy"></p><p>糖塔山國王宮界石<br><img src="https://1013399.github.io/image-6/324/143156150_l.jpg" alt="糖塔山國王宮界石" loading="lazy"></p><p>糖塔山(紙寮窩山)<br><img src="https://1013399.github.io/image-6/324/143156209_l.jpg" alt="糖塔山(紙寮窩山)" loading="lazy"></p><p>陡下木棧道(第一處木棧道)<br><img src="https://1013399.github.io/image-6/324/143156212_l.jpg" alt="文林古道、糖塔山、高梘頭山" loading="lazy"></p><p>竹林步道(地上滿是楓葉)<br><img src="https://1013399.github.io/image-6/324/143156219_l.jpg" alt="文林古道、糖塔山、高梘頭山" loading="lazy"></p><p>里程木樁1400公尺處(第二處木棧道)<br><img src="https://1013399.github.io/image-6/324/143156223_l.jpg" alt="文林古道、糖塔山、高梘頭山" loading="lazy"></p><p>蓄水池叉路<br><img src="https://1013399.github.io/image-6/324/143156230_l.jpg" alt="文林古道、糖塔山、高梘頭山" loading="lazy"></p><p>蓄水池<br><img src="https://1013399.github.io/image-6/324/143156225_l.jpg" alt="文林古道、糖塔山、高梘頭山" loading="lazy"></p><p>里程木樁1200公尺處<br><img src="https://1013399.github.io/image-6/324/143156309_l.jpg" alt="文林古道、糖塔山、高梘頭山" loading="lazy"></p><p>新設涼亭(叉路直往新埔)<br><img src="https://1013399.github.io/image-6/324/143156313_l.jpg" alt="文林古道、糖塔山、高梘頭山" loading="lazy"></p><p>第三處木棧道<br><img src="https://1013399.github.io/image-6/324/143156320_l.jpg" alt="文林古道、糖塔山、高梘頭山" loading="lazy"></p><p>第四處木棧道<br><img src="https://1013399.github.io/image-6/324/143156326_l.jpg" alt="文林古道、糖塔山、高梘頭山" loading="lazy"></p><p>紙寮窩叉路(右下回文林古道登山口，左上至山頭即為高梘頭山)<br><img src="https://1013399.github.io/image-6/324/143156331_l.jpg" alt="文林古道、糖塔山、高梘頭山" loading="lazy"></p><p>高梘頭山聯勤製圖隊三等三角點<br><img src="https://1013399.github.io/image-6/324/143156548_l.jpg" alt="文林古道、糖塔山、高梘頭山" loading="lazy"></p><p>枕木步道(亦為新設)<br><img src="https://1013399.github.io/image-6/324/143156432_l.jpg" alt="文林古道、糖塔山、高梘頭山" loading="lazy"></p><p>接產業道路(里程0K處，取左續行)<br><img src="https://1013399.github.io/image-6/324/143156441_l.jpg" alt="文林古道、糖塔山、高梘頭山" loading="lazy"></p><p>社區住宅<br><img src="https://1013399.github.io/image-6/324/143156448_l.jpg" alt="文林古道、糖塔山、高梘頭山" loading="lazy"></p><p>由此進入鄧雨賢紀念公園<br><img src="https://1013399.github.io/image-6/324/143156451_l.jpg" alt="文林古道、糖塔山、高梘頭山" loading="lazy"></p><p>鄧雨賢紀念公園<br><img src="https://1013399.github.io/image-6/324/143156456_l.jpg" alt="鄧雨賢紀念公園" loading="lazy"></p><p>鄧雨賢生平介紹<br><img src="https://1013399.github.io/image-6/324/143156462_l.jpg" alt="鄧雨賢生平介紹" loading="lazy"></p><p>鄧雨賢紀念公園落成誌<br><img src="https://1013399.github.io/image-6/324/143156469_l.jpg" alt="鄧雨賢紀念公園落成誌" loading="lazy"></p><p>文昌廟(文林閣)<br><img src="https://1013399.github.io/image-6/324/143156091_l.jpg" alt="文昌廟(文林閣)" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',39),m={href:"http://www.tonyhuang39.com/tony0433/tony0433.html",target:"_blank",rel:"noopener noreferrer"},c=t("br",null,null,-1),d=t("img",{src:"https://1013399.github.io/image-6/324/143156573_l.jpg",alt:"文林古道、糖塔山、高梘頭山 參考地圖",loading:"lazy"},null,-1);function _(u,y){const i=l("ExternalLinkIcon");return p(),r("div",null,[h,o(" more "),b,t("p",null,[a("原圖連結→"),t("a",m,[a("Tony的自然人文旅記"),e(i)]),c,d])])}const f=g(n,[["render",_],["__file","post-324-2010-01-11.html.vue"]]);export{f as default};
