import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as r,a as o,b as i,f as t,d as s,e}from"./app-a9847bbb.js";const h={},n=i("p",null,[i("strong",null,"天上山"),t("，本名內坡山，海拔430公尺，有顆三等三角點1099號，位於土城鄉大案村嶺頂，是三峽、新店、土城三市鎮之界山；山頂有一片小岩堆，面積不大卻為本山稜中之最高點，視界極為遼闊，北觀全台北盆地，東面是新店，西面為三峽街道及大漢溪沿線，南面即是北插天山系列。俯瞰附近山峰均皆俯首稱臣，有種唯我獨尊之勢，故又名皇帝山。該山名列台灣小百岳#018。")],-1),b=e('<p><strong>太極嶺</strong>，海拔約330公尺，位於土城天上山系山麓，民國81年由曾進發老先生及友人發現後共同開發花園廣場，曾進發老先生精通天文地理，認為此點活動能增年益壽長命百歲。</p><p><strong>五城山</strong>，海拔418公尺，無基點，是天上山與文筆山之間的中途站，有座名為「英雄練武嶺」的休息亭，因此也叫「練武嶺」。</p><p><strong>將軍嶺</strong>，海拔約310公尺，位於土城清水山區，有一大鐵皮屋，備有桌子、洗手台，供山友休息、用餐。土城市公所於數年前植栽山櫻花樹苗，有整片成林的櫻花可供觀賞。</p><p><strong>文筆山</strong>，又名安坑山，海拔375公尺，台北縣測量基點，與五城山共同環抱山中湖，與天上山相接，土城市公所舖設良好之步道，可由土城或新店起登。</p><p>走走日期：2010/01/01</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>北二高北上於三峽下交流道往土城方向，至土城中央路三段(台3線)，過永寧捷運站後叉路右轉金城路一段(北88)，於金城路二段遇明德路右轉，不久右轉青雲路，過德霖技術學院後叉路右轉石門路，石門路行駛約1Km叉路取左(石門路單數號門牌，路較寬)，路底即為山中湖，山中湖門口有告示牌停車收費100元，於大門外路旁停車。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→山中湖(右側有「清水登山步道」石碑及「自得亭」、左側立有登山步道圖)<br> →叉路(過涼亭第一個叉路，直行可往文筆山，取右往太極嶺)<br> →鞍部十字叉路(前方可下石門路，左往五城山，先取右往太極嶺)<br> →<strong>太極嶺</strong>(直行好漢坡往石門路，原路折返)<br> →回到十字叉路(直行往五城山)<br> →一城休閒小站<br> →亮心園<br> →觀景嶺(開始陡上)<br> →山城登山隊休閒亭(繼續陡上)<br> →<strong>五城山練武嶺</strong>(左往文筆山，取右往天上山)<br> →叉路(左為繞腰路，右上小山頭後接回繞腰路，取左)<br> →叉路(右上小山頭後接回繞腰路，續直行)<br> →叉路(右上雜草多，續直行)<br> →叉路(右後往石門路，續直行)<br> →叉路(右下往甘露公園，續直行)<br> →<strong>天上山</strong>(展望佳，旁有望月亭)<br> →原路折返<br> →回到五城山(直行往文筆山)<br> →樂山亭<br> →樂山攀岩訓練場(暫不開放)<br> →叉路(左下往山中湖，續直行)<br> →十字叉路(左下往山中湖，右往新店，續直行)<br> →<strong>將軍嶺</strong><br> →叉路(右往伴吾社區，取左)<br> →菜園叉路(直往山中湖，取右往文筆山)<br> →叉路(左下往山中湖，取右上)<br> →<strong>文筆山</strong>(續直行下山)<br> →文景亭<br> →叉路(右往龍德寺，續直行)<br> →回到山中湖</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>登山時間：約3.5小時(含休息)</p><p>高低落差：約180公尺</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>清水登山步道石碑及自得亭<br><img src="https://1013399.github.io/image-6/325/142538666_l.jpg" alt="清水登山步道石碑及自得亭" loading="lazy"></p><p>叉路(取右往太極嶺)<br><img src="https://1013399.github.io/image-6/325/142538684_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>山中湖<br><img src="https://1013399.github.io/image-6/325/142538693_l.jpg" alt="山中湖 太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>鞍部十字叉路(取右往太極嶺)<br><img src="https://1013399.github.io/image-6/325/142538776_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>太極嶺<br><img src="https://1013399.github.io/image-6/325/142538786_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>太極嶺休息平台<br><img src="https://1013399.github.io/image-6/325/142538802_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>太極嶺上有2棵紅楓<br><img src="https://1013399.github.io/image-6/325/142538820_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>太極嶺展望<br><img src="https://1013399.github.io/image-6/325/142538829_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>太極嶺仰望五城山<br><img src="https://1013399.github.io/image-6/325/142538839_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>一城休閒小站<br><img src="https://1013399.github.io/image-6/325/142538849_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>亮心園<br><img src="https://1013399.github.io/image-6/325/142538856_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>觀景嶺<br><img src="https://1013399.github.io/image-6/325/142538866_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>開始陡上<br><img src="https://1013399.github.io/image-6/325/142538885_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>山城登山隊休閒亭<br><img src="https://1013399.github.io/image-6/325/142538905_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>繼續陡上<br><img src="https://1013399.github.io/image-6/325/142538924_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>五城山練武嶺<br><img src="https://1013399.github.io/image-6/325/142539091_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>叉路(取左繞腰路)<br><img src="https://1013399.github.io/image-6/325/142539180_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>叉路(直行繞腰路)<br><img src="https://1013399.github.io/image-6/325/142539191_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>叉路(續直行)<br><img src="https://1013399.github.io/image-6/325/142539201_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>石門路叉路(續直行)<br><img src="https://1013399.github.io/image-6/325/142539209_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>甘露公園叉路(續直行)<br><img src="https://1013399.github.io/image-6/325/142539218_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>甘露公園叉路指示牌<br><img src="https://1013399.github.io/image-6/325/142539313_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>天上山<br><img src="https://1013399.github.io/image-6/325/142539321_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>天上山三等三角點1099號<br><img src="https://1013399.github.io/image-6/325/142539324_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>天上山眺望台北盆地(四張相片組合，點圖放大)<br><img src="https://1013399.github.io/image-6/325/142761952_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>天上山往南眺望北插天山系<br><img src="https://1013399.github.io/image-6/325/142539345_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>樂山亭(五城山旁)<br><img src="https://1013399.github.io/image-6/325/142539349_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>樂山攀岩訓練場<br><img src="https://1013399.github.io/image-6/325/142539432_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>山中湖叉路(續直行)<br><img src="https://1013399.github.io/image-6/325/142539442_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>十字叉路(左下往山中湖，右往新店，續直行)<br><img src="https://1013399.github.io/image-6/325/142539564_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>將軍嶺<br><img src="https://1013399.github.io/image-6/325/142539578_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>伴吾社區叉路(取左)<br><img src="https://1013399.github.io/image-6/325/142539589_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>菜園叉路(取右往文筆山)<br><img src="https://1013399.github.io/image-6/325/142539658_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>山中湖叉路(取右上)<br><img src="https://1013399.github.io/image-6/325/142539671_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>文筆山<br><img src="https://1013399.github.io/image-6/325/142539683_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>文筆山台北縣測量基點<br><img src="https://1013399.github.io/image-6/325/142539694_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>文景亭<br><img src="https://1013399.github.io/image-6/325/142539705_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><p>龍德寺叉路(續直行)<br><img src="https://1013399.github.io/image-6/325/142539713_l.jpg" alt="太極嶺、五城山、天上山、將軍嶺、文筆山" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',51),m={href:"http://www.keepon.com.tw/ActiveSite/Article/One.asp?ArticleID=983",target:"_blank",rel:"noopener noreferrer"},c=i("br",null,null,-1),d=i("img",{src:"https://1013399.github.io/image-6/325/142540023_l.jpg",alt:"太極嶺、五城山、天上山、將軍嶺、文筆山",loading:"lazy"},null,-1),_=i("h2",{id:"文筆山參考地圖",tabindex:"-1"},[i("a",{class:"header-anchor",href:"#文筆山參考地圖","aria-hidden":"true"},"#"),t(" 文筆山參考地圖")],-1),u=i("p",null,"(上圖不夠詳細)",-1),j=i("p",null,[t("拍攝自山中湖入口左側導覽圖"),i("br"),i("img",{src:"https://1013399.github.io/image-6/325/142538675_l.jpg",alt:"太極嶺、五城山、天上山、將軍嶺、文筆山 參考地圖",loading:"lazy"})],-1);function y(z,f){const a=l("ExternalLinkIcon");return p(),r("div",null,[n,o(" more "),b,i("p",null,[t("原圖連結→"),i("a",m,[t("登山補給站"),s(a)]),c,d]),_,u,j])}const v=g(h,[["render",y],["__file","post-325-2010-01-04.html.vue"]]);export{v as default};
