import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{r as o,o as n,c as l,b as t,e as a,d as i,a as p,f as s}from"./app-39f3f472.js";const h={},g=t("p",null,[t("strong",null,"迎龍古道"),a("，位於南庄鄉南富村文武宮後山，曾是迎神賽會舞龍拚陣頭的要道，古道荒廢了七十年，近年經社區民眾賦予新生命，整修成為休閒健身道路，沿途雜木蓊鬱，林相豐富，鄉公所近年已多次在該處舉辦大型活動，也是每年賞油桐花的健身古道。")],-1),c={href:"http://nanzhuang.hotravel.com.tw/travelwalk_02.php",target:"_blank",rel:"noopener noreferrer"},d=t("p",null,[t("strong",null,"大鵬山"),a("，海拔約259公尺(自行測得)，位於苗栗縣南庄鄉南富村文武宮後方，文武宮擴建後，設有步道通往山頂，山頂有座2層樓涼亭，涼亭頂上立有一尊孫悟空像，涼亭後方有顆無名基石。")],-1),b={href:"http://sixstar.cca.gov.tw/frontsite/cms/viewlistAction.do?method=doViewRead&contentId=NzUwNzI=&menuId=NjA1&subMenuId=NjA1",target:"_blank",rel:"noopener noreferrer"},m=s('<p>走走日期：2009/12/27</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>上午走完啞嗎頂古道，於南庄老街享用客家美食後，時間尚早，曾聽說南庄大南埔有條「迎龍古道」，便想順道走走，自南庄縣道124線往三灣方向行駛，過龍門隧道後，立即左轉苗20鄉道，至白雲寺牌樓見有迎龍古道指示便往左轉，但卻一路往象山方向前進，越走越覺不對勁，經詢問當地居民才知走錯路，好心的當地居民騎機車在前帶路，一路領我們到達文武宮，還說這就是迎龍古道，原來迎龍古道全程皆為水泥產業道路，沒想到竟然「以車代步」走完了迎龍古道。<br>     於文武宮停車場停妥車後，見廟後方山頂有一涼亭，據當地居民稱廟後有條步道可通往山頂，便決定走走，大約15分鐘便攻頂，在此驚見6隻藍腹鷳，真是不虛此行。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→文武宮停車場<br> →文武宮(自廟左水泥小產道往上行)<br> →接鵝卵石步道<br> →觀景小平台叉路(左回文武宮右側，取右續上)<br> →山頂2層樓孫悟空涼亭(賞景後續行，驚見6隻藍腹鷳，涼亭後方發現一顆無名基石)<br> →高壓電塔(續行有路往農家，但無地圖，不敢貿然造訪，原路回)<br> →回到涼亭<br> →回到觀景小平台(取直行，從廟右側出)<br> →回到文武宮<br> →回到文武宮停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>登山時間：約30分鐘</p><p>高低落差：約110公尺</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>文武宮牌樓<br><img src="https://1013399.github.io/image-6/328/141920799_l.jpg" alt="文武宮牌樓" loading="lazy"></p><p>南富社區(大南埔)導覽圖<br><img src="https://1013399.github.io/image-6/328/141920786_l.jpg" alt="南富社區(大南埔)導覽圖" loading="lazy"></p><p>叉路(右往迎龍古道，左往文武宮)<br><img src="https://1013399.github.io/image-6/328/141920813_l.jpg" alt="迎龍古道、大鵬山" loading="lazy"></p><p>由此水泥小產道上行<br><img src="https://1013399.github.io/image-6/328/141920779_l.jpg" alt="迎龍古道、大鵬山" loading="lazy"></p><p>鵝卵石步道<br><img src="https://1013399.github.io/image-6/328/141920825_l.jpg" alt="迎龍古道、大鵬山" loading="lazy"></p><p>觀景小平台<br><img src="https://1013399.github.io/image-6/328/141920831_l.jpg" alt="迎龍古道、大鵬山" loading="lazy"></p><p>步道一景<br><img src="https://1013399.github.io/image-6/328/141920905_l.jpg" alt="迎龍古道、大鵬山" loading="lazy"></p><p>山頂2層樓孫悟空涼亭<br><img src="https://1013399.github.io/image-6/328/141920908_l.jpg" alt="迎龍古道、大鵬山 山頂2層樓孫悟空涼亭" loading="lazy"></p><p>涼亭後方無名基石<br><img src="https://1013399.github.io/image-6/328/141920912_l.jpg" alt="迎龍古道、大鵬山" loading="lazy"></p><p>涼亭展望<br><img src="https://1013399.github.io/image-6/328/141920915_l.jpg" alt="迎龍古道、大鵬山" loading="lazy"></p><p>高壓電塔<br><img src="https://1013399.github.io/image-6/328/141920920_l.jpg" alt="迎龍古道、大鵬山 高壓電塔" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>(事後自行繪製，點圖可放大)</p><figure><img src="https://1013399.github.io/image-6/328/142129796_l.jpg" alt="迎龍古道、大鵬山 參考地圖" tabindex="0" loading="lazy"><figcaption>迎龍古道、大鵬山 參考地圖</figcaption></figure>',22);function _(u,f){const e=o("ExternalLinkIcon");return n(),l("div",null,[g,t("p",null,[a("(相關介紹："),t("a",c,[a("南庄民宿旅遊網"),i(e)]),a(")")]),p(" more "),d,t("p",null,[a("(相關介紹："),t("a",b,[a("行政院文化建設委員會台灣社區通"),i(e)]),a(")")]),m])}const x=r(h,[["render",_],["__file","post-328-2009-12-30.html.vue"]]);export{x as default};
