import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o,c as r,a as e,b as g,f as i,d as p,e as s}from"./app-a01d9b9c.js";const h={},n=g("p",null,[g("strong",null,"獵人古道"),i("，位於南投縣信義鄉望美村，全名為「馬努多斯獵人古道」，為昔日布農族勇士上山打獵的路徑，由望鄉部落古道口起登，途經石板涼亭、梅園、望美山，到望高寮瞭望台，若體力允許，可再續行至瓊山。獵人古道沿途可眺望玉山主峰、玉山北峰、八通關大山及郡大山、望鄉山，風景秀麗，望鄉部落也種植許多梅樹、櫻花樹，花季期間來訪，更有一番美景。")],-1),m=g("p",null,[g("strong",null,"望美山"),i("，海拔1426公尺，位於南投縣信義鄉獵人古道，立有省政府圖根補點(另一面刻有聯勤三等三角點內補363號)，山頂西向展望佳，可眺望羅娜山、俯瞰阿里不動溪。")],-1),b=g("p",null,[g("strong",null,"瓊山"),i("，海拔1846公尺，亦位於南投縣信義鄉望美村，立有聯勤三等三角點內補364號(另一面刻有新中橫19號)，山頂腹地廣，但為樹林所圍繞，無展望。")],-1),c=s('<p>走走日期：2017/02/19</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>搭乘遊覽車從新竹科學園區管理局出發，上國道1號轉國道3號下名間交流道往集集方向(台16線)，經過名間、集集、水里接台21線往信義鄉，過了同富國小後，於台21線102.6Km右轉往望鄉部落，約再行1.2公里到達望鄉牌樓下車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→望鄉部落牌樓，下車<br><img src="https://1013399.github.io/image-1/33/1154264291_l.jpg" alt="IMAG0927.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→整裝後出發，進入望鄉部落<br><img src="https://1013399.github.io/image-1/33/1154266041_l.jpg" alt="IMAG0929.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→叉路，取右續行<br><img src="https://1013399.github.io/image-1/33/1154265270_l.jpg" alt="IMAG0931.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→獵人古道入口，旁有導覽解說牌<br><img src="https://1013399.github.io/image-1/33/1154266613_l.jpg" alt="IMAG0933.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→取左上石階進入獵人古道<br><img src="https://1013399.github.io/image-1/33/1154267426_l.jpg" alt="IMAG0935.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→叉路，直行不知去向，取右上<br><img src="https://1013399.github.io/image-1/33/1154267610_l.jpg" alt="IMAG0936.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→沿途種植許多梅樹、櫻花樹<br><img src="https://1013399.github.io/image-1/33/1154267612_l.jpg" alt="IMAG0937.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→石板屋涼亭，休息<br><img src="https://1013399.github.io/image-1/33/1154266510_l.jpg" alt="IMAG0938.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→眺望望鄉山<br><img src="https://1013399.github.io/image-1/33/1154264294_l.jpg" alt="IMAG0939.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→續行，開始之字型上坡<br><img src="https://1013399.github.io/image-1/33/1154266721_l.jpg" alt="IMAG0940-43.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→廢棄平台<br><img src="https://1013399.github.io/image-1/33/1154266823_l.jpg" alt="IMAG0944.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→依舊之字型上坡，漸漸趨陡<br><img src="https://1013399.github.io/image-1/33/1154266512_l.jpg" alt="IMAG0946-50.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→上至稜線，休息<br><img src="https://1013399.github.io/image-1/33/1154264296_l.jpg" alt="IMAG0951.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→續行，接水泥路，取左行<br><img src="https://1013399.github.io/image-1/33/1154267613_l.jpg" alt="IMAG0952.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→叉路，直行不知去向，依獵人古道指標取右上<br><img src="https://1013399.github.io/image-1/33/1154265271_l.jpg" alt="IMAG0953.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→經過茶園<br><img src="https://1013399.github.io/image-1/33/1154266724_l.jpg" alt="IMAG0955.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→叉路，直行不知去向，取左上<br><img src="https://1013399.github.io/image-1/33/1154267429_l.jpg" alt="IMAG0956.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→一路往上，稍後進入竹林<br><img src="https://1013399.github.io/image-1/33/1154264297_l.jpg" alt="IMAG0958-59.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→叉路，右有茶園及工寮，前方烤肉區有木桌椅，休息<br><img src="https://1013399.github.io/image-1/33/1154266825_l.jpg" alt="IMAG0960.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→續行，此段為較為平緩的山徑<br><img src="https://1013399.github.io/image-1/33/1154266917_l.jpg" alt="IMAG0962-65.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→路旁一根台大(國)923水泥樁<br><img src="https://1013399.github.io/image-1/33/1154266826_l.jpg" alt="IMAG0966.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→到達望美山山頂<br><img src="https://1013399.github.io/image-1/33/1154264389_l.jpg" alt="IMAG0967.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→望美山，省政府圖根補點<br><img src="https://1013399.github.io/image-1/33/1154267616_l.jpg" alt="IMAG0968-76.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→續行，接上瞭望台林道，左下往台21線，續直行<br><img src="https://1013399.github.io/image-1/33/1154266515_l.jpg" alt="IMAG0977.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→約行10公尺遇叉路，左為林道，右為山徑，殊途同歸，續行林道<br><img src="https://1013399.github.io/image-1/33/1154265372_l.jpg" alt="IMAG0978.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→到達望高寮瞭望台，林道的終點(6.1Km)，在此休息、泡茶、午餐<br><img src="https://1013399.github.io/image-1/33/1154266725_l.jpg" alt="IMAG0979.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→瞭望台後方柵欄有顆台大37號基石<br><img src="https://1013399.github.io/image-1/33/1154266619_l.jpg" alt="IMAG0982-83.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→登上瞭望台3樓，眺望玉山(逆光拍攝)<br><img src="https://1013399.github.io/image-1/33/1154267430_l.jpg" alt="IMAG0984.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→旁有解說牌可對照群山<br><img src="https://1013399.github.io/image-1/33/1154266045_l.jpg" alt="IMAG0985.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→休息後續行下坡往瓊山<br><img src="https://1013399.github.io/image-1/33/1154264393_l.jpg" alt="IMAG0987.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→下抵鞍部<br><img src="https://1013399.github.io/image-1/33/1154267431_l.jpg" alt="IMAG0988.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→續行，先上後下<br><img src="https://1013399.github.io/image-1/33/1154264394_l.jpg" alt="IMAG0989-90.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→叉路，右可往阿里不動大峽谷，續直行<br><img src="https://1013399.github.io/image-1/33/1154266920_l.jpg" alt="IMAG0990A.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→開始陡升坡，往瓊山需爬三段陡坡<br><img src="https://1013399.github.io/image-1/33/1154266046_l.jpg" alt="IMAG0991-92.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→經過台大606(又似605)水泥樁<br><img src="https://1013399.github.io/image-1/33/1154265374_l.jpg" alt="IMAG0993.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→第二段陡坡<br><img src="https://1013399.github.io/image-1/33/1154267704_l.jpg" alt="IMAG0994-97.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"><br><img src="https://1013399.github.io/image-1/33/1154267328_l.jpg" alt="IMAG0998-01.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→陡坡結束，轉下坡<br><img src="https://1013399.github.io/image-1/33/1154264395_l.jpg" alt="IMAG1002.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→下抵瓊山鞍部<br><img src="https://1013399.github.io/image-1/33/1154264396_l.jpg" alt="IMAG1003.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→最後一段陡坡<br><img src="https://1013399.github.io/image-1/33/1154264990_l.jpg" alt="IMAG1004-07.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→陡坡結束，穿越芒草<br><img src="https://1013399.github.io/image-1/33/1154264991_l.jpg" alt="IMAG1008.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→到達瓊山<br><img src="https://1013399.github.io/image-1/33/1154266829_l.jpg" alt="IMAG1009.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→瓊山，聯勤三等三角點內補364號(另一面刻新中橫19號)<br><img src="https://1013399.github.io/image-1/33/1154264992_l.jpg" alt="IMAG1011.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→休息拍照後，原路折返，回到瓊山鞍部<br><img src="https://1013399.github.io/image-1/33/1154266830_l.jpg" alt="IMAG1020.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→回到望高寮瞭望台<br><img src="https://1013399.github.io/image-1/33/1154264993_l.jpg" alt="IMAG1023.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→續行林道<br><img src="https://1013399.github.io/image-1/33/1154264994_l.jpg" alt="IMAG1024.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→叉路，直行山徑往望美山(稍早來時路)，取右改走林道下山<br><img src="https://1013399.github.io/image-1/33/1154266049_l.jpg" alt="IMAG1026.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→回道林道1K處<br><img src="https://1013399.github.io/image-1/33/1154266052_l.jpg" alt="IMAG1029.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→叉路，左往望鄉部落，取右續行<br><img src="https://1013399.github.io/image-1/33/1154266626_l.jpg" alt="IMAG1030.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><p>→回到台21線，上車<br><img src="https://1013399.github.io/image-1/33/1154267519_l.jpg" alt="IMAG1031.jpg - 南投信義獵人古道、望美山、瓊山" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約7.5小時(含休息、午餐)</p><p>高低落差：(不含上下起伏)<br> 望鄉部落牌樓 → 瓊山(上山) 約上升960公尺<br> 瓊山 → 台21線(下山) 約下降1100公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',56),j={href:"http://59-120-215-226.hinet-ip.hinet.net/(X(1)S(1xw5y3yk0h33kw5z3uommukh))/thread-e0fe702c-15d8-e411-93ec-000e04b74954.html",target:"_blank",rel:"noopener noreferrer"},d=g("figure",null,[g("img",{src:"https://1013399.github.io/image-1/33/1154267903_l.jpg",alt:"信義獵人古道、望美山、望高寮、瓊山-LEoN.png - 南投信義獵人古道、望美山、瓊山",tabindex:"0",loading:"lazy"}),g("figcaption",null,"信義獵人古道、望美山、望高寮、瓊山-LEoN.png - 南投信義獵人古道、望美山、瓊山")],-1),_=g("h2",{id:"落差圖",tabindex:"-1"},[g("a",{class:"header-anchor",href:"#落差圖","aria-hidden":"true"},"#"),i(" 落差圖")],-1),u={href:"http://blog.xuite.net/c220435988/123/374507394",target:"_blank",rel:"noopener noreferrer"},y=g("br",null,null,-1),z=g("img",{src:"https://1013399.github.io/image-1/33/1154266120_l.jpg",alt:"信義獵人古道、望美山、望高寮、瓊山落差圖-陳生.jpg - 南投信義獵人古道、望美山、瓊山",loading:"lazy"},null,-1);function I(A,G){const t=l("ExternalLinkIcon");return o(),r("div",null,[n,m,b,e(" more "),c,g("p",null,[i("原圖連結→"),g("a",j,[i("登山補給站-u9123511(LEoN)"),p(t)])]),d,_,g("p",null,[i("原圖連結→"),g("a",u,[i("陳生(登山記事)"),p(t)]),y,z])])}const x=a(h,[["render",I],["__file","post-33-2017-03-02.html.vue"]]);export{x as default};
