import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as r,c as p,a as e,b as i,f as a,d as o,e as s}from"./app-7f62a6ff.js";const h={},b=i("p",null,[i("strong",null,"福人登山步道"),a("，海拔約240公尺至360公尺左右，位於桃園縣楊梅鎮福人路旁，全線為原始的土石山徑，山坡內上下的交錯來回、走法多樣，叉路多、無標示且甚少登山路條，部份樹幹上有白漆箭頭標示，但似乎作用不大，建議首次來訪山友，採右上左下或左上右下大O型一圈，原則上遇叉路時，選擇雜草較少的一方即可。")],-1),n=s('<p>走走日期：2009/12/24</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>楊梅鎮台1線縱貫路於中山路125巷進入，接校前路(桃67)直行，過中山高後Y型叉路(正前方為7-11)，右轉桃71不久遇福人路左入，前進數十公尺處就可看到登山口，登山口旁有空地停車方便。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→登山口<br> →叉路(左右兩線分岔，採右上左下，取右)<br> →叉路(樹幹上有白漆箭頭往左，不予理會仍取右)<br> →第一處廢棄碉堡<br> →第二處廢棄碉堡(旁有叉路出福人路)<br> →第一座山<br> →叉路(左下可能是往好漢坡，取直行)<br> →排水山溝(陡下過山溝後再陡上)<br> →叉路(左右皆可，取右上後，雜草多，右側有水泥圍牆)<br> →第二座山(左右兩線會合)<br> →叉路(仍取右)<br> →叉路(左為正確路徑，右上雜草多且有繩索擋道，取右上一探)<br> →小山頭(應是步道最高點，樹上有一些舊登山條，但未見標示、尋無基點)<br> →原路退回繩索擋道的叉路(取直下)<br> →叉路(仍取右)<br> →叉路(先取右一探，雜草似乎剛被砍除)<br> →揚昇高爾夫球場(拍張照後退回，撿到兩顆高爾夫球)<br> →球場禁止進入警示牌<br> →叉路(左為回程路徑，取直行)<br> →拉繩叉路(左下接前一叉路往回程路徑，再取直行看看)<br> →竹林(前方無路，原路退回)<br> →回到拉繩叉路(取右下，步道開始一路上上下下，偶有陡坡)<br> →叉路(右方雜草較多，取左)<br> →水泥小橋<br> →叉路(右方應往老莊路，有新路條但雜草多，取左回)<br> →第一座山(與前者不同，不知為何有兩個第一座山)<br> →小水池<br> →回到左右兩線分岔叉路<br> →回到登山口</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>登山時間：右上左下O型一圈約2小時</p><p>高低落差：約120公尺</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>登山口<br><img src="https://1013399.github.io/image-6/330/141622899_l.jpg" alt="福人登山步道 登山口" loading="lazy"></p><p>叉路(左右兩線分岔點)<br><img src="https://1013399.github.io/image-6/330/141622905_l.jpg" alt="福人登山步道" loading="lazy"></p><p>叉路(取右)<br><img src="https://1013399.github.io/image-6/330/141622910_l.jpg" alt="福人登山步道" loading="lazy"></p><p>第一處廢棄碉堡<br><img src="https://1013399.github.io/image-6/330/141622914_l.jpg" alt="福人登山步道" loading="lazy"></p><p>第二處廢棄碉堡<br><img src="https://1013399.github.io/image-6/330/141622919_l.jpg" alt="福人登山步道" loading="lazy"></p><p>第一座山<br><img src="https://1013399.github.io/image-6/330/141622924_l.jpg" alt="福人登山步道" loading="lazy"></p><p>第一座山展望<br><img src="https://1013399.github.io/image-6/330/141622978_l.jpg" alt="福人登山步道 展望" loading="lazy"></p><p>右側可出福人路<br><img src="https://1013399.github.io/image-6/330/141623042_l.jpg" alt="福人登山步道" loading="lazy"></p><p>叉路(左下可能往好漢坡，取直行)<br><img src="https://1013399.github.io/image-6/330/141623068_l.jpg" alt="福人登山步道" loading="lazy"></p><p>陡下過山溝後再陡上<br><img src="https://1013399.github.io/image-6/330/141623074_l.jpg" alt="福人登山步道" loading="lazy"></p><p>排水山溝<br><img src="https://1013399.github.io/image-6/330/141623078_l.jpg" alt="福人登山步道 排水山溝" loading="lazy"></p><p>叉路(左右皆可，取右上)<br><img src="https://1013399.github.io/image-6/330/141623085_l.jpg" alt="福人登山步道" loading="lazy"></p><p>叉路(雜草多，右側有水泥圍牆)<br><img src="https://1013399.github.io/image-6/330/141623088_l.jpg" alt="福人登山步道" loading="lazy"></p><p>第二座山<br><img src="https://1013399.github.io/image-6/330/141623091_l.jpg" alt="福人登山步道" loading="lazy"></p><p>叉路(仍取右)<br><img src="https://1013399.github.io/image-6/330/141623140_l.jpg" alt="福人登山步道" loading="lazy"></p><p>叉路(左為正確路徑，取右上一探)<br><img src="https://1013399.github.io/image-6/330/141623148_l.jpg" alt="福人登山步道" loading="lazy"></p><p>小山頭(應是步道最高點，樹上有一些舊登山條)<br><img src="https://1013399.github.io/image-6/330/141623181_l.jpg" alt="福人登山步道" loading="lazy"></p><p>叉路(仍取右)<br><img src="https://1013399.github.io/image-6/330/141623185_l.jpg" alt="福人登山步道" loading="lazy"></p><p>叉路(先取右再回)<br><img src="https://1013399.github.io/image-6/330/141623186_l.jpg" alt="福人登山步道" loading="lazy"></p><p>揚昇高爾夫球場<br><img src="https://1013399.github.io/image-6/330/141623188_l.jpg" alt="揚昇高爾夫球場" loading="lazy"></p><p>球場禁止進入警示牌<br><img src="https://1013399.github.io/image-6/330/141623191_l.jpg" alt="福人登山步道" loading="lazy"></p><p>叉路(左為回程路徑，取直行)<br><img src="https://1013399.github.io/image-6/330/141623195_l.jpg" alt="福人登山步道" loading="lazy"></p><p>拉繩叉路(再取直行看看)<br><img src="https://1013399.github.io/image-6/330/141623199_l.jpg" alt="福人登山步道" loading="lazy"></p><p>竹林(前方無路，原路退回)<br><img src="https://1013399.github.io/image-6/330/141623205_l.jpg" alt="福人登山步道" loading="lazy"></p><p>叉路(右方雜草較多，取左)<br><img src="https://1013399.github.io/image-6/330/141623209_l.jpg" alt="福人登山步道" loading="lazy"></p><p>水泥小橋<br><img src="https://1013399.github.io/image-6/330/141623217_l.jpg" alt="福人登山步道" loading="lazy"></p><p>叉路(右方應往老莊路，有新路條但雜草多，取左回)<br><img src="https://1013399.github.io/image-6/330/141623225_l.jpg" alt="福人登山步道" loading="lazy"></p><p>聽說是穿山甲的巢穴<br><img src="https://1013399.github.io/image-6/330/141623236_l.jpg" alt="福人登山步道 穿山甲的巢穴" loading="lazy"></p><p>又一個第一座山<br><img src="https://1013399.github.io/image-6/330/141623240_l.jpg" alt="福人登山步道" loading="lazy"></p><p>小水池<br><img src="https://1013399.github.io/image-6/330/141622891_l.jpg" alt="福人登山步道 小水池" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',39),c={href:"http://www.keepon.com.tw/ActiveSite/Article/One.asp?ArticleID=24222",target:"_blank",rel:"noopener noreferrer"},m=i("br",null,null,-1),d=i("img",{src:"https://1013399.github.io/image-6/330/141623359_l.jpg",alt:"福人登山步道 參考地圖",loading:"lazy"},null,-1);function _(u,j){const t=g("ExternalLinkIcon");return r(),p("div",null,[b,e(" more "),n,i("p",null,[a("原圖連結→"),i("a",c,[a("藍天圖集"),o(t)]),m,d])])}const f=l(h,[["render",_],["__file","post-330-2009-12-25.html.vue"]]);export{f as default};
