import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r,o as l,c as p,a as o,b as t,f as i,d as s,e}from"./app-c5577115.js";const n={},h=t("p",null,[t("strong",null,"大艽芎古道"),i("是因長滿艽芎而得名，但現在大艽芎古道上是一條桐花密佈的古徑。是早年大溪通往復興鄉、羅浮等內山地區的重要便道，也是農作物運送下山到市集販賣的農道。")],-1),b=t("p",null,[t("strong",null,"林嵩壽山"),i("，海拔460公尺，有顆三井基石，位於山路右側，樹林間無展望。")],-1),d=t("p",null,[t("strong",null,"東竹篙厝山"),i("，海拔450公尺，也有顆三井基石，位於山路左側，樹林間亦無展望。")],-1),c=e('<p><strong>內湳溝山</strong>，海拔480公尺，管林所水泥柱，位於路徑左側5公尺處的樹林內，無展望。</p><p><strong>頭寮山</strong>，海拔493公尺，又名湳仔溝山或湳溝山，山頂有總督府圖根點基石，可眺望石門水庫阿姆坪二層坪附近群山。眺望枕頭山，遠處可見南插天山，更遠處可見夫婦山。俯瞰龍潭地區。俯瞰大溪地區。</p><p><strong>湳仔溝古道</strong>，又名<strong>總督府登山步道</strong>，進出口分別在台七線7.5K百吉隧道慈湖端及過百吉隧道後右轉承恩路約0.5公里一家「客家麵」小吃店後。</p><p><strong>湳仔溝堤步道</strong>，為湳仔溝野溪整治工程，將堤岸整修為步道，沿途有解說，長約1800M。</p><p>走走日期：2009/12/05</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><h3 id="北上較近路線" tabindex="-1"><a class="header-anchor" href="#北上較近路線" aria-hidden="true">#</a> 北上較近路線</h3><p>(會經過石門水庫收費亭，08:00~18:00小車需收費80元)</p><p>國道3號下關西交流道往關西方向，接台3線(中豐路)往龍潭方向，於龍源路(台3乙線)右轉往石門水庫，遇民治路(桃63)右轉，過石門水庫收費亭，沿環湖道路(懷德路)行駛至承恩路叉路左轉，不久抵達湳仔溝停車場停車。</p><h3 id="實際行駛路線" tabindex="-1"><a class="header-anchor" href="#實際行駛路線" aria-hidden="true">#</a> 實際行駛路線</h3><p>國道3號下關西交流道往關西方向，接台3線(中豐路)往龍潭方向，再接台4線(石門路)往大溪方向，過溪洲大橋後左轉康莊路往大溪，至安和路紅綠燈右轉不久接台7線往復興鄉，經慈湖、百吉隧道，過百吉隧道後往右後方接承恩路，直走約1.5K便可達湳仔溝停車場。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→湳仔溝停車場(大艽芎古道入口就在對面)<br> →<strong>大艽芎古道</strong>入口<br> →山神廟，設有解說牌<br> →稜線五叉路(左往溪洲山、直下往水中土地公、右前方往慈湖頭寮，右方枕木階梯上至休息小平台，取右後方續行)<br> →叉路(左右兩路線會於林嵩壽山會合，左線會經過#75高壓電塔，右線會經過長修亭、神仙亭，取右線續行)<br> →觀景平台<br> →長修亭(有吊床、廁所)<br> →神仙亭(旁有一造型特殊小廟)<br> →觀景平台(展望不錯，眺望南插天山、夫婦山、那結山、外鳥嘴山)<br> →<strong>林嵩壽山</strong>(山頂無標示、無展望，旁有三井基石，此處也是左右兩路線會合處)<br> →<strong>東竹篙厝山</strong>(山頂無標示、無展望，也有三井基石)<br> →<strong>內湳溝山</strong>(管林所水泥基石，位於路徑左側5公尺處的樹林內)<br> →叉路(左下往慈湖停車場，取右續行)<br> →廢班舍(似為寢室)<br> →廢崗哨<br> →<strong>頭寮山</strong>(總督府圖根點，展望不錯，拍照休息後拉繩陡下)<br> →叉路(前往百吉林蔭步道、洞口站，取右<strong>湳仔溝古道</strong>)<br> →路徑右轉90度極陡枕木階梯<br> →桂竹林<br> →湳仔溝古道口(接環湖公路內湳溝站)<br> →客家麵店(午餐)<br> →取右公路回<br> →叉路(左方有小橋，前往一探)<br> →筍林橋(湳仔溝7號，過橋後右方無路徑，退回公路)<br> →叉路(左方又有小橋，再前往一探)<br> →懷風橋(湳仔溝6號，過橋後取右，捨公路改走<strong>湳仔溝堤步道</strong>)<br> →鳳儀橋(湳仔溝5號)<br> →親水橋(湳仔溝4號)<br> →石公橋(湳仔溝3號，路徑轉為木棧道)<br> →花語橋(湳仔溝2號，木棧道多處腐朽，小心通過)<br> →泉源橋(湳仔溝1號，步道終點，取右過橋即為湳仔溝停車場)<br> →湳仔溝停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>登山時間：O型一圈約3小時</p><p>高低落差：約240公尺</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>湳仔溝停車場<br><img src="https://1013399.github.io/image-6/332/140067538_l.jpg" alt="湳仔溝停車場" loading="lazy"></p><p>大艽芎古道入口<br><img src="https://1013399.github.io/image-6/332/140067597_l.jpg" alt="大艽芎古道入口" loading="lazy"></p><p>古道枕木階梯<br><img src="https://1013399.github.io/image-6/332/140067703_l.jpg" alt="大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山" loading="lazy"></p><p>山神廟<br><img src="https://1013399.github.io/image-6/332/140067711_l.jpg" alt="大艽芎古道 山神廟" loading="lazy"></p><p>山神廟解說牌<br><img src="https://1013399.github.io/image-6/332/140067715_l.jpg" alt="大艽芎古道 山神廟解說牌" loading="lazy"></p><p>稜線五叉路<br><img src="https://1013399.github.io/image-6/332/140067724_l.jpg" alt="大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山" loading="lazy"></p><p>休息小平台<br><img src="https://1013399.github.io/image-6/332/140067729_l.jpg" alt="大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山" loading="lazy"></p><p>叉路(左右兩路線會於林嵩壽山會合，取右線續行)<br><img src="https://1013399.github.io/image-6/332/140067735_l.jpg" alt="大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山" loading="lazy"></p><p>長修亭<br><img src="https://1013399.github.io/image-6/332/140067768_l.jpg" alt="大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山" loading="lazy"></p><p>神仙亭<br><img src="https://1013399.github.io/image-6/332/140067775_l.jpg" alt="大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山" loading="lazy"></p><p>造型特殊小廟<br><img src="https://1013399.github.io/image-6/332/140067781_l.jpg" alt="大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山" loading="lazy"></p><p>觀景平台<br><img src="https://1013399.github.io/image-6/332/140067785_l.jpg" alt="大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山" loading="lazy"></p><p>觀景平台展望<br><img src="https://1013399.github.io/image-6/332/140067795_l.jpg" alt="大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山" loading="lazy"></p><p>林嵩壽山<br><img src="https://1013399.github.io/image-6/332/140067855_l.jpg" alt="林嵩壽山" loading="lazy"></p><p>東竹篙厝山<br><img src="https://1013399.github.io/image-6/332/140067856_l.jpg" alt="東竹篙厝山" loading="lazy"></p><p>內湳溝山<br><img src="https://1013399.github.io/image-6/332/140067874_l.jpg" alt="內湳溝山" loading="lazy"></p><p>內湳溝山管林所水泥基石<br><img src="https://1013399.github.io/image-6/332/140067875_l.jpg" alt="內湳溝山管林所水泥基石" loading="lazy"></p><p>叉路(左下往慈湖停車場，取右續行)<br><img src="https://1013399.github.io/image-6/332/140067879_l.jpg" alt="大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山" loading="lazy"></p><p>廢班舍<br><img src="https://1013399.github.io/image-6/332/140067882_l.jpg" alt="大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山" loading="lazy"></p><p>廢崗哨<br><img src="https://1013399.github.io/image-6/332/140067908_l.jpg" alt="大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山" loading="lazy"></p><p>頭寮山<br><img src="https://1013399.github.io/image-6/332/140067912_l.jpg" alt="頭寮山" loading="lazy"></p><p>頭寮山總督府圖根點<br><img src="https://1013399.github.io/image-6/332/140067965_l.jpg" alt="頭寮山" loading="lazy"></p><p>頭寮山展望<br><img src="https://1013399.github.io/image-6/332/140068022_l.jpg" alt="頭寮山展望" loading="lazy"></p><p>拉繩陡下(往回拍攝)<br><img src="https://1013399.github.io/image-6/332/140068025_l.jpg" alt="大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山" loading="lazy"></p><p>叉路(前往百吉林蔭步道，取右湳仔溝古道)<br><img src="https://1013399.github.io/image-6/332/140068065_l.jpg" alt="大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山" loading="lazy"></p><p>極陡枕木階梯<br><img src="https://1013399.github.io/image-6/332/140068071_l.jpg" alt="大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山" loading="lazy"></p><p>桂竹林<br><img src="https://1013399.github.io/image-6/332/140068077_l.jpg" alt="大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山" loading="lazy"></p><p>湳仔溝古道口<br><img src="https://1013399.github.io/image-6/332/140068081_l.jpg" alt="大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山" loading="lazy"></p><p>湳仔溝7號-筍林橋<br><img src="https://1013399.github.io/image-6/332/140068118_l.jpg" alt="湳仔溝7號-筍林橋" loading="lazy"></p><p>叉路(左前方為湳仔溝6號-懷風橋)<br><img src="https://1013399.github.io/image-6/332/140068157_l.jpg" alt="大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山" loading="lazy"></p><p>湳仔溝6號-懷風橋<br><img src="https://1013399.github.io/image-6/332/140068161_l.jpg" alt="湳仔溝6號-懷風橋" loading="lazy"></p><p>鳳儀橋、親水橋、石公橋、花語橋<br><img src="https://1013399.github.io/image-6/332/140068167_l.jpg" alt="鳳儀橋、親水橋、石公橋、花語橋" loading="lazy"></p><p>湳仔溝堤步道木棧道<br><img src="https://1013399.github.io/image-6/332/140068172_l.jpg" alt="湳仔溝堤步道木棧道" loading="lazy"></p><p>木棧道多處腐朽<br><img src="https://1013399.github.io/image-6/332/140068175_l.jpg" alt="大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山" loading="lazy"></p><p>湳仔溝1號-泉源橋<br><img src="https://1013399.github.io/image-6/332/140067535_l.jpg" alt="湳仔溝1號-泉源橋" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',52),m={href:"http://www.keepon.com.tw/ActiveSite/Article/One.asp?ArticleID=24485",target:"_blank",rel:"noopener noreferrer"},_=t("br",null,null,-1),u=t("img",{src:"https://1013399.github.io/image-6/332/140068421_l.jpg",alt:"大艽芎古道、林嵩壽山、東竹篙厝山、內湳溝山、頭寮山",loading:"lazy"},null,-1);function j(y,z){const a=r("ExternalLinkIcon");return l(),p("div",null,[h,b,d,o(" more "),c,t("p",null,[i("原圖連結→"),t("a",m,[i("登山補給站(brownlee小李)"),s(a)]),_,u])])}const k=g(n,[["render",j],["__file","post-332-2009-12-07.html.vue"]]);export{k as default};
