import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as e,c as g,a as p,b as t,f as a,d as o,e as s}from"./app-63889d57.js";const n={},h=t("p",null,[t("strong",null,"神桌山"),a("，海拔762公尺，三等三角點No.4138，位於苗栗縣南庄鄉、三灣鄉和獅潭鄉的交界處，可以說是南庄、獅潭、三灣鄉界山，也是中港溪、後龍溪系的分野。")],-1),b=t("p",null,[a("神桌山登山口可由台三線106.8K處轉入；也可由南庄外環道轉入西溪大橋而上。另外也可由仙山(神仙縱走)或象山(神象縱走)縱走過來。"),t("br"),a("     "),t("strong",null,"桃牛坪山"),a("，海拔727公尺，台灣省政府圖根補點，位於神桌山往仙山的稜線上。桃牛坪客語意為「栓綁牛隻的平坦地」。")],-1),c=s('<p>走走日期：2009/11/29</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道一號下頭份交流道接上縣道124線往三灣方向行駛，抵南庄後走外環道，至西溪大橋右轉，過橋後叉路左轉(右往啞嗎頂古道)，約5分鐘後叉路往右後彎上行，有桐花步道入口不銹鋼大拱門，過牌樓後一路爬升，前段柏油、後段水泥再接碎石子路，不久後抵一空地停車，旁有大型木製指示牌。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>這次走的路線是南庄路線，登山口在電線桿-西村幹#130，由於要順走桃牛坪山，改由電線桿-西村幹#142附近登山口起登，再由電線桿西村幹#130回。<br> →空地停車處(取右入小徑)<br> →伯公廟(旁邊樹上釘有1號救援樁，杉木林轉為竹林)<br> →接到產業道路(取右新鋪設水泥路)<br> →產業道路叉路(直走不知往何處，取左上，轉為土石路，芒草夾徑)<br> →土石路轉為水泥路(旁有標示牌說明)<br> →神桌山登山口(電線桿-西村幹#130，需攀爬擋土牆，但不由此上，續沿水泥路上行)<br> →桃牛坪山登山口(電線桿-西村幹#142、#143之間，取右上林中小徑)<br> →2號救援樁叉路(右往神桌山，先取左往仙山)<br> →<strong>桃牛坪山</strong>(有一白色圖根補點，旁邊樹上釘有3號救援樁)<br> →原路折返<br> →回到2號救援樁叉路(取左往神桌山)<br> →叉路(右下往神桌山登山口、南庄，取直行)<br> →展望小平台(視野佳)<br> →<strong>神桌山</strong>(有顆三等三角點及「南庄事業區第三林班造林基石」，續行可往象山)<br> →原路折返<br> →回到叉路口(取左回神桌山登山口)<br> →神桌山登山口(接到產業道路，電線桿-西村幹#130處)<br> →回到停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>登山時間：約2小時</p><p>高低落差：約200公尺</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>大型木製指示牌(停車處)<br><img src="https://1013399.github.io/image-6/333/139452328_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><p>1號救援樁<br><img src="https://1013399.github.io/image-6/333/139452335_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><p>伯公廟<br><img src="https://1013399.github.io/image-6/333/139452341_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><p>杉木林轉為竹林<br><img src="https://1013399.github.io/image-6/333/139452347_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><p>接到產業道路<br><img src="https://1013399.github.io/image-6/333/139452354_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><p>產業道路叉路(取左)<br><img src="https://1013399.github.io/image-6/333/139452357_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><p>路旁特殊黃花<br><img src="https://1013399.github.io/image-6/333/139452361_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><p>芒草夾徑<br><img src="https://1013399.github.io/image-6/333/139452378_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><p>神桌山登山口<br><img src="https://1013399.github.io/image-6/333/139452382_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><p>路旁楓葉已轉紅<br><img src="https://1013399.github.io/image-6/333/139452398_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><p>桃牛坪山登山口<br><img src="https://1013399.github.io/image-6/333/139452443_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><p>2號救援樁叉路(先取左往仙山)<br><img src="https://1013399.github.io/image-6/333/139452453_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><p>桃牛坪山<br><img src="https://1013399.github.io/image-6/333/139452490_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><p>桃牛坪山省政府圖根補點<br><img src="https://1013399.github.io/image-6/333/139452554_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><p>3號救援樁<br><img src="https://1013399.github.io/image-6/333/139452460_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><p>叉路(右下往神桌山登山口、南庄，取直行)<br><img src="https://1013399.github.io/image-6/333/139452583_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><p>一小段攀岩<br><img src="https://1013399.github.io/image-6/333/139452591_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><p>展望小平台<br><img src="https://1013399.github.io/image-6/333/139452599_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><p>神桌山三等三角點<br><img src="https://1013399.github.io/image-6/333/139452610_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><p>神桌山三等三角點No.4138<br><img src="https://1013399.github.io/image-6/333/139452616_l.jpg" alt="桃牛坪山、神桌山" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',29),d={href:"http://www.keepon.com.tw/ActiveSite/Article/One.asp?ArticleID=14688",target:"_blank",rel:"noopener noreferrer"},m=t("br",null,null,-1),_=t("img",{src:"https://1013399.github.io/image-6/333/139453005_l.jpg",alt:"桃牛坪山、神桌山 參考地圖",loading:"lazy"},null,-1);function u(j,y){const i=l("ExternalLinkIcon");return e(),g("div",null,[h,b,p(" more "),c,t("p",null,[a("原圖連結→"),t("a",d,[a("登山補給站(brownlee小李)"),o(i)]),m,_])])}const x=r(n,[["render",u],["__file","post-333-2009-11-30.html.vue"]]);export{x as default};
