import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as r,a as o,b as i,f as t,d as s,e}from"./app-c5577115.js";const n={},h=i("p",null,[t("十一月的"),i("strong",null,"草嶺古道"),t("正是芒花綻放的季節，東北角海岸國家風景區第八年舉辦「草嶺古道芒花季活動」，集滿「遠望坑親水公園」及「護管所」兩處戳章者，可至福隆遊客中心或大里遊客中心兌換活動紀念品，沿途各點並設置闖關小遊戲，增加遊走古道趣味，福隆遊客中心備有接駁公車前往遠望坑，票價20元。")],-1),b=e('<p><strong>草嶺古道</strong>位於台北縣貢寮鄉遠望坑福德祠與宜蘭縣頭城鎮大里天公廟之間，由福隆車站起至大里天公廟，全長約8.5km，屬淡蘭古道中的一段，沿途有遠望坑親水公園、福德祠、雄鎮蠻煙碑、虎字碑、埡口觀景亭等景點，步行時間約須3~4小時。</p><p>走走日期：2009/11/15</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道三號北上於汐止系統交流道接國道一號，下八堵交流道後不久再接台62線，行駛至瑞濱端出口接台2線(北部濱海公路)右轉南下，抵福隆遊客中心停車場停車。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→福隆遊客中心停車場(先到火車站前買福隆鄉野便當，再回來搭接駁車)<br> →新社橋下車<br> →遠望坑橋<br> →<strong>遠望坑親水公園</strong>(領取芒花物語集章活動明信片，已蓋好戳章)<br> →梯田(凌波舞遊戲，過關送一瓶礦泉水，沒過關畫臉)<br> →<strong>草嶺古道口</strong>(續走柏油路)<br> →<strong>跌死馬橋</strong><br> →時空門(由此左轉，路徑轉為石板路，此處也有小遊戲)<br> →大榕樹(陡坡開始)<br> →<strong>仙跡岩</strong>(陡坡結束)<br> →<strong>雄鎮蠻煙</strong>石碣<br> →休憩亭(開運遊戲，過關送紋身貼紙，沒過關貼臉上，在此享用福隆便當)<br> →<strong>虎字碑</strong><br> →<strong>埡口</strong>(先右上往觀景亭)<br> →埡口觀景亭(敲祈福鐘，寫祈福卡，直行往桃源谷步道，往回續行草嶺古道)<br> →<strong>客棧遺址</strong>(女店小二奉薑母茶，去寒好喝)<br> →觀景亭(轉盤遊戲，還是送紋身貼紙)<br> →<strong>護管所</strong>(掛著芒劍山寨招牌，此處記得蓋戳章)<br> →大里天公廟(備有炒米粉)<br> →大里遊客中心(兌換紀念品，筆記本已發完，改發購物袋)<br> →大里火車站(搭電聯車回福隆)<br> →福隆火車站<br> →福隆遊客中心停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>登山時間：約3.5時(含午餐、休息、賞景)</p><p>高低落差：約300公尺</p></div><div class="hint-container note"><p class="hint-container-title">後記</p><p>今日本想連走小百岳「灣坑頭山」，因天候不佳、雨勢未歇，猶豫之間已經浪費不少時間，於是決定來個古道輕鬆走就好，下接駁車後竟然太陽露臉了，走完古道全程也未飄一滴雨，真是天公作美。<br>     草嶺古道接駁車有：福隆遊客中心至遠望坑20元，大里遊客中心至福隆遊客中心43元(搭電聯車只要15元)，福隆至瑞芳憑DM免費(福隆遊客中心索取或至2009東北角芒花季活動網下載)。</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>東北角海岸國家風景區管理處<br><img src="https://1013399.github.io/image-6/336/138380791_l.jpg" alt="東北角海岸國家風景區管理處" loading="lazy"></p><p>福隆遊客中心<br><img src="https://1013399.github.io/image-6/336/138378760_l.jpg" alt="福隆遊客中心" loading="lazy"></p><p>龍洞四季灣馬車<br><img src="https://1013399.github.io/image-6/336/138378773_l.jpg" alt="龍洞四季灣馬車" loading="lazy"></p><p>遠望坑橋<br><img src="https://1013399.github.io/image-6/336/138379025_l.jpg" alt="坑橋" loading="lazy"></p><p>路旁基石(基準點九四六九号)<br><img src="https://1013399.github.io/image-6/336/138379037_l.jpg" alt="草嶺古道 路旁基石(基準點九四六九号)" loading="lazy"></p><p>草嶺古道路線圖<br><img src="https://1013399.github.io/image-6/336/138379044_l.jpg" alt="草嶺古道路線圖" loading="lazy"></p><p>遠望坑親水公園<br><img src="https://1013399.github.io/image-6/336/138379159_l.jpg" alt="坑親水公園" loading="lazy"></p><p>親水公園旁梯田<br><img src="https://1013399.github.io/image-6/336/138379175_l.jpg" alt="坑親水公園" loading="lazy"></p><p>草嶺古道口<br><img src="https://1013399.github.io/image-6/336/138379187_l.jpg" alt="草嶺古道口" loading="lazy"></p><p>跌死馬橋介紹<br><img src="https://1013399.github.io/image-6/336/138379207_l.jpg" alt="草嶺古道 跌死馬橋" loading="lazy"></p><p>跌死馬橋<br><img src="https://1013399.github.io/image-6/336/138379311_l.jpg" alt="草嶺古道 跌死馬橋" loading="lazy"></p><p>時空門(由此左轉，路徑轉為石板路)<br><img src="https://1013399.github.io/image-6/336/138379400_l.jpg" alt="草嶺古道 時空門" loading="lazy"></p><p>大榕樹<br><img src="https://1013399.github.io/image-6/336/138379415_l.jpg" alt="草嶺古道 大榕樹" loading="lazy"></p><p>草嶺古道緣由介紹<br><img src="https://1013399.github.io/image-6/336/138379479_l.jpg" alt="草嶺古道緣由介紹" loading="lazy"></p><p>陡坡開始<br><img src="https://1013399.github.io/image-6/336/138379485_l.jpg" alt="草嶺古道" loading="lazy"></p><p>仙跡岩<br><img src="https://1013399.github.io/image-6/336/138379498_l.jpg" alt="草嶺古道 仙跡岩" loading="lazy"></p><p>雄鎮蠻煙石碣<br><img src="https://1013399.github.io/image-6/336/138379506_l.jpg" alt="草嶺古道 雄鎮蠻煙石碣" loading="lazy"></p><p>古道小橋<br><img src="https://1013399.github.io/image-6/336/138379515_l.jpg" alt="草嶺古道 小橋" loading="lazy"></p><p>休憩亭<br><img src="https://1013399.github.io/image-6/336/138379521_l.jpg" alt="草嶺古道 休憩亭" loading="lazy"></p><p>芒花出現<br><img src="https://1013399.github.io/image-6/336/138379525_l.jpg" alt="草嶺古道 芒花" loading="lazy"></p><p>虎字碑<br><img src="https://1013399.github.io/image-6/336/138379532_l.jpg" alt="草嶺古道 虎字碑" loading="lazy"></p><p>埡口平台<br><img src="https://1013399.github.io/image-6/336/138379537_l.jpg" alt="草嶺古道 埡口平台" loading="lazy"></p><p>埡口觀景亭<br><img src="https://1013399.github.io/image-6/336/138379542_l.jpg" alt="草嶺古道 埡口觀景亭" loading="lazy"></p><p>祈福鐘<br><img src="https://1013399.github.io/image-6/336/138379546_l.jpg" alt="草嶺古道 祈福鐘" loading="lazy"></p><p>回望草嶺古道<br><img src="https://1013399.github.io/image-6/336/138379551_l.jpg" alt="草嶺古道" loading="lazy"></p><p>桃源谷步道(往灣坑頭山)<br><img src="https://1013399.github.io/image-6/336/138379562_l.jpg" alt="桃源谷步道(往灣坑頭山)" loading="lazy"></p><p>俯瞰大里地區<br><img src="https://1013399.github.io/image-6/336/138379574_l.jpg" alt="大里地區" loading="lazy"></p><p>土地測量局四等控制點<br><img src="https://1013399.github.io/image-6/336/138379582_l.jpg" alt="土地測量局四等控制點" loading="lazy"></p><p>客棧遺址<br><img src="https://1013399.github.io/image-6/336/138379602_l.jpg" alt="草嶺古道" loading="lazy"></p><p>觀景亭<br><img src="https://1013399.github.io/image-6/336/138379616_l.jpg" alt="草嶺古道" loading="lazy"></p><p>護管所<br><img src="https://1013399.github.io/image-6/336/138379720_l.jpg" alt="草嶺古道 護管所" loading="lazy"></p><p>朦朧中的龜山島<br><img src="https://1013399.github.io/image-6/336/138379731_l.jpg" alt="龜山島" loading="lazy"></p><p>大里天公廟旁石刻<br><img src="https://1013399.github.io/image-6/336/138379744_l.jpg" alt="大里天公廟旁石刻" loading="lazy"></p><p>大里天公廟<br><img src="https://1013399.github.io/image-6/336/138379757_l.jpg" alt="大里天公廟" loading="lazy"></p><p>大里遊客中心<br><img src="https://1013399.github.io/image-6/336/138379823_l.jpg" alt="大里遊客中心" loading="lazy"></p><p>大里火車站<br><img src="https://1013399.github.io/image-6/336/138379836_l.jpg" alt="大里火車站" loading="lazy"></p><p>芒花季活動旗幟<br><img src="https://1013399.github.io/image-6/336/138379813_l.jpg" alt="草嶺古道 芒花季" loading="lazy"></p><p>芒花物語集章活動明信片(正面)<br><img src="https://1013399.github.io/image-6/336/138379872_l.jpg" alt="草嶺古道 芒花物語集章活動明信片" loading="lazy"></p><p>芒花物語集章活動明信片(背面)<br><img src="https://1013399.github.io/image-6/336/138378101_l.jpg" alt="草嶺古道 芒花物語集章活動明信片" loading="lazy"></p><p>福隆至瑞芳免費巴士DM<br><img src="https://1013399.github.io/image-6/336/138379855_l.jpg" alt="福隆至瑞芳免費巴士DM" loading="lazy"></p><p>活動紀念品─購物袋<br><img src="https://1013399.github.io/image-6/336/138379865_l.jpg" alt="活動紀念品─購物袋" loading="lazy"></p><p>接駁車時刻表<br><img src="https://1013399.github.io/image-6/336/138380760_l.jpg" alt="草嶺古道 接駁車時刻表" loading="lazy"></p><p>火車時刻表<br><img src="https://1013399.github.io/image-6/336/138380765_l.jpg" alt="草嶺古道 火車時刻表" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',54),m={href:"http://www.necoast-nsa.gov.tw/activity/flower09/page1-3.html",target:"_blank",rel:"noopener noreferrer"},c=i("br",null,null,-1),d=i("img",{src:"https://1013399.github.io/image-6/336/138380767_l.jpg",alt:"草嶺古道 參考地圖",loading:"lazy"},null,-1);function _(u,y){const a=l("ExternalLinkIcon");return p(),r("div",null,[h,o(" more "),b,i("p",null,[i("a",m,[t("2009東北角芒花季活動網"),s(a)]),c,d])])}const f=g(n,[["render",_],["__file","post-336-2009-11-17.html.vue"]]);export{f as default};
