import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as r,a as e,b as a,f as t,d as o,e as s}from"./app-c5577115.js";const n={},h=a("p",null,[a("strong",null,"文山步道"),t("，位於新竹縣新埔鎮與芎林鄉交界處，為近年由有心之士完成具有觀光效益之步道，有彎道、好漢坡、階梯和平地，可以交互著走，訓練不同的肌肉，可以欣賞高鐵景觀和文山國小的百年楓樹，有緣還可以端坐山頂看老鷹自由的遨翔天際。")],-1),b=a("p",null,[a("strong",null,"吧哩嘓山"),t("，海拔175公尺，山頂為小空地，有個無名基石，新埔舊稱「吧哩嘓」，為平埔族語的音譯，原意不可考，可能是以此命名。")],-1),c=s('<p><strong>犁頭山</strong>，海拔157公尺，三等三角點九七號，另有頭前溪水源基石，位於文山步道山頂。展望極佳，可以俯瞰竹北六家地區與台灣高鐵六家站。</p><p>登山日期：2009/10/31</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>竹北往關西方向行駛(118縣道)，於文山路587巷右轉(有文山古道指示，斜對面為遠東化纖廠)，進入巷子後第一個叉路左轉，沿著指示至大龍眼樹右方有一個大廣場可以停車，登山口就在前方左側。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→登山口<br> →八爪樹<br> →稜線叉路(左往石頭坑山，取右往犁頭山、文山國小)<br> →觀景小平台(可見六家高鐵站)<br> →叉路(右下往登山口，取左行)<br> →第一個小山頭<br> →<strong>吧哩嘓山</strong>(第二個小山頭)<br> →第三個小山頭<br> →叉路(左方路跡明顯、有路條，應是通往六家地區，取直行)<br> →<strong>犁頭山</strong>(近距離欣賞六家高鐵站，直行拉繩陡下應是通往竹北，取右下往文山國小)<br> →竹林路段<br> →文山國小(入內欣賞三棵百年大楓樹)<br> →文山國小119階景觀階梯(大門口)<br> →灌溉溝渠(找不到路回登山口，詢問當地居民才知應從操場旁馬路下切，只好沿溝渠繞回)<br> →回到登山口</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>登山時間：約1.5小時</p><p>高低落差：約110公尺</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>大龍眼樹旁大廣場(停車處)<br><img src="https://1013399.github.io/image-6/338/137070593_l.jpg" alt="大龍眼樹旁大廣場(停車處) 文山步道、吧哩嘓山、犁頭山" loading="lazy"></p><p>登山口<br><img src="https://1013399.github.io/image-6/338/137070603_l.jpg" alt="文山步道登山口" loading="lazy"></p><p>八爪樹<br><img src="https://1013399.github.io/image-6/338/137070611_l.jpg" alt="八爪樹 文山步道、吧哩嘓山、犁頭山" loading="lazy"></p><p>遠東化纖廠<br><img src="https://1013399.github.io/image-6/338/137070620_l.jpg" alt="遠東化纖廠 文山步道、吧哩嘓山、犁頭山" loading="lazy"></p><p>稜線叉路(取右往犁頭山、文山國小)<br><img src="https://1013399.github.io/image-6/338/137070765_l.jpg" alt="文山步道、吧哩嘓山、犁頭山" loading="lazy"></p><p>觀景小平台<br><img src="https://1013399.github.io/image-6/338/137070864_l.jpg" alt="文山步道、吧哩嘓山、犁頭山" loading="lazy"></p><p>平台遠眺六家高鐵站<br><img src="https://1013399.github.io/image-6/338/137070962_l.jpg" alt="文山步道、吧哩嘓山、犁頭山" loading="lazy"></p><p>叉路(右下往登山口，取左行)<br><img src="https://1013399.github.io/image-6/338/137071069_l.jpg" alt="文山步道、吧哩嘓山、犁頭山" loading="lazy"></p><p>小山頭<br><img src="https://1013399.github.io/image-6/338/137071076_l.jpg" alt="文山步道、吧哩嘓山、犁頭山" loading="lazy"></p><p>吧哩嘓山<br><img src="https://1013399.github.io/image-6/338/137071141_l.jpg" alt="吧哩嘓山" loading="lazy"></p><p>吧哩嘓山無名基石<br><img src="https://1013399.github.io/image-6/338/137071146_l.jpg" alt="吧哩嘓山無名基石" loading="lazy"></p><p>再望六家高鐵站<br><img src="https://1013399.github.io/image-6/338/137071154_l.jpg" alt="六家高鐵站" loading="lazy"></p><p>第三個小山頭<br><img src="https://1013399.github.io/image-6/338/137071163_l.jpg" alt="文山步道、吧哩嘓山、犁頭山" loading="lazy"></p><p>叉路(左方路跡明顯往六家，取直行)<br><img src="https://1013399.github.io/image-6/338/137071171_l.jpg" alt="文山步道、吧哩嘓山、犁頭山" loading="lazy"></p><p>頭前溪水源基石<br><img src="https://1013399.github.io/image-6/338/137071181_l.jpg" alt="頭前溪水源基石" loading="lazy"></p><p>犁頭山<br><img src="https://1013399.github.io/image-6/338/137071202_l.jpg" alt="犁頭山" loading="lazy"></p><p>犁頭山三等三角點<br><img src="https://1013399.github.io/image-6/338/137071188_l.jpg" alt="犁頭山三等三角點" loading="lazy"></p><p>三等三角點九七號<br><img src="https://1013399.github.io/image-6/338/137071196_l.jpg" alt="犁頭山三等三角點" loading="lazy"></p><p>直行拉繩陡下應是通往竹北，取右下往文山國小<br><img src="https://1013399.github.io/image-6/338/137071207_l.jpg" alt="文山步道、吧哩嘓山、犁頭山" loading="lazy"></p><p>犁頭山上近看六家高鐵站<br><img src="https://1013399.github.io/image-6/338/137071214_l.jpg" alt="文山步道、吧哩嘓山、犁頭山" loading="lazy"></p><p>竹林路段<br><img src="https://1013399.github.io/image-6/338/137071272_l.jpg" alt="文山步道、吧哩嘓山、犁頭山" loading="lazy"></p><p>文山國小<br><img src="https://1013399.github.io/image-6/338/137071333_l.jpg" alt="文山國小" loading="lazy"></p><p>文山國小操場旁三棵百年大楓樹<br><img src="https://1013399.github.io/image-6/338/137071342_l.jpg" alt="文山國小操場旁三棵百年大楓樹" loading="lazy"></p><p>119階景觀階梯解說牌<br><img src="https://1013399.github.io/image-6/338/137071347_l.jpg" alt="文山步道、吧哩嘓山、犁頭山" loading="lazy"></p><p>灌溉溝渠(沿溝渠行至道路，先右轉再左轉回登山口)<br><img src="https://1013399.github.io/image-6/338/137070584_l.jpg" alt="文山步道、吧哩嘓山、犁頭山 灌溉溝渠" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',35),d={href:"http://designteng.googlepages.com/980221-03.jpg",target:"_blank",rel:"noopener noreferrer"},m=a("br",null,null,-1),_=a("img",{src:"https://1013399.github.io/image-6/338/137071376_l.jpg",alt:"文山步道、吧哩嘓山、犁頭山 參考地圖",loading:"lazy"},null,-1);function u(j,y){const i=l("ExternalLinkIcon");return p(),r("div",null,[h,b,e(" more "),c,a("p",null,[t("原圖連結→"),a("a",d,[t("小鄧的登山札記"),o(i)]),m,_])])}const x=g(n,[["render",u],["__file","post-338-2009-11-03.html.vue"]]);export{x as default};
