import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r,o as g,c as e,a as p,b as a,f as i,d as o,e as h}from"./app-a9847bbb.js";const s={},n=a("p",null,[a("strong",null,"南觀音山"),i("，又名大坑觀音山，海拔318公尺，山頂有三等三角點No.5012及三等聯勤測補No.78024基石，位於台中市北屯區大坑，鄰近大坑風景區，該山不是因山之形狀而得名，而是因山頂有觀音菩薩聖像而得名，名列台灣小百岳#042。")],-1),d=h('<p>登山日期：2009/10/25</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><h3 id="北部南下路線" tabindex="-1"><a class="header-anchor" href="#北部南下路線" aria-hidden="true">#</a> 北部南下路線</h3><p>國道1號高速公路南下，下台中大雅交流道後往台中方向行駛(中清路)，至文心路左轉，過北屯路接上東山路，過東山路地下道續行，三叉取直過倡和橋，一路直行過逢甲橋至大坑圓環右轉橫坑巷，遇中興巷右轉，過大坑橋接廓子巷，左側即為登山口。</p><h3 id="實際行走路線" tabindex="-1"><a class="header-anchor" href="#實際行走路線" aria-hidden="true">#</a> 實際行走路線</h3><p>走完三汀山後回到東平路往台中市方向，遇中山路右轉，過太平國中左彎後遇大興路右轉，遇叉路直行長安東路，接廓子路過第一號橋續直行廓子路，經中台科技大學後，於大坑橋橋頭前右轉停車場停車。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→登山口(大坑橋橋頭附近，旁有黑色大佛手，循水泥階梯陡上)<br> →叉路(右往觀音小徑登山口，續直上)<br> →叉路(旁有鐵皮屋，右側有道路，續直行)<br> →天海大石<br> →涼亭(已殘破不堪)<br> →入佛門(黑色大佛手)<br> →地藏王小廟(南觀音山三角點就在旁邊)<br> →玉佛寺(屋頂一座觀音像，廟前備有素餐供遊客食用，左有地藏王殿、前有釋迦太子樓)<br> →休息觀景後下山(取地藏王殿旁產道下山，觀音像列於路旁)<br> →叉路(右續走產道，取直下階梯)<br> →十字路口叉路(右回產道，直往樂山亭、總統椅，取左往無極殿下山)<br> →小鐵橋<br> →山神廟<br> →無極殿<br> →回到停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>登山時間：約1小時( ○型一圈)</p><p>高低落差：約150公尺</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>大坑橋橋頭<br><img src="https://1013399.github.io/image-6/339/136546854_l.jpg" alt="大坑橋橋頭 南觀音山" loading="lazy"></p><p>登山口<br><img src="https://1013399.github.io/image-6/339/136546863_l.jpg" alt="南觀音山登山口" loading="lazy"></p><p>陡上水泥階梯<br><img src="https://1013399.github.io/image-6/339/136546867_l.jpg" alt="南觀音山" loading="lazy"></p><p>轉為棧道陡上<br><img src="https://1013399.github.io/image-6/339/136546944_l.jpg" alt="南觀音山" loading="lazy"></p><p>鐵皮屋<br><img src="https://1013399.github.io/image-6/339/136547031_l.jpg" alt="南觀音山 鐵皮屋" loading="lazy"></p><p>天海大石<br><img src="https://1013399.github.io/image-6/339/136547043_l.jpg" alt="南觀音山 天海大石" loading="lazy"></p><p>俯瞰中台科技大學<br><img src="https://1013399.github.io/image-6/339/136547100_l.jpg" alt="中台科技大學" loading="lazy"></p><p>殘破的涼亭<br><img src="https://1013399.github.io/image-6/339/136547103_l.jpg" alt="南觀音山" loading="lazy"></p><p>繼續陡上<br><img src="https://1013399.github.io/image-6/339/136547106_l.jpg" alt="南觀音山" loading="lazy"></p><p>入佛門大手<br><img src="https://1013399.github.io/image-6/339/136547110_l.jpg" alt="入佛門大手" loading="lazy"></p><p>手上寫著入佛門<br><img src="https://1013399.github.io/image-6/339/136547113_l.jpg" alt="手上寫著入佛門" loading="lazy"></p><p>地藏王小廟<br><img src="https://1013399.github.io/image-6/339/136547198_l.jpg" alt="地藏王小廟" loading="lazy"></p><p>南觀音山三等三角點<br><img src="https://1013399.github.io/image-6/339/136547116_l.jpg" alt="南觀音山三等三角點" loading="lazy"></p><p>三等三角點No.5012<br><img src="https://1013399.github.io/image-6/339/136547120_l.jpg" alt="南觀音山三等三角點" loading="lazy"></p><p>三等聯勤測補No.78024<br><img src="https://1013399.github.io/image-6/339/136547204_l.jpg" alt="南觀音山" loading="lazy"></p><p>釋迦牟尼佛像<br><img src="https://1013399.github.io/image-6/339/136547200_l.jpg" alt="南觀音山 釋迦牟尼佛像" loading="lazy"></p><p>觀音菩薩聖像<br><img src="https://1013399.github.io/image-6/339/136547207_l.jpg" alt="南觀音山 觀音菩薩聖像" loading="lazy"></p><p>地藏王菩薩<br><img src="https://1013399.github.io/image-6/339/136547208_l.jpg" alt="南觀音山 地藏王菩薩" loading="lazy"></p><p>釋迦太子樓<br><img src="https://1013399.github.io/image-6/339/136547210_l.jpg" alt="南觀音山 釋迦太子樓" loading="lazy"></p><p>地藏王殿旁產道下山(觀音像列於路旁)<br><img src="https://1013399.github.io/image-6/339/136547214_l.jpg" alt="南觀音山 地藏王殿旁產道" loading="lazy"></p><p>十字路口叉路(取左下山)<br><img src="https://1013399.github.io/image-6/339/136547222_l.jpg" alt="南觀音山" loading="lazy"></p><p>小鐵橋<br><img src="https://1013399.github.io/image-6/339/136547267_l.jpg" alt="南觀音山 小鐵橋" loading="lazy"></p><p>山神廟<br><img src="https://1013399.github.io/image-6/339/136547273_l.jpg" alt="南觀音山 山神廟" loading="lazy"></p><p>無極殿<br><img src="https://1013399.github.io/image-6/339/136547277_l.jpg" alt="南觀音山 無極殿" loading="lazy"></p><p>過無極殿後取左回停車場<br><img src="https://1013399.github.io/image-6/339/136546851_l.jpg" alt="南觀音山 無極殿" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',37),b={href:"http://blog.xuite.net/laoweiz/blog/15438948",target:"_blank",rel:"noopener noreferrer"},c=a("br",null,null,-1),m=a("img",{src:"https://1013399.github.io/image-6/339/136547686_l.jpg",alt:"南觀音山 參考地圖",loading:"lazy"},null,-1);function _(u,z){const t=r("ExternalLinkIcon");return g(),e("div",null,[n,p(" more "),d,a("p",null,[i("原圖連結→"),a("a",b,[i("老尉子的部落格"),o(t)]),c,m])])}const f=l(s,[["render",_],["__file","post-339-2009-10-28.html.vue"]]);export{f as default};
