import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as r,c as p,a as o,b as i,f as t,d as e,e as s}from"./app-6513a715.js";const h={},b=i("p",null,[i("strong",null,"三汀山"),t("，又名望高寮，海拔480公尺，位於台中縣太平市，可遠眺台中盆地及大肚山脈，俯瞰太平市一江橋及頭汴坑溪，原有之三等三角點基石已遺失，聯勤測量局另設「陸補#145」基石，置於平台花圃中，列名台灣小百岳#043。")],-1),n=s('<p>三汀山共有五條登山步道，咬人狗坑登山步道將這五條登山步道串連在一起，其中叉路錯綜複雜，宛如迷宮，指示牌及導覽圖有點看不太懂，原則上，遇到叉路往上走就沒錯。</p><p><strong>埤腹山</strong>，網路資料稱埤頭山，海拔489公尺，位於三汀山旁，原來有一等三角點基石已遺失，但地面上有個十字盤石，距三汀山僅約2分鐘路程。</p><p>登山日期：2009/10/25</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>由國道1號高速公路南下，下台中大雅交流道後往台中方向行駛(中清路後接大雅路)，至進化北路左轉，過北屯路接進化路(台3線)，過建成路地下道遇樂業路左轉，過東平橋後接東平路(中130)，於一江橋前約20公尺左轉東平路2巷，車行約200公尺過中興橋(水泥小橋)左轉，依「咬人狗坑登山步道」及「三汀山望高寮」指示牌前進，續行約500公尺經土地公廟後，看到「咬人狗坑生態景觀步道」碑，路旁停車格可停車，往前續開至主入口前車停於路旁。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→主入口(左柏油路往長青運動廣場，取右一號步道陡上水泥路)<br> →叉路(左往二號步道，前方棧道坍塌，取右繞行)<br> →接回棧道(棧道途中右下方可見狀似台灣的大潭子)<br> →大潭子觀景涼亭(俯視大潭子、一江橋)<br> →叉路(右小徑一號步道可往長青運動廣場，取前方棧道上行)<br> →叉路(直往二號步道下山，取右上)<br> →休憩涼亭<br> →叉路(右往一號步道，取左)<br> →高壓電塔<br> →叉路(右泥土路往一號步道，取左水泥路，旁有廢棄小屋)<br> →叉路(左二號步道下山，取右)<br> →430棧道(直行為三號步道，取右陡上四百多階棧道)<br> →叉路(左有觀景小平台，取右)<br> →長青運動廣場(左往咬人狗坑登山步道，取右)<br> →叉路(右往一號步道下山，取左上，旁有鐵皮屋及步道解說牌)<br> →叉路(右下往好漢坡，續直行水泥路)<br> →水泥路終點(路徑改為枕木棧道)<br> →叉路(直走為老鼠崎登山步道可至觀雲嶺，取右水泥塊步道)<br> →<strong>三汀山</strong>(望高寮)(山頂平台寬廣，有二層樓觀景涼亭，續直行入竹林)<br> →<strong>埤<strong><strong>腹</strong></strong>山</strong>(埤頭山)(約2分鐘可達)<br> →原路退回<br> →回到三汀山觀景涼亭(休息)<br> →回到老鼠崎步道叉路<br> →回到長青運動廣場鐵皮屋叉路(取直走一號步道下山)<br> →健康嶺(為寬廣柏油路底，取右側枕木階梯下山，旁有水利署基石)<br> →接回柏油路(沿路而下)<br> →叉路(取右捷徑往下)<br> →回到高壓電塔<br> →回到大潭子觀景涼亭<br> →回到主入口</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>登山時間：約3小時(輕鬆悠閒緩步行程)</p><p>高低落差：約340公尺</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>主入口<br><img src="https://1013399.github.io/image-6/340/136545131_l.jpg" alt="三汀山、埤腹山 主入口" loading="lazy"></p><p>叉路(左往二號步道，前方棧道坍塌，取右繞行)<br><img src="https://1013399.github.io/image-6/340/136545223_l.jpg" alt="三汀山、埤腹山" loading="lazy"></p><p>枕木棧道<br><img src="https://1013399.github.io/image-6/340/136545228_l.jpg" alt="三汀山、埤腹山" loading="lazy"></p><p>棧道途中可見台灣潭<br><img src="https://1013399.github.io/image-6/340/136545305_l.jpg" alt="三汀山、埤腹山 台灣潭" loading="lazy"></p><p>大潭子觀景涼亭<br><img src="https://1013399.github.io/image-6/340/136545348_l.jpg" alt="三汀山、埤腹山 大潭子" loading="lazy"></p><p>叉路(取前方棧道上行)<br><img src="https://1013399.github.io/image-6/340/136545354_l.jpg" alt="三汀山、埤腹山" loading="lazy"></p><p>此處亦可見台灣潭<br><img src="https://1013399.github.io/image-6/340/136545358_l.jpg" alt="三汀山、埤腹山 台灣潭" loading="lazy"></p><p>叉路(直往二號步道下山，取右上)<br><img src="https://1013399.github.io/image-6/340/136545362_l.jpg" alt="三汀山、埤腹山" loading="lazy"></p><p>休憩涼亭<br><img src="https://1013399.github.io/image-6/340/136545368_l.jpg" alt="三汀山、埤腹山" loading="lazy"></p><p>過涼亭後叉路(右往一號步道，取左)<br><img src="https://1013399.github.io/image-6/340/136545375_l.jpg" alt="三汀山、埤腹山" loading="lazy"></p><p>高壓電塔<br><img src="https://1013399.github.io/image-6/340/136545380_l.jpg" alt="三汀山、埤腹山 高壓電塔" loading="lazy"></p><p>叉路(右泥土路往一號步道，取左水泥路)<br><img src="https://1013399.github.io/image-6/340/136545383_l.jpg" alt="三汀山、埤腹山" loading="lazy"></p><p>廢棄小屋<br><img src="https://1013399.github.io/image-6/340/136545450_l.jpg" alt="三汀山、埤腹山" loading="lazy"></p><p>叉路(左二號步道下山，取右)<br><img src="https://1013399.github.io/image-6/340/136545462_l.jpg" alt="三汀山、埤腹山" loading="lazy"></p><p>430棧道<br><img src="https://1013399.github.io/image-6/340/136545469_l.jpg" alt="三汀山、埤腹山 430棧道" loading="lazy"></p><p>陡上四百多階棧道<br><img src="https://1013399.github.io/image-6/340/136545478_l.jpg" alt="三汀山、埤腹山" loading="lazy"></p><p>430棧道後叉路(左有觀景小平台，取右)<br><img src="https://1013399.github.io/image-6/340/136545486_l.jpg" alt="三汀山、埤腹山" loading="lazy"></p><p>長青運動廣場<br><img src="https://1013399.github.io/image-6/340/136545498_l.jpg" alt="長青運動廣場" loading="lazy"></p><p>長青運動廣場旁鐵皮屋<br><img src="https://1013399.github.io/image-6/340/136545506_l.jpg" alt="長青運動廣場旁鐵皮屋" loading="lazy"></p><p>長青運動廣場叉路(右往一號步道下山，取左上)<br><img src="https://1013399.github.io/image-6/340/136545512_l.jpg" alt="三汀山、埤腹山" loading="lazy"></p><p>長青運動廣場旁步道解說牌<br><img src="https://1013399.github.io/image-6/340/136545518_l.jpg" alt="長青運動廣場旁步道解說牌" loading="lazy"></p><p>前面大概就是三汀山(望高寮)吧<br><img src="https://1013399.github.io/image-6/340/136545653_l.jpg" alt="三汀山、埤腹山" loading="lazy"></p><p>叉路(右下往好漢坡，續直行)<br><img src="https://1013399.github.io/image-6/340/136545662_l.jpg" alt="三汀山、埤腹山" loading="lazy"></p><p>水泥路轉為枕木棧道<br><img src="https://1013399.github.io/image-6/340/136545671_l.jpg" alt="三汀山、埤腹山" loading="lazy"></p><p>叉路(直走為老鼠崎登山步道，取右水泥塊步道)<br><img src="https://1013399.github.io/image-6/340/136545808_l.jpg" alt="三汀山、埤腹山" loading="lazy"></p><p>三汀山二層樓觀景涼亭<br><img src="https://1013399.github.io/image-6/340/136545872_l.jpg" alt="三汀山、埤腹山" loading="lazy"></p><p>三汀山聯勤測量局基石<br><img src="https://1013399.github.io/image-6/340/136545879_l.jpg" alt="三汀山聯勤測量局基石" loading="lazy"></p><p>聯勤測量局陸補#145<br><img src="https://1013399.github.io/image-6/340/136545885_l.jpg" alt="三汀山、埤腹山" loading="lazy"></p><p>望高寮解說牌<br><img src="https://1013399.github.io/image-6/340/136545888_l.jpg" alt="望高寮解說牌" loading="lazy"></p><p>涼亭展望<br><img src="https://1013399.github.io/image-6/340/136545897_l.jpg" alt="三汀山、埤腹山" loading="lazy"></p><p>埤腹山<br><img src="https://1013399.github.io/image-6/340/136545904_l.jpg" alt="埤腹山" loading="lazy"></p><p>埤腹山十字盤石<br><img src="https://1013399.github.io/image-6/340/136545909_l.jpg" alt="埤腹山十字盤石" loading="lazy"></p><p>健康嶺右側枕木階梯旁水利署基石<br><img src="https://1013399.github.io/image-6/340/136546134_l.jpg" alt="健康嶺右側枕木階梯旁水利署基石" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',44),m={href:"http://blog.xuite.net/laoweiz/blog/15439387",target:"_blank",rel:"noopener noreferrer"},c=i("br",null,null,-1),d=i("img",{src:"https://1013399.github.io/image-6/340/136546163_l.jpg",alt:"三汀山、埤腹山 參考地圖",loading:"lazy"},null,-1);function _(u,z){const a=l("ExternalLinkIcon");return r(),p("div",null,[b,o(" more "),n,i("p",null,[t("原圖連結→"),i("a",m,[t("老尉子的部落格"),e(a)]),c,d])])}const f=g(h,[["render",_],["__file","post-340-2009-10-27.html.vue"]]);export{f as default};
