import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as r,c as p,a as e,b as i,e as a,d as o,f as s}from"./app-9ec59630.js";const h={},n=i("p",null,[i("strong",null,"復興尖山"),a("，海拔1852公尺，三等三角點No.6231，位於桃園縣復興鄉北橫公路上，山型聳立有如其名，山頂視野極佳，與夫婦山及那結山合稱復興三尖。一般山友多從北橫59.3公里處起登，或與巴博庫魯山連登。")],-1),b=s('<p>復興尖山登山路徑全程濕滑，需小心攀登，前段為陡上樹根路，路途中偶有巨木倒臥；中段平緩稜線，潮濕泥濘，依序為蕨類區、雜木林區與杜鵑林區；末段則為陡上岩石與杜鵑林樹根交錯，必需拉繩攀樹，開始有較佳的展望。</p><p>登山日期：2009/10/18</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>開車由北二高於關西下交流道，轉118縣道(羅馬公路)再接台7線北橫公路，右轉往巴陵(需注意31.1K之雪霧隧道限速僅30km，隧道出口有警察守候測速)，過巴陵橋後續行，抵達台7線59.3K處附近，於較寬處停車(登山口前約可停2~3部，過登山口後約可停3部)。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→登山口(位於台7線59.3K路旁，有點不太明顯)<br> →一開始就是急陡坡<br> →小平台(約走30分鐘可達，在此休息後續行，先下坡再上坡)<br> →到達「往尖山」指示牌(仍為陡坡，但不再急陡)<br> →巨大倒木(已被裁成數段，附近有多棵巨木)<br> →「1689峰」山下叉路(左往庫魯山，取直往復興尖山，轉為平緩泥濘山徑)<br> →坍塌處觀景點(視野佳，左可俯瞰上巴陵民宿區)<br> →小山溝<br> →白色陶瓷絕緣礙子(釘在樹上，不知作用為何)<br> →又一處小山溝<br> →樹洞(穿越而過，又開始進入急陡坡)<br> →到達「加油哦！尖山基點快到了！」指示牌(其實還尚須30~40分鐘到達山頂)<br> →小山頭(陡上2~3個小山頭)<br> →尖山山頂(視野佳，左前方陡下往巴博庫魯山，右可往復興尖山第二登山口)<br> →原路返回<br> →回到緩坡路段<br> →回到坍塌區<br> →回到「1689峰」山下<br> →回到登山口</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>登山時間：上山約4小時(含午餐、休息)、下山約3小時(全程濕滑泥濘，小心為妙)</p><p>高低落差：約680公尺</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>登山口<br><img src="https://1013399.github.io/image-6/341/135881041_l.jpg" alt="桃園 復興尖山 登山口" loading="lazy"></p><p>小平台<br><img src="https://1013399.github.io/image-6/341/135881082_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>「往尖山」指示牌<br><img src="https://1013399.github.io/image-6/341/135881084_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>不知名蘭花<br><img src="https://1013399.github.io/image-6/341/135881034_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>巨大倒木<br><img src="https://1013399.github.io/image-6/341/135881112_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>巨木<br><img src="https://1013399.github.io/image-6/341/135881116_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>「1689峰」山下<br><img src="https://1013399.github.io/image-6/341/135881130_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>左往庫魯山，直行往復興尖山<br><img src="https://1013399.github.io/image-6/341/135881123_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>坍塌處觀景點<br><img src="https://1013399.github.io/image-6/341/135881169_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>小山溝<br><img src="https://1013399.github.io/image-6/341/135881197_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>白色陶瓷絕緣礙子<br><img src="https://1013399.github.io/image-6/341/135881202_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>另一處小山溝<br><img src="https://1013399.github.io/image-6/341/135881236_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>此處樹木都奇形怪狀<br><img src="https://1013399.github.io/image-6/341/135881248_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>樹洞<br><img src="https://1013399.github.io/image-6/341/135881255_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>可愛橙色蕈類<br><img src="https://1013399.github.io/image-6/341/135881324_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>「加油哦！尖山基點快到了！」指示牌<br><img src="https://1013399.github.io/image-6/341/135881260_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>攀爬極陡岩石區<br><img src="https://1013399.github.io/image-6/341/135881266_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>攀爬極陡箭竹林區<br><img src="https://1013399.github.io/image-6/341/135881270_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>不知是否為夫婦山<br><img src="https://1013399.github.io/image-6/341/135881321_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>看到尖山山頂了<br><img src="https://1013399.github.io/image-6/341/135881281_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>復興尖山<br><img src="https://1013399.github.io/image-6/341/135881308_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>復興尖山三等三角點<br><img src="https://1013399.github.io/image-6/341/135881290_l.jpg" alt="桃園 復興尖山三等三角點" loading="lazy"></p><p>復興尖山三等三角點No.6231<br><img src="https://1013399.github.io/image-6/341/135881300_l.jpg" alt="復興尖山三等三角點No.6231" loading="lazy"></p><p>左前方續行可往巴博庫魯山<br><img src="https://1013399.github.io/image-6/341/135881313_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><p>復興尖山山頂展望<br><img src="https://1013399.github.io/image-6/341/135881317_l.jpg" alt="桃園 復興尖山" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',35),c={href:"http://www.keepon.com.tw/ActiveSite/Article/One.asp?ArticleID=11081",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),m=i("img",{src:"https://1013399.github.io/image-6/341/135882936_l.jpg",alt:"桃園 復興尖山 參考地圖",loading:"lazy"},null,-1),_=i("br",null,null,-1),u=i("img",{src:"https://1013399.github.io/image-6/341/135379276_l.jpg",alt:"桃園 復興尖山 參考地圖",loading:"lazy"},null,-1);function j(y,z){const t=g("ExternalLinkIcon");return r(),p("div",null,[n,e(" more "),b,i("p",null,[a("原圖連結→"),i("a",c,[a("登山補給站(brownlee前輩航跡圖)"),o(t)]),d,m,_,u])])}const N=l(h,[["render",j],["__file","post-341-2009-10-19.html.vue"]]);export{N as default};
