import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as r,a as e,b as i,e as a,d as o,f as s}from"./app-4a074c75.js";const h={},n=i("p",null,[i("strong",null,"波津加山"),a("，海拔1772公尺，位於台中縣和平鄉大甲溪北岸，山頂有一顆三等三角點No.6583，登山步道全長約3.6K(不含捎來步道0.8K)，山徑陡峭，登山者稱之為「坡真崎」，為「谷關七雄」中的老四。")],-1),b=i("p",null,"波津加山登山口，在谷關溫泉區四季溫泉會館旁的捎來步道，距離波津加山登山口800公尺，沿途可欣賞谷關地區的風景，捎來步道最高點的涼亭，即為波津加山登山口，每0.5公里設有一里程木樁。",-1),c=s('<p>波津加山另有一登山口，在大雪山林道43Ｋ鞍馬山莊森林浴步道，由上往下走，落差約500公尺，此路線較為輕鬆，但需購票進入大雪山森林遊樂區。</p><hr><p>今日選擇谷關捎來步道起登，這段落差約140公尺的陡升步道，走來也頗為累人，到達高點涼亭後，會先下切至鞍部再一路上陡坡，「坡真崎」果然名不虛傳，2K至2.5K亂石堆最為難走，2.5K至3K則較為平緩，途中遇鹿港山友陳先生，腳程相近，便一道同行，沿途相聊甚歡，減緩了行程的疲憊，還嘗了一塊鹿港名產牛舌餅，真是感謝。</p><p>登山日期：2009/09/06</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>開車行駛國道一號，於台中系統交流道轉國道四號往豐原，至國道四號路底左轉接台3線(167K)左轉往東勢，過東勢大橋後右轉台8線往谷關，至台8線34.5K後，左下過谷關大橋往四季溫泉會館行駛，到四季溫泉會館後在路邊停車。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→四季溫泉會館(捎來步道)<br> →觀景涼亭(叉路，左往溫泉文化館，取右往波津加山登山口)<br> →波津加山登山口(旁有步道說明牌)<br> →鞍部(俯瞰右下方天輪壩、左下方地下水道沉澱池)<br> →第一處木椅<br> →波津加山步道0.5K木樁(往前數公尺有石椅休息小平台)<br> →波津加山步道1.0K木樁<br> →第二處木椅<br> →波津加山步道1.5K木樁<br> →波津加山步道2.0K木樁(開始陡上亂石區)<br> →叉路(右為Z字路，取左捷徑)<br> →波津加山步道2.5K木樁(路徑轉為平緩好走)<br> →涼亭(寬廣平地，再此休息午餐)<br> →波津加山步道3.0K木樁(又開始陡坡)<br> →第三處木椅(展望佳)<br> →大石區(設有鐵架和繩索，方便攀爬)<br> →波津加山步道3.5K木樁<br> →<strong>波津加山</strong>(山頂空間不大，基點後方有空地木椅，可供休息)<br> →原路折返<br> →回到午餐涼亭<br> →回到登山口觀景涼亭<br> →回到四季溫泉會館</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>登山時間(含休息)：上山約4小時(從四季溫泉會館處至波津加山)、下山約3小時</p><p>高低落差：約1030公尺(含稍來步道)</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>四季溫泉會館旁的捎來步道口<br><img src="https://1013399.github.io/image-6/345/132931520_l.jpg" alt="四季溫泉會館旁的捎來步道口" loading="lazy"></p><p>捎來步道口<br><img src="https://1013399.github.io/image-6/345/132931523_l.jpg" alt="捎來步道口" loading="lazy"></p><p>捎來步道(轉為石板步道)<br><img src="https://1013399.github.io/image-6/345/132931525_l.jpg" alt="捎來步道" loading="lazy"></p><p>捎來步道(一路陡上)<br><img src="https://1013399.github.io/image-6/345/132931527_l.jpg" alt="捎來步道" loading="lazy"></p><p>捎來步道(繼續陡上)<br><img src="https://1013399.github.io/image-6/345/132931572_l.jpg" alt="捎來步道" loading="lazy"></p><p>觀景涼亭叉路(波津加山登山口在涼亭後方)<br><img src="https://1013399.github.io/image-6/345/132931580_l.jpg" alt="波津加山" loading="lazy"></p><p>涼亭後方新天輪三角點No.13<br><img src="https://1013399.github.io/image-6/345/132931586_l.jpg" alt="波津加山" loading="lazy"></p><p>涼亭後方水資會大甲36基石<br><img src="https://1013399.github.io/image-6/345/132931629_l.jpg" alt="波津加山" loading="lazy"></p><p>波津加山登山口<br><img src="https://1013399.github.io/image-6/345/132931635_l.jpg" alt="波津加山" loading="lazy"></p><p>鞍部俯瞰左下方地下水道沉澱池<br><img src="https://1013399.github.io/image-6/345/132931639_l.jpg" alt="波津加山 地下水道沉澱池" loading="lazy"></p><p>鞍部俯瞰右下方天輪壩<br><img src="https://1013399.github.io/image-6/345/132931645_l.jpg" alt="波津加山 天輪壩" loading="lazy"></p><p>波津加山步道0.5K木樁<br><img src="https://1013399.github.io/image-6/345/132931648_l.jpg" alt="波津加山" loading="lazy"></p><p>石椅休息小平台<br><img src="https://1013399.github.io/image-6/345/132931655_l.jpg" alt="波津加山" loading="lazy"></p><p>波津加山步道1.5K木樁<br><img src="https://1013399.github.io/image-6/345/132931663_l.jpg" alt="波津加山" loading="lazy"></p><p>波津加山步道2.0K木樁<br><img src="https://1013399.github.io/image-6/345/132931702_l.jpg" alt="波津加山" loading="lazy"></p><p>陡上亂石區<br><img src="https://1013399.github.io/image-6/345/132931708_l.jpg" alt="波津加山" loading="lazy"></p><p>亂石區(往回拍攝)<br><img src="https://1013399.github.io/image-6/345/132931716_l.jpg" alt="波津加山" loading="lazy"></p><p>叉路(左右皆可，左為捷徑)<br><img src="https://1013399.github.io/image-6/345/132931756_l.jpg" alt="波津加山" loading="lazy"></p><p>波津加山步道2.5K木樁<br><img src="https://1013399.github.io/image-6/345/132931759_l.jpg" alt="波津加山" loading="lazy"></p><p>休息涼亭(午餐)<br><img src="https://1013399.github.io/image-6/345/132931763_l.jpg" alt="波津加山" loading="lazy"></p><p>波津加山步道3.0K木樁<br><img src="https://1013399.github.io/image-6/345/132931767_l.jpg" alt="波津加山" loading="lazy"></p><p>又開始陡坡<br><img src="https://1013399.github.io/image-6/345/132931868_l.jpg" alt="波津加山" loading="lazy"></p><p>第三處木椅處展望佳<br><img src="https://1013399.github.io/image-6/345/132931872_l.jpg" alt="波津加山" loading="lazy"></p><p>大石區<br><img src="https://1013399.github.io/image-6/345/132931940_l.jpg" alt="波津加山" loading="lazy"></p><p>大石區鐵架<br><img src="https://1013399.github.io/image-6/345/132931956_l.jpg" alt="波津加山" loading="lazy"></p><p>波津加山步道3.5K木樁<br><img src="https://1013399.github.io/image-6/345/132932063_l.jpg" alt="波津加山" loading="lazy"></p><p>最後一段大石區<br><img src="https://1013399.github.io/image-6/345/132932077_l.jpg" alt="波津加山" loading="lazy"></p><p><strong>波津加山</strong>(山頂空間不大)<br><img src="https://1013399.github.io/image-6/345/132932081_l.jpg" alt="波津加山" loading="lazy"></p><p>波津加山三等三角點No.6583<br><img src="https://1013399.github.io/image-6/345/132932085_l.jpg" alt="波津加山" loading="lazy"></p><p>波津加山基點後方木椅休息區<br><img src="https://1013399.github.io/image-6/345/132932088_l.jpg" alt="波津加山" loading="lazy"></p><p>波津加山山頂附近展望<br><img src="https://1013399.github.io/image-6/345/132931500_l.jpg" alt="波津加山山頂附近展望" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',43),m={href:"http://www.keepon.com.tw/ActiveSite/Article/One.asp?ArticleID=14782",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-6/345/132932199_l.jpg",alt:"波津加山 參考地圖",loading:"lazy"},null,-1);function u(j,y){const t=l("ExternalLinkIcon");return p(),r("div",null,[n,b,e(" more "),c,i("p",null,[a("原圖連結→"),i("a",m,[a("登山補給站(mori前輩航跡圖)"),o(t)]),d,_])])}const f=g(h,[["render",u],["__file","post-345-2009-09-07.html.vue"]]);export{f as default};
