import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as l,c as o,a as r,b as g,e as i,d as s,f as e}from"./app-9ec59630.js";const h={},n=g("p",null,[g("strong",null,"南子吝步道"),i("，位於新北市瑞芳區，全長約990公尺，是一條觀山賞海的小型步道，沿途可俯視南雅奇岩景觀，東望鼻頭角、西眺基隆嶼，適合全家大小同遊。")],-1),m=g("p",null,[g("strong",null,"南子吝山"),i("，又稱大埔尾山，海拔196公尺，位於南子吝步道終點，山頂有一星狀指標小平台和土地調查局圖根點基石，山頂展望絕佳，能俯瞰南雅聚落，眺望苦苓嶺、無耳茶壺山、半平山、燦光寮山等，遠眺水湳洞、基隆嶼、陰陽海、基隆山和鼻頭角的岬灣美景。")],-1),c=g("p",null,[g("strong",null,"南子吝東峰"),i("，海拔約180公尺，由南子吝步道900公尺指標前轉彎處進入，為一處臨崖小空地，無防護設施必須小心，下方即濱海公路的明隧道，可一覽南雅奇岩風光。")],-1),b=e('<p>走走日期：2016/12/31</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道一號北上，於大華系統交流道接台62線，行駛至瑞濱端出口接台2線(北部濱海公路)右轉，至台2線81.2Km南雅公車站牌旁停車場停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→南雅停車場<br><img src="https://1013399.github.io/image-1/35/1150310018_l.jpg" alt="IMAG0784.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→停車場旁步道解說牌<br><img src="https://1013399.github.io/image-1/35/1150307533_l.jpg" alt="IMAG0785-86.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→穿越馬路<br><img src="https://1013399.github.io/image-1/35/1150308415_l.jpg" alt="IMAG0789.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→南新宮，供奉天上聖母媽祖，先借用廟旁廁所<br><img src="https://1013399.github.io/image-1/35/1150309617_l.jpg" alt="IMAG0790.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→續行往相反方向，依指標往南子吝步道<br><img src="https://1013399.github.io/image-1/35/1150309818_l.jpg" alt="IMAG0791.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→經過民宅、涼亭後右轉<br><img src="https://1013399.github.io/image-1/35/1150309618_l.jpg" alt="IMAG0792.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→南子吝步道口<br><img src="https://1013399.github.io/image-1/35/1150308720_l.jpg" alt="IMAG0793.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→步道口里程牌(之後每100公尺設一塊)<br><img src="https://1013399.github.io/image-1/35/1150308416_l.jpg" alt="IMAG0794.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→步道經過欽賢國中後方圍牆<br><img src="https://1013399.github.io/image-1/35/1150309103_l.jpg" alt="IMAG0796.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→陡上木棧道<br><img src="https://1013399.github.io/image-1/35/1150308530_l.jpg" alt="IMAG0797-98.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→過了步道200公尺，有座觀景小平台<br><img src="https://1013399.github.io/image-1/35/1150307534_l.jpg" alt="IMAG0800-01.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→觀景平台展望<br><img src="https://1013399.github.io/image-1/35/1150307469_l.jpg" alt="IMAG0801A.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→續行，木棧道結束轉石階步道<br><img src="https://1013399.github.io/image-1/35/1150308131_l.jpg" alt="IMAG0802.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→石階步道之字型緩上<br><img src="https://1013399.github.io/image-1/35/1150309518_l.jpg" alt="IMAG0804.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→休憩涼亭，在此休息<br><img src="https://1013399.github.io/image-1/35/1150308721_l.jpg" alt="IMAG0805.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→休息後續行，回望鼻頭角岬灣<br><img src="https://1013399.github.io/image-1/35/1150309307_l.jpg" alt="IMAG0806.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→叉路，大約在步道890公尺處，右可往南子吝東峰，先取左往南子吝山<br><img src="https://1013399.github.io/image-1/35/1150307836_l.jpg" alt="IMAG0807.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→步道900公尺，南子吝山就在前方不遠處<br><img src="https://1013399.github.io/image-1/35/1150308722_l.jpg" alt="IMAG0808.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→叉路，左可往石梯坑山，取右續行<br><img src="https://1013399.github.io/image-1/35/1150309519_l.jpg" alt="IMAG0809.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→南子吝山山頂<br><img src="https://1013399.github.io/image-1/35/1150308132_l.jpg" alt="IMAG0810.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→南子吝山，土地調查局圖根點<br><img src="https://1013399.github.io/image-1/35/1150308133_l.jpg" alt="IMAG0812-13.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→遠眺基隆山、陰陽海<br><img src="https://1013399.github.io/image-1/35/1150307470_l.jpg" alt="IMAG0814.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→眺望無耳茶壺山、半平山<br><img src="https://1013399.github.io/image-1/35/1150309308_l.jpg" alt="IMAG0815.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→眺望苦苓嶺<br><img src="https://1013399.github.io/image-1/35/1150310019_l.jpg" alt="IMAG0816.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→遠眺鼻頭角岬灣<br><img src="https://1013399.github.io/image-1/35/1150307536_l.jpg" alt="IMAG0817.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→南子吝山，山頂解說牌<br><img src="https://1013399.github.io/image-1/35/1150307837_l.jpg" alt="IMAG0818.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→望海全景圖<br><img src="https://1013399.github.io/image-1/35/1150309520_l.jpg" alt="IMAG0819.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→原路折返<br><img src="https://1013399.github.io/image-1/35/1150308531_l.jpg" alt="IMAG0820.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→回到東峰叉路，右回步道口，取直一探東峰<br><img src="https://1013399.github.io/image-1/35/1150307838_l.jpg" alt="IMAG0821.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→東峰入口處之石椅上指標<br><img src="https://1013399.github.io/image-1/35/1150308532_l.jpg" alt="IMAG0822.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→穿越芒草山徑，抵達南子吝東峰，無基石，臨崖危險，賞景後原路回<br><img src="https://1013399.github.io/image-1/35/1150308134_l.jpg" alt="IMAG0823.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→回到欽賢國中後圍牆<br><img src="https://1013399.github.io/image-1/35/1150307839_l.jpg" alt="IMAG0824.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→回到南子吝步道口<br><img src="https://1013399.github.io/image-1/35/1150309619_l.jpg" alt="IMAG0825.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→回到南雅停車場，到附近的南雅奇岩參觀<br><img src="https://1013399.github.io/image-1/35/1150309620_l.jpg" alt="IMAG0826.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→南雅奇岩，為砂岩中的節理風化作用形成<br><img src="https://1013399.github.io/image-1/35/1150309309_l.jpg" alt="IMAG0827-32.jpg - 新北瑞芳南子吝步道" loading="lazy"></p><p>→參觀完南雅奇岩，至九份老街逛逛</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：南子吝步道一般來回約1小時，此次漫步行程花了約1.5小時</p><p>高低落差：約180公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',43),d={href:"http://www.tonyhuang39.com/tony0454/tony0454.html",target:"_blank",rel:"noopener noreferrer"},j=g("br",null,null,-1),_=g("img",{src:"https://1013399.github.io/image-1/35/1150309104_l.jpg",alt:"瑞芳南子吝山-Tony.GIF - 新北瑞芳南子吝步道",loading:"lazy"},null,-1);function u(y,I){const t=p("ExternalLinkIcon");return l(),o("div",null,[n,m,c,r(" more "),b,g("p",null,[i("原圖連結→"),g("a",d,[i("Tony的自然人文旅記"),s(t)]),j,_])])}const G=a(h,[["render",u],["__file","post-35-2017-01-13.html.vue"]]);export{G as default};
