import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as p,c as r,a as e,b as a,f as i,d as o,e as s}from"./app-19acc492.js";const h={},n=a("p",null,[a("strong",null,"鬼澤山"),i("，又名熊頭山，海拔1040公尺，三等三角點No.6272，山字森林三角點，位於新竹縣五峰鄉竹林村和平部落，沿途擁有優美山林景色，可眺望整個部落景色，遇上晴朗的好天氣時，更能望見五峰全景，可說是和平部落的最高點。")],-1),b=s('<p>鬼澤山是日治時期取名，當初命名原因不可考，可能是沿用日警或採集樟腦日商的名字而來，也可能是當初日人覺得這裡環境很差，所以命名為鬼澤。當地原住民祖先對這座山叫做熊頭山，熊是他們泰雅族非常尊敬的動物，把牠當作山神來看待，所以鬼澤山沒有鬼，是在地人尊敬的山頭。(參考網路資料)</p><p>部落居民最近利用多元就業方案，將古道整理出來，清除妨礙登山雜草、倒竹，路徑寬廣好走(一路都有廣興兩公婆所繫的路條，不怕迷失)，陡坡處設有竹欄杆方便攀爬，山頂更設有二排長條竹椅供登山客休息，沿途竹林、杉木林、雜木林交替，林相變化豐富，惟竹林間蚊子稍多，並有蛇出沒，需小心注意。</p><p>登山日期：2009/07/19</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>竹東市區轉122縣道往五峰，約36.8K處叉路取左往和平部落(左前方有台塑加油站)，下行約1K通過「和平橋」後取右上往和平部落，續行約2.7K叉路取左往「谷燕鳟魚場」指標行，續行約0.3K叉路即可見「鬼澤山步道」指示牌(「鬼」字不見了)，於指示牌前大水塔旁空地停車(其實仍可往右上水泥產道續開，於轉彎處附近空地停車)。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→停車處(旁有「頭目的家」民宿，鬼澤山就在眼前，也可回頭望北埔五指山)<br> →叉路(右水泥產道往上有鐵柵門，續直行)<br> →水泥產道終止(改為泥土產道)<br> →果園(續行穿越果園)<br> →登山口(泥土產道終點)<br> →石塊區(有一大石，平坦如桌)<br> →竹欄杆(開始陡上)<br> →竹林區(坡度趨緩，在此發現一尾青蛇)<br> →山字水泥柱(快到山頂了)<br> →鬼澤山(已起霧，視野不佳，但模糊中仍可見和平部落及上坪溪)<br> →原路折返<br> →回到登山口<br> →回到果園<br> →回到停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>登山時間(含休息)：從停車處起登，上山約1.5小時、下山約1小時</p><p>高低落差：停車處至登山口約150公尺、登山口至三角點約210公尺</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>122縣道約36.8K處叉路取左下往和平部落<br><img src="https://1013399.github.io/image-6/351/129077785_l.jpg" alt="122縣道約36.8K處叉路" loading="lazy"></p><p>和平部落(上方即為「頭目的家」)<br><img src="https://1013399.github.io/image-6/351/129077801_l.jpg" alt="和平部落" loading="lazy"></p><p>鬼澤山步道指示牌(取右上)<br><img src="https://1013399.github.io/image-6/351/129077806_l.jpg" alt="鬼澤山步道指示牌" loading="lazy"></p><p>鬼澤山就在眼前<br><img src="https://1013399.github.io/image-6/351/129077808_l.jpg" alt="鬼澤山" loading="lazy"></p><p>回頭望北埔五指山<br><img src="https://1013399.github.io/image-6/351/129077830_l.jpg" alt="北埔五指山" loading="lazy"></p><p>水泥產道叉路(取直行)<br><img src="https://1013399.github.io/image-6/351/129077834_l.jpg" alt="鬼澤山" loading="lazy"></p><p>此轉彎處約可停三部車<br><img src="https://1013399.github.io/image-6/351/129077837_l.jpg" alt="鬼澤山" loading="lazy"></p><p>水泥產道兩側竹林<br><img src="https://1013399.github.io/image-6/351/129077839_l.jpg" alt="鬼澤山" loading="lazy"></p><p>水泥產道終止改泥土產道(前方即果園)<br><img src="https://1013399.github.io/image-6/351/129077843_l.jpg" alt="鬼澤山" loading="lazy"></p><p>果園中甜柿已結果<br><img src="https://1013399.github.io/image-6/351/129077846_l.jpg" alt="鬼澤山" loading="lazy"></p><p>泥土產道<br><img src="https://1013399.github.io/image-6/351/129077869_l.jpg" alt="鬼澤山" loading="lazy"></p><p>泥土產道終止即登山口<br><img src="https://1013399.github.io/image-6/351/129077873_l.jpg" alt="鬼澤山" loading="lazy"></p><p>大石平坦如桌<br><img src="https://1013399.github.io/image-6/351/129077967_l.jpg" alt="鬼澤山" loading="lazy"></p><p>竹欄杆(開始陡上)<br><img src="https://1013399.github.io/image-6/351/129077978_l.jpg" alt="鬼澤山" loading="lazy"></p><p>這裡更陡<br><img src="https://1013399.github.io/image-6/351/129077990_l.jpg" alt="鬼澤山" loading="lazy"></p><p>竹林區(坡度趨緩)<br><img src="https://1013399.github.io/image-6/351/129078001_l.jpg" alt="鬼澤山 竹林區" loading="lazy"></p><p>青蛇(行動緩慢，不知是否有毒)<br><img src="https://1013399.github.io/image-6/351/129078093_l.jpg" alt="鬼澤山 青蛇" loading="lazy"></p><p>青蛇(先讓它走)<br><img src="https://1013399.github.io/image-6/351/129078176_l.jpg" alt="鬼澤山 青蛇" loading="lazy"></p><p>鬼澤山<br><img src="https://1013399.github.io/image-6/351/129078229_l.jpg" alt="鬼澤山" loading="lazy"></p><p>山字水泥柱<br><img src="https://1013399.github.io/image-6/351/129078275_l.jpg" alt="鬼澤山" loading="lazy"></p><p>鬼澤山三等三角點及森林三角點<br><img src="https://1013399.github.io/image-6/351/129078282_l.jpg" alt="鬼澤山三等三角點及森林三角點" loading="lazy"></p><p>三等三角點No6272及山字森林三角點<br><img src="https://1013399.github.io/image-6/351/129078288_l.jpg" alt="三等三角點No6272及山字森林三角點" loading="lazy"></p><p>俯瞰和平部落及上坪溪<br><img src="https://1013399.github.io/image-6/351/129078294_l.jpg" alt="和平部落及上坪溪" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',34),c={href:"http://designteng.googlepages.com/980110-03.jpg",target:"_blank",rel:"noopener noreferrer"},d=a("br",null,null,-1),m=a("img",{src:"https://1013399.github.io/image-6/351/129078314_l.jpg",alt:"鬼澤山 參考地圖",loading:"lazy"},null,-1);function _(u,j){const t=g("ExternalLinkIcon");return p(),r("div",null,[n,e(" more "),b,a("p",null,[i("原圖連結→"),a("a",c,[i("小鄧的登山札記"),o(t)]),d,m])])}const f=l(h,[["render",_],["__file","post-351-2009-07-20.html.vue"]]);export{f as default};
