import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o as l,c as o,a as g,b as a,f as t,d as p,e as n}from"./app-c5577115.js";const s={},h=a("p",null,[a("strong",null,"夫婦山"),t("是由兩座緊鄰的尖峰組成，屬拉拉山山稜西延二尖起山峰，遠望好似夫婦相對得名，在雲端依偎永不分離，遠望極為顯眼，較矮的是婦山，較高的是有基點的夫山，為「復興三尖」之一(復興尖、那結山、夫婦山)。")],-1),c=n('<p><strong>夫山</strong>，海拔1870公尺，二等三角點No.1534，復興三尖老大，展望極佳，遠眺西丘斯山，遙望大桃園地區，續行可往上巴陵、拉拉山。<br>     <strong>婦山</strong>，海拔1780公尺，無基石，展望稍差。</p><p>登山日期：2009/07/12</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>開車由北二高於關西下交流道，轉118縣道(羅馬公路)至59.3K接台7線北橫公路，左往大溪，取右往巴陵，於31.1K之雪霧隧道前左轉往下，下到河谷經雪霧鬧橋，沿產業道路而上，沿主線道行，遇叉路大都走右線，經利民商店處取右往上，再遇叉路右轉，產業道路盡頭即為夫婦山登山口，當地原住民新設有停車場，每次收費50元。今日天氣良好，多路人馬齊聚一堂，約停了30部大小車輛，熱鬧非常。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→停車場(在此可眺望群山，展望極佳，夫婦山亦在眼前)<br> →大空地(不知有何用途)<br> →登山口(開始即陡上)<br> →小山頭(視野豁然開闊)<br> →緩坡後又陡上<br> →<strong>婦山</strong>(喘息賞景，苗栗山友在此煮麵條，享用午餐)<br> →續行(陡下約80公尺)<br> →鞍部(狹窄、無空地可供休息再續行，由此再陡上約170公尺)<br> →展望點(回頭望婦山)<br> →<strong>夫山</strong>(展望極佳，眺望群山，但炎熱無蔭)<br> →空地樹蔭涼爽處休息、午餐(續往前行約10公尺)<br> →原路折返<br> →回到婦山<br> →返回停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>登山時間(含休息)：來回約5小時</p><p>高低落差：登山口至婦山約370公尺，婦山至夫山先陡下80公尺再陡上170公尺</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>新設停車場<br><img src="https://1013399.github.io/image-6/352/128484747_l.jpg" alt="夫婦山 停車場" loading="lazy"></p><p>停車場仰望夫婦山<br><img src="https://1013399.github.io/image-6/352/128484795_l.jpg" alt="夫婦山" loading="lazy"></p><p>停車場遠眺群山<br><img src="https://1013399.github.io/image-6/352/128484844_l.jpg" alt="夫婦山" loading="lazy"></p><p>沿途多段箭竹窄徑<br><img src="https://1013399.github.io/image-6/352/128484850_l.jpg" alt="夫婦山" loading="lazy"></p><p>婦山<br><img src="https://1013399.github.io/image-6/352/128484941_l.jpg" alt="夫婦山 婦山" loading="lazy"></p><p>往夫山途中展望點回頭望婦山<br><img src="https://1013399.github.io/image-6/352/128484946_l.jpg" alt="夫婦山 婦山" loading="lazy"></p><p>展望點景色<br><img src="https://1013399.github.io/image-6/352/128484953_l.jpg" alt="夫婦山 展望點景色" loading="lazy"></p><p>展望點遠眺群山<br><img src="https://1013399.github.io/image-6/352/128484961_l.jpg" alt="夫婦山" loading="lazy"></p><p>展望點景色<br><img src="https://1013399.github.io/image-6/352/128484968_l.jpg" alt="夫婦山 展望點景色" loading="lazy"></p><p>巨石<br><img src="https://1013399.github.io/image-6/352/128484976_l.jpg" alt="夫婦山 巨石" loading="lazy"></p><p>夫山<br><img src="https://1013399.github.io/image-6/352/128484979_l.jpg" alt="夫婦山 夫山" loading="lazy"></p><p>夫山二等三角點<br><img src="https://1013399.github.io/image-6/352/128484989_l.jpg" alt="夫山二等三角點" loading="lazy"></p><p>夫山二等三角點No.1534<br><img src="https://1013399.github.io/image-6/352/128484997_l.jpg" alt="夫山二等三角點No.1534" loading="lazy"></p><p>續行可往上巴陵、拉拉山<br><img src="https://1013399.github.io/image-6/352/128485005_l.jpg" alt="夫婦山" loading="lazy"></p><p>北橫之星(位於台7線約27k處，回程在此參觀休息、上廁所)<br><img src="https://1013399.github.io/image-6/352/128485008_l.jpg" alt="北橫之星" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',25),d={href:"http://www.yougoipay.com/kenny/w679/index.htm",target:"_blank",rel:"noopener noreferrer"},b=a("br",null,null,-1),m=a("img",{src:"https://1013399.github.io/image-6/352/128485129_l.jpg",alt:"夫婦山 參考地圖",loading:"lazy"},null,-1);function _(u,y){const i=e("ExternalLinkIcon");return l(),o("div",null,[h,g(" more "),c,a("p",null,[t("原圖連結→"),a("a",d,[t("蕭郎：獨步山林間"),p(i)]),b,m])])}const z=r(s,[["render",_],["__file","post-352-2009-07-15.html.vue"]]);export{z as default};
