import{_ as i}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o as n,c as o,a as l,b as a,e as t,d as s,f as h}from"./app-4a074c75.js";const p={},g=a("p",null,[a("strong",null,"新寮瀑布步道"),t("，位於宜蘭縣冬山鄉新寮二路底，從林務局冬山工作站入口到瀑布水潭，全長900多公尺，海拔120～220公尺，步道平緩，沿途多溪水及動植物景觀，是一條舒適的林蔭溪谷步道，可享受森林浴、吸收芬多精、陰離子、瀑布水氣，溪谷有兩條單行道的繩索吊橋(飛瀑吊橋及清溪吊橋)。")],-1),d=h('<p>2007年林務局羅東管理處又往上游開闢步道可觀賞第二、第三及第四層瀑布，很不巧的是，再通往第二瀑布途中，便橋被落石擊中斷毀，已有施工人員在搶修中，據林務局人員說修復約需一週，因此未能欣賞到四層瀑布，有點可惜。</p><p><strong>2009宜蘭國際蘭雨節</strong>，於2009/07/11起正式開幕，分別由<strong>武荖坑風景區</strong>「冒險山林園區」、<strong>冬山河親水公園</strong>「夢幻星河園區」、<strong>頭城外澳海洋園區</strong>「活力海洋園區」，同步展開一系列活動，今日為開幕前一日，開放免費試玩，但似乎尚未準備就緒，而且天氣酷熱難耐，閒逛一圈踩踩水後，便前往大溪漁港採購漁貨，踏上歸途了。</p><p>走走日期：2009/07/10</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><h3 id="由台灣西部出發" tabindex="-1"><a class="header-anchor" href="#由台灣西部出發" aria-hidden="true">#</a> 由台灣西部出發</h3><p>國道3號北上接國道5號(北宜高速公路)，下羅東交流道，右轉鄉間小道至台9線後左轉往南，至冬山鄉後於台9線91.3K處右轉，依中山休閒農業區指標循宜34鄉道(義成路)前進，途中右轉丸山路，行至宜34鄉道4.1K遇叉路口左轉新寮路，途經杏輝藥廠，至中山福德廟左轉過新寮橋，右轉5公尺左側小入口為仁山自然步道入口(有大型導覽圖)，於冬山鄉立托兒所前，取右行駛堤防旁道路(新寮二路)往新寮瀑布，經仁山植物園停車場，至林務局冬山工作站前停車場停車。</p><h3 id="實際行車路線" tabindex="-1"><a class="header-anchor" href="#實際行車路線" aria-hidden="true">#</a> 實際行車路線</h3><p>自蘇澳出發，走台9線往北，過武荖坑風景區續往北行，遇見左側第一個台塑加油站時有座橋，過橋後左轉接義成路一段，約行2km後叉路左轉八寶路，再行1.2km接丸山路左轉，往前約500m叉路左轉新寮路，途經杏輝藥廠，至中山福德廟左轉過新寮橋，右轉5公尺左側小入口為仁山自然步道入口(有大型導覽圖)，於冬山鄉立托兒所前，取右行駛堤防旁道路(新寮二路)往新寮瀑布，經仁山植物園停車場，至林務局冬山工作站前停車場停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→停車場<br> →管制站(需登記領通行證)<br> →進入步道(左木屑道、右碎石道，步道旁新寮溪乾涸無水)<br> →新寮瀑布步道指示牌<br> →第一叉路(右可至清溪吊橋，但有禁止右轉標示牌，續直行)<br> →瀑布前叉路(<strong>新寮瀑布</strong>，右為飛瀑吊橋，取左木棧階梯往觀瀑平台)<br> →觀瀑平台(在此欣賞瀑布、拍照)<br> →回瀑布叉路(取左過吊橋)<br> →飛瀑吊橋<br> →過橋後遇叉路口(右可經清溪吊橋返回，取左Z字型山路陡上崖壁棧道)<br> →橋斷不通返回(途中可俯視瀑布、溪谷)<br> →回到吊橋叉路(下溪谷脫鞋泡泡腳、休息)<br> →清溪吊橋<br> →回到第一叉路口(取左返回)<br> →管制站(繳回通行證)<br> →回到停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間(含休息)：來回約1小時</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>林務局冬山工作站前停車場<br><img src="https://1013399.github.io/image-6/353/128484501_l.jpg" alt="林務局冬山工作站前停車場" loading="lazy"></p><p>管制站<br><img src="https://1013399.github.io/image-6/353/128484505_l.jpg" alt="林務局冬山工作站 管制站" loading="lazy"></p><p>新寮瀑布步道指示牌<br><img src="https://1013399.github.io/image-6/353/128484511_l.jpg" alt="新寮瀑布步道指示牌" loading="lazy"></p><p>新寮瀑布觀瀑平台<br><img src="https://1013399.github.io/image-6/353/128484514_l.jpg" alt="新寮瀑布觀瀑平台" loading="lazy"></p><p>新寮瀑布<br><img src="https://1013399.github.io/image-6/353/128484520_l.jpg" alt="新寮瀑布" loading="lazy"></p><p>飛瀑吊橋<br><img src="https://1013399.github.io/image-6/353/128484526_l.jpg" alt="飛瀑吊橋" loading="lazy"></p><p>崖壁棧道(往第二瀑布)<br><img src="https://1013399.github.io/image-6/353/128484530_l.jpg" alt="新寮瀑布步道" loading="lazy"></p><p>橋斷不通施工中<br><img src="https://1013399.github.io/image-6/353/128484538_l.jpg" alt="新寮瀑布步道" loading="lazy"></p><p>俯視觀瀑平台、吊橋、溪谷<br><img src="https://1013399.github.io/image-6/353/128484544_l.jpg" alt="新寮瀑布步道" loading="lazy"></p><p>冬山河(利澤簡橋)<br><img src="https://1013399.github.io/image-6/353/128484549_l.jpg" alt="冬山河(利澤簡橋)" loading="lazy"></p><p>冬山河親水公園<br><img src="https://1013399.github.io/image-6/353/128484555_l.jpg" alt="冬山河親水公園" loading="lazy"></p><p>2009宜蘭國際蘭雨節即將登場<br><img src="https://1013399.github.io/image-6/353/128484558_l.jpg" alt="2009宜蘭國際蘭雨節" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',26),c={href:"http://www.tonyhuang39.com/tony0498/tony0498.html",target:"_blank",rel:"noopener noreferrer"},b=a("br",null,null,-1),m=a("img",{src:"https://1013399.github.io/image-6/353/128484577_l.jpg",alt:"新寮瀑布步道 參考地圖",loading:"lazy"},null,-1);function _(u,f){const r=e("ExternalLinkIcon");return n(),o("div",null,[g,l(" more "),d,a("p",null,[t("原圖連結→"),a("a",c,[t("Tony的自然人文旅記"),s(r)]),b,m])])}const x=i(p,[["render",_],["__file","post-353-2009-07-14.html.vue"]]);export{x as default};
