import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{r as i,o as n,c as o,a as h,b as a,f as e,d as l,e as s}from"./app-6513a715.js";const d={},p=a("p",null,[a("strong",null,"拳頭姆步道"),e("，海拔310公尺，因小山頭外型酷似拳頭而得名，位於宜蘭縣三星鄉天送埤，2007年由林務局羅東林管處整建完成，整條步道穿梭在林蔭茂密的烏心石與肖楠造林區，途中有兩座觀景平台(蘭陽溪眺景平台、蘭陽平原眺景平台)可遠眺蘭陽平原與蘭陽溪谷，另有雀榕平台及小型生態池，步道全長1.8公里，以杉木作階梯，碎木屑鋪底，坡度平緩舒適，是一條老少咸宜的自然步道。")],-1),c=s('<p>走走日期：2009/07/09</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><h3 id="由台灣西部出發" tabindex="-1"><a class="header-anchor" href="#由台灣西部出發" aria-hidden="true">#</a> 由台灣西部出發</h3><p>國道3號北上接國道5號(北宜高速公路)，下羅東交流道後右轉台7丙線往羅東、三星，過天送埤後左轉續行台7丙線，至台7丙9.5K處拳頭姆步道入口旁停車。</p><h3 id="實際行車路線" tabindex="-1"><a class="header-anchor" href="#實際行車路線" aria-hidden="true">#</a> 實際行車路線</h3><p>上午走完松羅國家步道，前往天送埤品嘗聞名已久的三星卜肉後，從天送埤左轉台7丙線，至台7丙9.5K處拳頭姆步道入口旁停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→拳頭姆步道入口(有木製拳頭意象造景)<br> →拳頭姆步道起點<br> →第一叉路(直行接第二叉路，取右往蘭陽平原眺景平台)<br> →蘭陽平原眺景平台<br> →第二叉路(左後方往第一叉路，左前方往雀榕，取右續行步道)<br> →蘭陽溪眺景平台<br> →生態池(雜草密佈沒看到池水，步道0.9K處)<br> →第三叉路(直行回第二叉路，取右往雀榕)<br> →雀榕平台(旁有一顆雀榕，相當特殊)<br> →返回第三叉路<br> →回到第二叉路<br> →回到拳頭姆步道入口</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間(含休息)：來回約1小時</p><p>高低落差：約100公尺</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>拳頭姆步道入口<br><img src="https://1013399.github.io/image-6/354/128483423_l.jpg" alt="拳頭姆步道入口" loading="lazy"></p><p>拳頭姆步道入口旁天送埤導覽圖<br><img src="https://1013399.github.io/image-6/354/128483427_l.jpg" alt="拳頭姆步道入口旁天送埤導覽圖" loading="lazy"></p><p>拳頭姆步道起點<br><img src="https://1013399.github.io/image-6/354/128483534_l.jpg" alt="拳頭姆步道起點" loading="lazy"></p><p>拳頭姆步道起點旁步道導覽圖<br><img src="https://1013399.github.io/image-6/354/128483563_l.jpg" alt="拳頭姆步道起點旁步道導覽圖" loading="lazy"></p><p>蘭陽平原眺景平台<br><img src="https://1013399.github.io/image-6/354/128483625_l.jpg" alt="蘭陽平原眺景平台" loading="lazy"></p><p>遠眺蘭陽溪谷、泰雅大橋<br><img src="https://1013399.github.io/image-6/354/128483650_l.jpg" alt="蘭陽溪谷、泰雅大橋" loading="lazy"></p><p>雀榕<br><img src="https://1013399.github.io/image-6/354/128483656_l.jpg" alt="雀榕" loading="lazy"></p><p>雀榕平台<br><img src="https://1013399.github.io/image-6/354/128483699_l.jpg" alt="雀榕平台 拳頭姆自然步道" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',20),g={href:"http://www.sanshing.gov.tw/releaseRedirect.do?unitID=160&pageID=6711",target:"_blank",rel:"noopener noreferrer"},b=a("br",null,null,-1),_=a("img",{src:"https://1013399.github.io/image-6/354/128483772_l.jpg",alt:"拳頭姆自然步道 參考地圖",loading:"lazy"},null,-1);function m(u,f){const t=i("ExternalLinkIcon");return n(),o("div",null,[p,h(" more "),c,a("p",null,[e("原圖連結→"),a("a",g,[e("三星鄉公所網站"),l(t)]),b,_])])}const y=r(d,[["render",m],["__file","post-354-2009-07-13.html.vue"]]);export{y as default};
