import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o as l,c as o,a as n,b as a,e as t,d as p,f as g}from"./app-4a074c75.js";const s={},h=a("p",null,[a("strong",null,"松羅國家步道"),t("，位於宜蘭縣大同鄉玉蘭村，是羅東林管處在宜蘭縣內已開闢的六條國家級步道之一，係依昔日泰雅獵徑整修而成，全程長1.8公里，但欲抵達入口處之前必須先走一段前置路段約1.5km，前置路段炎熱無蔭；松羅步道則蔭涼舒適，因為位在松羅溪邊，所以松羅步道擁有其他步道無法相比的眾多濕生植物、眾多的附生植物、眾多的水濱昆蟲、鳥類，還有眾多的蕨類，行走期間，隨時都能聽到溪水潺潺，從開放以來，就一直是很熱門的健行路線，每逢假日都能吸引眾多遊客前來。")],-1),c=g('<p>走走日期：2009/07/09</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號北上接國道5號(北宜高速公路)，宜蘭交流道下高速公路右轉大福路(192縣道)，大福路底接中山路(台9線)左轉，過宜蘭橋後右轉接環河路，環河路底再接員山路(台7線)，經大同加油站行駛至台7線97.7k松羅橋頭前，取右循堤防旁產業道路往松羅國家步道，約1km後至車輛管制站前的停車場停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→停車場<br> →管制站<br> →松羅國家步道看板(旁邊溪谷有很多苦花魚，在此賞魚、休息)<br> →自來水淨水廠<br> →松羅國家步道入口(旁有導覽解說牌)<br> →木橋<br> →繩索吊橋<br> →第一叉路(左為山線路徑，取右溪邊路徑)<br> →與山線路徑會合<br> →第二叉路(左為山線路徑，再取右溪邊路徑)<br> →與山線路徑會合<br> →第三叉路(左為山線路徑，仍取右溪邊路徑)<br> →一線天石階<br> →<strong>巨石瀑布</strong>(終點，取左繞回)<br> →回到第三叉路口(取直行)<br> →回到第二叉路口(取直行)<br> →回到第一叉路口(取直行)<br> →回到繩索吊橋<br> →回到步道入口<br> →回到停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間(含休息)：來回約2.5小時</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>松羅國家步道看板(賞魚處)<br><img src="https://1013399.github.io/image-6/355/128482720_l.jpg" alt="松羅國家步道看板(賞魚處) " loading="lazy"></p><p>自來水淨水廠<br><img src="https://1013399.github.io/image-6/355/128482840_l.jpg" alt="自來水淨水廠" loading="lazy"></p><p>松羅國家步道入口<br><img src="https://1013399.github.io/image-6/355/128482851_l.jpg" alt="松羅國家步道入口" loading="lazy"></p><p>導覽解說牌<br><img src="https://1013399.github.io/image-6/355/128482860_l.jpg" alt="松羅國家步道導覽解說牌" loading="lazy"></p><p>木橋<br><img src="https://1013399.github.io/image-6/355/128482869_l.jpg" alt="松羅國家步道" loading="lazy"></p><p>步道一景<br><img src="https://1013399.github.io/image-6/355/128482928_l.jpg" alt="松羅國家步道" loading="lazy"></p><p>「幹」上開花解說牌<br><img src="https://1013399.github.io/image-6/355/128482936_l.jpg" alt="松羅國家步道" loading="lazy"></p><p>繩索吊橋<br><img src="https://1013399.github.io/image-6/355/128482943_l.jpg" alt="松羅國家步道" loading="lazy"></p><p>松羅溪一景<br><img src="https://1013399.github.io/image-6/355/128483013_l.jpg" alt="松羅溪" loading="lazy"></p><p>漂亮的珊瑚狀蕈類<br><img src="https://1013399.github.io/image-6/355/128483020_l.jpg" alt="松羅國家步道 珊瑚狀蕈類" loading="lazy"></p><p>另一處珊瑚狀蕈類<br><img src="https://1013399.github.io/image-6/355/128483030_l.jpg" alt="松羅國家步道 珊瑚狀蕈類" loading="lazy"></p><p>一線天石階<br><img src="https://1013399.github.io/image-6/355/128483077_l.jpg" alt="松羅國家步道 一線天石階" loading="lazy"></p><p>巨石瀑布<br><img src="https://1013399.github.io/image-6/355/128483159_l.jpg" alt="松羅國家步道 巨石瀑布" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',22),d={href:"http://www.tonyhuang39.com/tony0387.html",target:"_blank",rel:"noopener noreferrer"},b=a("br",null,null,-1),m=a("img",{src:"https://1013399.github.io/image-6/355/128483203_l.jpg",alt:"松羅國家步道",loading:"lazy"},null,-1);function _(u,f){const i=e("ExternalLinkIcon");return l(),o("div",null,[h,n(" more "),c,a("p",null,[t("原圖連結→"),a("a",d,[t("Tony的自然人文旅記"),p(i)]),b,m])])}const z=r(s,[["render",_],["__file","post-355-2009-07-13.html.vue"]]);export{z as default};
