import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r,o,c as n,a as s,b as t,f as a,d as i,e as p}from"./app-2f66486a.js";const h={},g=t("p",null,"新竹地區共有三座鳥嘴山，分別是尖石內鳥嘴山、關西外鳥嘴山及五峰鳥嘴山。",-1),c=t("p",null,[t("strong",null,"五峰鳥嘴山"),a("，海拔1550公尺，位於新竹縣五峰鄉、苗栗縣南庄鄉交界，山頂有三等三角點6295號及頭前溪水源保護區界樁基石。")],-1),d=t("p",null,"往五峰鳥嘴山可從122縣道的35.5K、42.5K及50.4K處開始上山，行前事先詢問當地民宿得知，42.5K較為曲折難行，50.4K前桃山隧道正在施工中(平日有交通管制，假日放行)，所以決定從35.5K處右切上山(與前往山上人家路線相同)。",-1),_=t("hr",null,null,-1),b=t("p",null,[t("strong",null,"張學良故居園區"),a("由新竹縣政府斥資六千萬元打造，在西安事變七十二周年對外開放，張學良故居舊址原接近清泉部落1號吊橋旁，但當年被沖垮後幾乎未留下任何遺跡，僅餘部分房屋基礎，目前遺址處改建為小公園，重修的少帥故居則選擇建在上坪溪對岸的2號吊橋旁。這幢經參考張學良後人提供的照片所蓋出的日式黑瓦建築，雖因新建之故少了些古樸氣息，但洋溢和風典雅風情。")],-1),m=t("p",null,"目前館內陳列許多與張學良有關的史料，除以大事記敘述他的一生外，也透過地圖、影像資料等，呈現他一生顛沛流離的經過與際遇，及從1946年到1959年間少帥是如何在清泉度過他的山居歲月，而他離開清泉後轉被軟禁至高雄西子灣、台北北投等地的經過，也有相關照片陳列。館內還模擬張學良昔時書房景觀，並擺放有他讀書沉思的塑像，反映他當年在幽禁歲月中研讀明史等著作的情形。",-1),u=t("p",null,"張學良故居每周一休館，開放時間為上午八時至下午五時，免費參觀。",-1),f={href:"http://www.wretch.cc/blog/a4928159/25809951",target:"_blank",rel:"noopener noreferrer"},j=p('<p>登山日期：2009/06/20</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><h3 id="往鳥嘴山" tabindex="-1"><a class="header-anchor" href="#往鳥嘴山" aria-hidden="true">#</a> 往鳥嘴山</h3><p>竹東市區轉122縣道往五峰，於35.5K右轉上山，經十八兒、廢棄的大隘派出所之後，接茅圃產業道路左轉(右往南庄)，經鵝公髻山登山口、涼山、山上人家叉路續行往白蘭部落，沿著尤外巴棍農場指標前進，至巴頓農場叉路口(左往巴頓農場)，直行往鳥嘴山露營區，抵四叉路八角涼亭，車停於此。</p><h3 id="往清泉" tabindex="-1"><a class="header-anchor" href="#往清泉" aria-hidden="true">#</a> 往清泉</h3><p>自八角涼亭下山至白蘭部落後，叉路有指示往土場、觀霧右轉，一路直下接122縣道50.4K，左轉往竹東方向，至清泉叉路往右後直走便可到達。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→八角涼亭(此時還艷陽高照)<br> →氣象局自動雨量計(開始起霧)<br> →鳥嘴山露營區<br> →竹林登山口(陡坡，霧更濃了)<br> →稜線叉路(左往大窩山、比林山，取右往鳥嘴山，又是陡坡)<br> →<strong>鳥嘴山</strong>(右叉路往鵝公髻山180分，取左往觀景小山頭)<br> →觀景小山頭(霧茫茫一片，天氣好時應該視野不錯)<br> →原路折返<br> →回到鳥嘴山露營區(巧遇桃園大千登山隊山友，閒聊一會)<br> →八角涼亭</p><div class="hint-container note"><p class="hint-container-title">後記</p><p>這次行程本來預計是鳥嘴山、大窩山O型繞，因受蓮花颱風影響，天候不佳，加上山徑濕滑，於是決定登上鳥嘴山後原路折返，回程改由122縣道50.4K處下山前往清泉，順訪張學良故居。</p><p>回到八角涼亭休息更衣後，遇七、八位阿公級山友剛下山，得知鳥嘴山還有另一新登山口(網路上尚無資料)，沿途山徑較緩而且風景更美，建議我們前往一探登山口，於是開車前往看看(四叉路右轉，往地上「田」字白色噴漆方向)，並估計里程，發現似乎還不錯，從八角涼亭到登山口約800公尺左右，下回有機會再來走走。</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>八角涼亭<br><img src="https://1013399.github.io/image-6/359/126836313_l.jpg" alt="鳥嘴山 八角涼亭" loading="lazy"></p><p>氣象局自動雨量計<br><img src="https://1013399.github.io/image-6/359/126836322_l.jpg" alt="鳥嘴山 氣象局自動雨量計" loading="lazy"></p><p>鳥嘴山露營區<br><img src="https://1013399.github.io/image-6/359/126836334_l.jpg" alt="鳥嘴山露營區" loading="lazy"></p><p>往登山口<br><img src="https://1013399.github.io/image-6/359/126836339_l.jpg" alt="鳥嘴山" loading="lazy"></p><p>竹林登山口<br><img src="https://1013399.github.io/image-6/359/126836344_l.jpg" alt="鳥嘴山 竹林登山口" loading="lazy"></p><p>鳥嘴山<br><img src="https://1013399.github.io/image-6/359/126836350_l.jpg" alt="鳥嘴山" loading="lazy"></p><p>鳥嘴山三等三角點及頭前溪水源保護區界樁基石<br><img src="https://1013399.github.io/image-6/359/126836425_l.jpg" alt="鳥嘴山三等三角點及頭前溪水源保護區界樁基石" loading="lazy"></p><p>竹林中濃霧<br><img src="https://1013399.github.io/image-6/359/126836429_l.jpg" alt="鳥嘴山" loading="lazy"></p><p>張學良故居<br><img src="https://1013399.github.io/image-6/359/126836449_l.jpg" alt="清泉張學良故居" loading="lazy"></p><p>張學良故居<br><img src="https://1013399.github.io/image-6/359/126836577_l.jpg" alt="清泉張學良故居" loading="lazy"></p><p>張學良故居<br><img src="https://1013399.github.io/image-6/359/126836579_l.jpg" alt="清泉張學良故居" loading="lazy"></p><p>正在播放張學良事蹟影片<br><img src="https://1013399.github.io/image-6/359/126836582_l.jpg" alt="清泉張學良故居" loading="lazy"></p><p>清泉導覽圖<br><img src="https://1013399.github.io/image-6/359/126836593_l.jpg" alt="清泉導覽圖" loading="lazy"></p><p>清泉吊橋<br><img src="https://1013399.github.io/image-6/359/126836670_l.jpg" alt="清泉吊橋" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',25),z={href:"http://www.forest-park.biz/news/data/map12.jpg",target:"_blank",rel:"noopener noreferrer"},y=t("br",null,null,-1),w=t("img",{src:"https://1013399.github.io/image-6/359/121692031_l.jpg",alt:"鳥嘴山 參考地圖",loading:"lazy"},null,-1),x={href:"http://www.keepon.com.tw/ActiveSite/Article/One.asp?ArticleID=7012",target:"_blank",rel:"noopener noreferrer"},k=t("br",null,null,-1),K=t("img",{src:"https://1013399.github.io/image-6/359/126836779_l.jpg",alt:"鳥嘴山 參考地圖",loading:"lazy"},null,-1);function v(N,V){const e=r("ExternalLinkIcon");return o(),n("div",null,[g,c,d,s(" more "),_,b,m,u,t("p",null,[a("(參考資料："),t("a",f,[a("a4928159部落格"),i(e)]),a(")")]),j,t("p",null,[t("a",z,[a("山上人家森林農場網站"),i(e)]),y,w]),t("p",null,[t("a",x,[a("藍天圖集"),i(e)]),k,K])])}const E=l(h,[["render",v],["__file","post-359-2009-06-22.html.vue"]]);export{E as default};
