import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r,o as p,c as e,a as o,b as a,f as t,d as g,e as s}from"./app-2f66486a.js";const h={},n=a("p",null,[a("strong",null,"竹東森林公園"),t("，位於竹東鎮內西南後山，全山林木參天，並有普照宮大願寺等廟宇，可從公園路竹東國中圍牆邊登山口或沿普照宮後面小路可以到達山稜體能活動區，每天都有許多鎮民來此爬山或運動。")],-1),b=s('<p><strong>樹杞林山</strong>，海拔286公尺，三等三角點NO.101，位於新竹縣竹東鎮南方，登山口在公園路公園高分49電力電線桿附近。</p><p><strong>大鄉自然步道</strong>，位於新竹縣竹東鎮大鄉里，由社區熱心人士歷經多年的努力，塑造出一個良好的自然環境教育的場域，並由新竹荒野保護協會製作植物解說牌，附近的上館國小、大肚國小、山湖國小的老師們，也紛紛以這裡為自然教學基地，同時也成為每年4、5月賞油桐的新景點。</p><p>登山日期：2009/06/13</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國三下竹林交流道往竹東方向行駛，過竹林大橋後至上智小學四叉路，左轉接東寧路(縣道122)，至大同路右轉至竹東警察分局，左轉康寧街，過竹東國中後遇公園路再右轉，可將車停於鎮立圖書館、羽球館或普照宮。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→羽球館停車場<br> →普照宮(續直行往廁所方向)<br> →接森林公園登山步道<br> →雙涼亭、體能活動區(左有叉路往大鄉自然步道，續直行)<br> →大願寺<br> →接公園路(取左上行，約40~50度的柏油路)<br> →凱旋大地社區公園路入口<br> →公園高幹49電線杆(對面即為樹杞林山登山口，前方為竹東垃圾場)<br> →高壓電塔(新竹-竹東線037、龍水-竹東線021)<br> →叉路(過高壓電塔後約10公尺，雜草多、路跡不明顯，取右往三角點)<br> →<strong>樹杞林山</strong>(雜草多無視野，拍照後回保線路)<br> →高壓電塔(新竹-竹東線038、龍水-竹東線022)<br> →接大鄉自然步道(為O形步道，取右行)<br> →叉路(設有大鄉自然步道地圖，直往麻園湖出口，取左往樹屋)<br> →黃姓墓園(右側為探索教育區，設有多處木桌椅)<br> →樹屋(左回大鄉自然步道，取直往研究路)<br> →大鄉社區槌球場(路旁為竹東圳，球場旁有電流急急棒遊戲，以腳踏車發電)<br> →研究路59巷6弄(大鄉自然步道出口，續行研究路)<br> →左切研究路偶數號門牌道路(不需走到中豐路)<br> →研究路82號(取左廢棄豬寮小徑上山)<br> →回森林公園體能活動區<br> →普照宮<br> →羽球館停車場</p><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>竹東鎮立羽毛球館<br><img src="https://1013399.github.io/image-6/360/126317181_l.jpg" alt="竹東鎮立羽毛球館" loading="lazy"></p><p>普照宮<br><img src="https://1013399.github.io/image-6/360/126317199_l.jpg" alt="竹東鎮 普照宮" loading="lazy"></p><p>普照宮廁所往森林公園登山步道<br><img src="https://1013399.github.io/image-6/360/126317227_l.jpg" alt="竹東鎮 普照宮" loading="lazy"></p><p>森林公園登山步道<br><img src="https://1013399.github.io/image-6/360/126317241_l.jpg" alt="竹東森林公園登山步道" loading="lazy"></p><p>雙涼亭<br><img src="https://1013399.github.io/image-6/360/126317248_l.jpg" alt="竹東森林公園登山步道" loading="lazy"></p><p>體能活動區<br><img src="https://1013399.github.io/image-6/360/126317256_l.jpg" alt="竹東森林公園登山步道" loading="lazy"></p><p>往大鄉自然步道叉路<br><img src="https://1013399.github.io/image-6/360/126317393_l.jpg" alt="竹東森林公園登山步道" loading="lazy"></p><p>體能活動區<br><img src="https://1013399.github.io/image-6/360/126317399_l.jpg" alt="竹東森林公園登山步道" loading="lazy"></p><p>森林公園登山步道<br><img src="https://1013399.github.io/image-6/360/126317406_l.jpg" alt="竹東森林公園登山步道" loading="lazy"></p><p>森林公園登山步道往大願寺<br><img src="https://1013399.github.io/image-6/360/126317483_l.jpg" alt="竹東森林公園登山步道" loading="lazy"></p><p>往大願寺<br><img src="https://1013399.github.io/image-6/360/126317486_l.jpg" alt="竹東森林公園登山步道" loading="lazy"></p><p>羅狀元醒世詩歌<br><img src="https://1013399.github.io/image-6/360/126317489_l.jpg" alt="羅狀元醒世詩歌" loading="lazy"></p><p>大願寺<br><img src="https://1013399.github.io/image-6/360/126317526_l.jpg" alt="大願寺" loading="lazy"></p><p>大願寺前大佛<br><img src="https://1013399.github.io/image-6/360/126317536_l.jpg" alt="大願寺前大佛" loading="lazy"></p><p>凱旋大地社區公園路入口<br><img src="https://1013399.github.io/image-6/360/126317546_l.jpg" alt="凱旋大地社區公園路入口" loading="lazy"></p><p>樹杞林山登山口(如果只為一探基點，可停車於路旁，約10分鐘可到達)<br><img src="https://1013399.github.io/image-6/360/126317556_l.jpg" alt="樹杞林山登山口" loading="lazy"></p><p>高壓電塔<br><img src="https://1013399.github.io/image-6/360/126317567_l.jpg" alt="樹杞林山登山口 高壓電塔" loading="lazy"></p><p>高壓電塔(新竹-竹東線037、龍水-竹東線021)<br><img src="https://1013399.github.io/image-6/360/126317575_l.jpg" alt="樹杞林山登山口" loading="lazy"></p><p>此叉路往三角點<br><img src="https://1013399.github.io/image-6/360/126317625_l.jpg" alt="樹杞林山登山口" loading="lazy"></p><p>樹杞林山三等三角點<br><img src="https://1013399.github.io/image-6/360/126317722_l.jpg" alt="樹杞林山三等三角點" loading="lazy"></p><p>樹杞林山三等三角點NO.101<br><img src="https://1013399.github.io/image-6/360/126317761_l.jpg" alt="樹杞林山三等三角點NO.101" loading="lazy"></p><p>植物解說牌(大圓牌為中英文說明；小圓牌有問答題目，背後有解答)<br><img src="https://1013399.github.io/image-6/360/126317780_l.jpg" alt="樹杞林山 植物解說牌" loading="lazy"></p><p>往麻園湖出口叉路檳榔園<br><img src="https://1013399.github.io/image-6/360/126317788_l.jpg" alt="樹杞林山、大鄉自然步道" loading="lazy"></p><p>樹屋<br><img src="https://1013399.github.io/image-6/360/126317793_l.jpg" alt="樹杞林山 樹屋" loading="lazy"></p><p>樹屋叉路<br><img src="https://1013399.github.io/image-6/360/126317797_l.jpg" alt="樹杞林山 樹屋" loading="lazy"></p><p>竹東圳<br><img src="https://1013399.github.io/image-6/360/126317800_l.jpg" alt="竹東圳" loading="lazy"></p><p>竹東圳解說牌(大鄉社區槌球場旁)<br><img src="https://1013399.github.io/image-6/360/126317820_l.jpg" alt="竹東圳解說牌" loading="lazy"></p><p>大鄉社區槌球場<br><img src="https://1013399.github.io/image-6/360/126317840_l.jpg" alt="大鄉社區槌球場" loading="lazy"></p><p>電流急急棒遊戲(以腳踏車發電)<br><img src="https://1013399.github.io/image-6/360/126317881_l.jpg" alt="電流急急棒遊戲" loading="lazy"></p><p>研究路59巷6弄(大鄉自然步道出入口)<br><img src="https://1013399.github.io/image-6/360/126317885_l.jpg" alt="大鄉自然步道出入口 研究路59巷6弄" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><h3 id="竹東森林公園、樹杞林山" tabindex="-1"><a class="header-anchor" href="#竹東森林公園、樹杞林山" aria-hidden="true">#</a> 竹東森林公園、樹杞林山</h3>',40),d={href:"http://www.keepon.com.tw/Keepon/kpmt/blueskymap/P271.SWF",target:"_blank",rel:"noopener noreferrer"},c=a("br",null,null,-1),m=a("img",{src:"https://1013399.github.io/image-6/360/126318475_l.jpg",alt:"竹東森林公園登山步道、樹杞林山 參考地圖",loading:"lazy"},null,-1),_=a("h3",{id:"大鄉自然步道",tabindex:"-1"},[a("a",{class:"header-anchor",href:"#大鄉自然步道","aria-hidden":"true"},"#"),t(" 大鄉自然步道")],-1),u={href:"http://www.wretch.cc/blog/greengiant/7088341",target:"_blank",rel:"noopener noreferrer"},y=a("br",null,null,-1),j=a("img",{src:"https://1013399.github.io/image-6/360/126318467_l.jpg",alt:"大鄉自然步道 參考地圖",loading:"lazy"},null,-1);function z(f,x){const i=r("ExternalLinkIcon");return p(),e("div",null,[n,o(" more "),b,a("p",null,[a("a",d,[t("藍天圖集"),g(i)]),c,m]),_,a("p",null,[a("a",u,[t("大鄉社區大大的部落格"),g(i)]),y,j])])}const N=l(h,[["render",z],["__file","post-360-2009-06-15.html.vue"]]);export{N as default};
