import{_ as i}from"./plugin-vue_export-helper-c27b6911.js";import{r as o,o as l,c as n,a as s,b as t,f as a,d as e,e as g}from"./app-2f66486a.js";const h={},p=t("p",null,[t("strong",null,"石門山"),a("，海拔551公尺，位於龍潭石門水庫旁，又稱小竹坑山，山頂有一等三角點及一顆水源點基石，往前約30公尺另有殖產局三角補點，山頂寬廣視野遼闊，該山名列台灣小百岳#024。")],-1),d=t("p",null,[t("strong",null,"石門山中峰"),a("，海拔510公尺，位於石門山與太平山兩山之間，又稱石門山東北峰、太平山西北峰或清水坑山，山頂有土木局三號水泥柱基石。")],-1),c=g('<p><strong>太平山</strong>，海拔535公尺，位於龍潭石門水庫旁，太平山距水庫較近，位於石門山東方。</p><p>石門山有多處入口，主要登山口為石門山牌樓、勞工育樂中心及小粗坑古道，山路四通八達，沿路林蔭茂密，可俯瞰大漢溪及石門水庫。<br>     石門山以前來過二次，都是從石門山牌樓起登，這次本想從小粗坑古道上山，開著車沒注意到崑崙藥用植物園叉路，竟糊裡糊塗來到勞工育樂中心，於是改從此處上山走走，下次有機會再走走小粗坑古道。</p><p>登山日期：2009/05/31</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>北二高下關西交流道，經關西接台三線往北，到龍泉右轉接台三乙線往石門水庫，過崑崙藥用植物園叉路，看到勞工育樂中心指示牌後右轉即可抵達勞工育樂中心停車場。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→勞工育樂中心停車場(由柵欄處進入啟登)<br> →叉路(直走往石門水庫，取右上往觀音像)<br> →叉路(左右皆可，左為健康道，取右平安道)<br> →觀音聖像(展望不錯，旁有攤販)<br> →龍穴崗(展望也不錯)<br> →叉路(左右皆可上石門山，有指示牌寫左20分、右40分，取右)<br> →<strong>石門山</strong>(直走往小粗坑古道，取左下叉路往太平山)<br> →叉路(左往觀音像，右往錢龍休息站，取直續行)<br> →叉路(直行往太平山，取左上往石門山中峰一探)<br> →<strong>石門山中峰</strong>(直行下山往民治路，折返)<br> →回叉路口(取左續往太平山)<br> →叉路(取左陡坡往太平山)<br> →<strong>太平山</strong>(再往前有電塔，續行往好漢坡下山)<br> →原路折返<br> →回到觀音像叉路(取右往觀音像下山)<br> →龍穴崗<br> →觀音像(取直走健康道階梯下山)<br> →勞工育樂中心停車場</p><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>勞工育樂中心停車場<br><img src="https://1013399.github.io/image-6/362/125018239_l.jpg" alt="石門山 勞工育樂中心停車場" loading="lazy"></p><p>觀音聖像<br><img src="https://1013399.github.io/image-6/362/125018243_l.jpg" alt="石門山 觀音聖像" loading="lazy"></p><p>龍穴崗<br><img src="https://1013399.github.io/image-6/362/125018247_l.jpg" alt="石門山 龍穴崗" loading="lazy"></p><p>叉路(左右皆可上石門山)<br><img src="https://1013399.github.io/image-6/362/125018249_l.jpg" alt="石門山" loading="lazy"></p><p>石門山<br><img src="https://1013399.github.io/image-6/362/125018310_l.jpg" alt="石門山" loading="lazy"></p><p>石門山一等三角點及水源點基石<br><img src="https://1013399.github.io/image-6/362/125018313_l.jpg" alt="石門山一等三角點及水源點基石" loading="lazy"></p><p>石門山展望不錯<br><img src="https://1013399.github.io/image-6/362/125018315_l.jpg" alt="石門山展望" loading="lazy"></p><p>石門山殖產局三角補點<br><img src="https://1013399.github.io/image-6/362/125018320_l.jpg" alt="石門山殖產局三角補點" loading="lazy"></p><p>遠眺太平山電塔<br><img src="https://1013399.github.io/image-6/362/125018327_l.jpg" alt="石門山遠眺太平山電塔" loading="lazy"></p><p>石門山中峰(清水坑山)<br><img src="https://1013399.github.io/image-6/362/125018333_l.jpg" alt="石門山中峰(清水坑山)" loading="lazy"></p><p>石門山中峰土木局三號基石<br><img src="https://1013399.github.io/image-6/362/125018380_l.jpg" alt="石門山中峰土木局三號基石" loading="lazy"></p><p>太平山<br><img src="https://1013399.github.io/image-6/362/125018387_l.jpg" alt="太平山" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><h3 id="石門山勞工育樂中心" tabindex="-1"><a class="header-anchor" href="#石門山勞工育樂中心" aria-hidden="true">#</a> 石門山勞工育樂中心</h3>',22),b={href:"http://shimen-shan.emmm.tw/sys/club_tra/map/map_2801_0b.jpg",target:"_blank",rel:"noopener noreferrer"},_=t("br",null,null,-1),m=t("img",{src:"https://1013399.github.io/image-6/362/125069227_l.jpg",alt:"石門山勞工育樂中心參考路線圖",loading:"lazy"},null,-1),u=t("h3",{id:"石門山地圖",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#石門山地圖","aria-hidden":"true"},"#"),a(" 石門山地圖")],-1),f={href:"http://www.tonyhuang39.com/tony0452/tony0452.html",target:"_blank",rel:"noopener noreferrer"},y=t("br",null,null,-1),j=t("img",{src:"https://1013399.github.io/image-6/362/125018697_l.jpg",alt:"石門山、石門山中峰、太平山 登山參考地圖",loading:"lazy"},null,-1);function z(x,k){const r=o("ExternalLinkIcon");return l(),n("div",null,[p,d,s(" more "),c,t("p",null,[t("a",b,[a("石門山勞工育樂中心網站"),e(r)]),_,m]),u,t("p",null,[t("a",f,[a("Tony的自然人文旅記"),e(r)]),y,j])])}const w=i(h,[["render",z],["__file","post-362-2009-06-01.html.vue"]]);export{w as default};
