import{_ as i}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o as l,c as o,a as p,b as a,e as t,d as g,f as s}from"./app-39f3f472.js";const n={},h=a("p",null,[a("strong",null,"十八尖山"),t("，海拔132公尺，位於新竹市東南郊，聯勤陸補三等三角點179號，是由十八個淺峰山頭連綿而成的丘陵地帶，因而得名，此山名列台灣小百岳#026。")],-1),c=a("p",null,"十八尖山擁有豐富的自然環境資源，樹木高大，古木參天，沿途遍山茂林，花卉飄香，行進於林廕道，上空氣清新怡人，且山內建築步道，運動設施俱全，山林內的石觀音(僅剩23尊)，也是觀賞的重點。",-1),b=s('<p>十八尖山有兩個入口，一個在博愛路，另一個則在寶山路。</p><p>登山日期：2009/05/24</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>新竹交流道下，往市區方向，沿光復路直行，過馬偕醫院後於學府路左轉，再左轉博愛路經新竹高商、自來水管理處，可抵達十八尖山公園東入口(設有免費立體停車場)。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>十八尖山主要由一條柏油路車道貫穿，沿途階梯步道、泥土山徑穿插其中，可自行取捨、搭配路線，以下路線僅供參考。</p><p>→停車場(對面有座獅子亭，獅子亭旁有變葉木步道，後有階梯步道直上主峰，取左行柏油路車道)<br> →賞花月牌樓(旁有十八尖山公園石碑、導覽圖，車輛禁止進入)<br> →重力基準站(旁有花鐘)<br> →第二座獅子亭<br> →叉路(車道大轉彎處，右上土階步道可往十八尖山東峰，續直行)<br> →幸福亭(很多人再此跳舞，取左往稜線而上)<br> →介壽亭<br> →國父百年誕辰紀念亭<br> →<strong>十八尖山</strong>(基石在國父百年誕辰紀念亭旁碉堡頂)<br> →接回原先的柏油路<br> →梅花亭<br> →休憩點叉路(右可經觀音亭接變葉木步道回停車場，有人在此合奏唱歌)<br> →益壽亭<br> →早起會廣場(快樂健身亭)<br> →折返<br> →回到休憩點叉路(取左往變葉木步道)<br> →觀音亭<br> →獅子亭<br> →停車場</p><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>綠色長廊(柏油路車道)<br><img src="https://1013399.github.io/image-6/365/124362649_l.jpg" alt="十八尖山 綠色長廊 柏油路車道" loading="lazy"></p><p>賞花月牌樓(配合主題，每年不一樣)<br><img src="https://1013399.github.io/image-6/365/124362660_l.jpg" alt="十八尖山 賞花月牌樓" loading="lazy"></p><p>十八尖山公園石碑<br><img src="https://1013399.github.io/image-6/365/124362667_l.jpg" alt="十八尖山公園石碑" loading="lazy"></p><p>重力基準站與花鐘<br><img src="https://1013399.github.io/image-6/365/124362672_l.jpg" alt="十八尖山 國家重力基準站" loading="lazy"></p><p>十八尖山東峰叉路(100/08/13補拍)<br><img src="https://1013399.github.io/image-6/365/194928692_l.jpg" alt="十八尖山東峰叉路" loading="lazy"></p><p>十八尖山東峰陸補三角點No.56(100/08/13補拍)<br><img src="https://1013399.github.io/image-6/365/194928699_l.jpg" alt="十八尖山東峰陸補三角點No.56" loading="lazy"></p><p>馬界基石(100/08/13補拍)<br><img src="https://1013399.github.io/image-6/365/194928694_l.jpg" alt="十八尖山 馬界基石" loading="lazy"></p><p>幸福亭<br><img src="https://1013399.github.io/image-6/365/124362678_l.jpg" alt="十八尖山 幸福亭" loading="lazy"></p><p>國父百年誕辰紀念亭<br><img src="https://1013399.github.io/image-6/365/124362719_l.jpg" alt="國父百年誕辰紀念亭" loading="lazy"></p><p>十八尖山基石(站在國父百年誕辰紀念亭拍攝)<br><img src="https://1013399.github.io/image-6/365/124362721_l.jpg" alt="十八尖山基石" loading="lazy"></p><p>十八尖山基石(爬上碉堡拍攝)<br><img src="https://1013399.github.io/image-6/365/124362750_l.jpg" alt="十八尖山基石" loading="lazy"></p><p>十八尖山聯勤陸補三等三角點179號<br><img src="https://1013399.github.io/image-6/365/124362778_l.jpg" alt="十八尖山聯勤陸補三等三角點179號" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',21),d={href:"http://18park.hccg.gov.tw/index.asp",target:"_blank",rel:"noopener noreferrer"},_=a("br",null,null,-1),m=a("img",{src:"https://1013399.github.io/image-6/365/124362792_l.jpg",alt:"十八尖山、十八尖山東峰 參考地圖",loading:"lazy"},null,-1);function u(f,j){const r=e("ExternalLinkIcon");return l(),o("div",null,[h,c,p(" more "),b,a("p",null,[a("a",d,[t("十八尖山公園主題網"),g(r)]),_,m])])}const x=i(n,[["render",u],["__file","post-365-2009-05-25.html.vue"]]);export{x as default};
