import{_ as p}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as e,c as s,a as n,b as t,f as i,d as o,w as h,e as a}from"./app-a9847bbb.js";const c={},b=t("p",null,[t("strong",null,"志繼山"),i("，海拔977公尺，又稱詩格山，位於桃園縣復興鄉與台北縣三峽鎮交界處，山頂有一顆山字森林三角點基石及一顆台北州稅務課基石，登山口位於東眼山林道旁。")],-1),d=t("p",null,[t("strong",null,"東眼山"),i("，海拔1212公尺，位於桃園縣復興鄉東眼山森林遊樂區內，山頂有三等三角點#6261基石，從阿姆坪遠眺此山，酷似一顆向東閃爍的大眼睛，故名東眼山，該山名列台灣小百岳#022。")],-1),_=a('<p>登山日期：2009/05/23</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>北二高下大溪交流道，左轉台3線往大溪方向再接台7線，先後經大溪、頭寮、慈湖、三民、復興後，於台7線17.7K處左轉桃119縣道進入東眼山產業道路，119縣道終止後接東眼山林道，至東眼山林道1K處(非119縣道1K處)路旁停車，此處即志繼山登山口，旁有「霞雲分線#143」電線桿。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→志繼山登山口<br> →叉路(直走亦可但較遠，取右竹林上陡坡)<br> →稜線(取右循稜線上行，有二段陡坡)<br> →山字基石<br> →<strong>志繼山</strong>(有叉路，左往水車寮、八仙橋、東鹿山，取直行)<br> →叉路(右陡下忘憂谷，續直行，山路平緩)<br> →小山頭<br> →另一小山頭<br> →東眼山自導式步道左線(旁有涼亭，取左往東眼山三角點，開始下雨了)<br> →休憩平台<br> →<strong>東眼山</strong>三角點(右下往東眼山餐廳，取直上)<br> →眺望平台(有望遠鏡可觀看台北101，但煙雨濛濛無展望)<br> →回東眼山三角點(取左下山往東眼山餐廳)<br> →休憩區叉路(右可接自導式步道左線，續直走)<br> →造型公廁(左接森林知性步道，因雨不再入內一遊，取右行)<br> →東眼山餐廳停車場(直走景觀步道往遊客中心)<br> →叉路(沿途遇多個叉路口皆取直行)<br> →遊客中心<br> →遊樂區大門收費站(踢林道往志繼山登山口)<br> →志繼山登山口</p>',5),m={class:"hint-container note"},u=t("p",{class:"hint-container-title"},"後記",-1),y=t("p",null,"回程途中買了一些木耳，本也想買些五月桃，老婆下車後驚見踏腳板上，有一隻吸血吸得鼓鼓的螞蝗，再看到腳踝附近有兩個小洞血流不止，五月桃也不買了，以衛生紙壓住傷口止血，開車開了約一小時回到老婆娘家，血還在流，最後以蘆薈塗抹才止住流血。",-1),j=t("p",null,"後來上網查有關螞蝗的小常識，才知螞蝗吸血時會分泌毒液，有麻醉效果及抗凝血因子，所以被咬時才會渾然不知，血流不止，以後爬山遇下雨天或走潮濕路徑時，應特別注意。",-1),z=a('<h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>山字基石(未到志繼山前)<br><img src="https://1013399.github.io/image-6/366/124320007_l.jpg" alt="山字基石" loading="lazy"></p><p>志繼山山字森林三角點基石<br><img src="https://1013399.github.io/image-6/366/124320085_l.jpg" alt="志繼山山字森林三角點基石" loading="lazy"></p><p>志繼山台北州稅務課基石<br><img src="https://1013399.github.io/image-6/366/124320091_l.jpg" alt="志繼山台北州稅務課基石" loading="lazy"></p><p>東眼山自導式步道左線指標<br><img src="https://1013399.github.io/image-6/366/124320101_l.jpg" alt="東眼山自導式步道左線指標" loading="lazy"></p><p>東眼山自導式步道左線<br><img src="https://1013399.github.io/image-6/366/124320109_l.jpg" alt="東眼山自導式步道左線" loading="lazy"></p><p>東眼山自導式步道左線旁涼亭<br><img src="https://1013399.github.io/image-6/366/124320115_l.jpg" alt="東眼山自導式步道左線旁涼亭" loading="lazy"></p><p>東眼山<br><img src="https://1013399.github.io/image-6/366/124320122_l.jpg" alt="東眼山" loading="lazy"></p><p>東眼山三等三角點<br><img src="https://1013399.github.io/image-6/366/124320136_l.jpg" alt="東眼山三等三角點" loading="lazy"></p><p>東眼山三等三角點#6261<br><img src="https://1013399.github.io/image-6/366/124320217_l.jpg" alt="東眼山三等三角點#6261" loading="lazy"></p><p>眺望平台<br><img src="https://1013399.github.io/image-6/366/124320221_l.jpg" alt="東眼山 眺望平台" loading="lazy"></p><p>煙雨濛濛<br><img src="https://1013399.github.io/image-6/366/124320231_l.jpg" alt="東眼山" loading="lazy"></p><p>造型公廁<br><img src="https://1013399.github.io/image-6/366/124320239_l.jpg" alt="東眼山 造型公廁" loading="lazy"></p><p>遊客中心<br><img src="https://1013399.github.io/image-6/366/124320302_l.jpg" alt="東眼山 遊客中心" loading="lazy"></p><p>東眼山森林遊樂區<br><img src="https://1013399.github.io/image-6/366/124320330_l.jpg" alt="東眼山森林遊樂區" loading="lazy"></p><p>遊樂區大門收費站<br><img src="https://1013399.github.io/image-6/366/124320492_l.jpg" alt="東眼山森林遊樂區大門" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',17),f={href:"http://www.yougoipay.com/kenny/w701/index.htm",target:"_blank",rel:"noopener noreferrer"},x=t("br",null,null,-1),k=t("img",{src:"https://1013399.github.io/image-6/366/124320697_l.jpg",alt:"志繼山、東眼山 參考地圖",loading:"lazy"},null,-1),w=a('<p>補2011/12/07重登志繼山、東眼山，順訪卡外山相片</p><p>東眼山遠眺台北101<br><img src="https://1013399.github.io/image-6/366/204958450_l.jpg" alt="志繼山、東眼山" loading="lazy"></p><p>東眼山遠眺八里觀音山<br><img src="https://1013399.github.io/image-6/366/204958451_l.jpg" alt="志繼山、東眼山" loading="lazy"></p><p>東眼山遠眺大屯山、七星山<br><img src="https://1013399.github.io/image-6/366/204958452_l.jpg" alt="志繼山、東眼山" loading="lazy"></p><p>東眼山林道轉彎處涼亭(生痕化石前)<br><img src="https://1013399.github.io/image-6/366/204958455_l.jpg" alt="志繼山、東眼山" loading="lazy"></p><p>卡外山登山口(涼亭右側約10公尺)<br><img src="https://1013399.github.io/image-6/366/204958446_l.jpg" alt="志繼山、東眼山" loading="lazy"></p><p>卡外山森林三角點<br><img src="https://1013399.github.io/image-6/366/204958453_l.jpg" alt="志繼山、東眼山" loading="lazy"></p><p>卡外山森林三角點<br><img src="https://1013399.github.io/image-6/366/204958454_l.jpg" alt="志繼山、東眼山" loading="lazy"></p>',8);function N(V,v){const r=l("RouterLink"),g=l("ExternalLinkIcon");return e(),s("div",null,[b,d,n(" more "),_,t("div",m,[u,y,j,t("p",null,[i("相關文章可參閱："),o(r,{to:"/posts/post-363-2009-06-01.html"},{default:h(()=>[i("【轉貼】認識螞蝗")]),_:1}),i("。")])]),z,t("p",null,[t("a",f,[i("蕭郎：獨步山林間"),o(g)]),x,k]),w])}const C=p(c,[["render",N],["__file","post-366-2009-05-25.html.vue"]]);export{C as default};
