import{_ as o}from"./plugin-vue_export-helper-c27b6911.js";import{r as i,o as g,c as n,a as p,b as t,e as a,d as r,w as s,f as h}from"./app-4a074c75.js";const c={},b=t("p",null,[t("strong",null,"虎山"),a("，又稱虎子山，海拔1492公尺，三等三角點#6634，位於苗栗泰安鄉，與上島山、橫龍山合稱水雲三星，是水雲三星的老大。山稜延伸至加里山再轉向南陡降，南面為大斷崖，造型特殊，從泰安溫泉遙望虎山，峭立挺拔、尖聳參天，特殊的山形像極了大霸尖山，以猛虎仰天長嘯的姿態，崢嶸出眾、氣勢雄偉的獨霸一方，有「山的大霸尖山」美譽。")],-1),d=t("p",null,"虎山擁有獨特粗獷的風貌，攀走一趟能親身經歷樣貌多變的植物林相等生態景觀。沿途有崩崖塌地的拉繩攀爬、有幹溪河床的亂石跨登、有土石樹根的陡坡蹭蹬，以及有緩坡腰繞的林蔭幽徑等各種山徑。登山口附近還有高掛山壁的水雲瀑布，更增添美麗色彩，豐富了爬山行程。",-1),_=t("p",null,"登山日期：2009/05/09",-1),m=t("h2",{id:"行車路線",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#行車路線","aria-hidden":"true"},"#"),a(" 行車路線")],-1),u=h('<p>中山高苗栗交流道下，往苗栗市區方向約1公里，再左轉台72快速道路往汶水。快速道路終點後右轉即為台三線，於127.7K「7-11便利商店」前，左轉苗62鄉道往泰安溫泉，沿途遇叉路，循「泰安溫泉」指標前行，過13K「虎山橋」後，即抵達泰安溫泉風景區大型停車場。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→泰安溫泉停車場(循碎石車道步行進入)<br> →木橋<br> →叉路(左上往觀景涼亭，續直行碎石路)<br> →水雲吊橋(碎石路結束，此處可停五、六輛車)<br> →木棧道<br> →叉路(右下往水雲瀑布，取左上往虎山)<br> →竹叢(開始平緩山腰路)<br> →溪谷(不過溪左陡上往虎山，沿途滿地油桐花)<br> →稜肩(遠眺上島山)<br> →岩石山溝(石塊陡坡)<br> →稜線叉路(右往加里山，取左往虎山)<br> →<strong>虎山</strong><br> →稜線叉路(取直行往加里山方向，從另一山路回溪谷，路程雖較長，但比較輕鬆)<br> →鞍部叉路(直行上坡往加里山，取右下，山路時陡時緩，途中可仰望虎山側面)<br> →溪谷(過溪後，接上原路)<br> →竹叢<br> →水雲瀑布叉路口(聽山友說來回約一小時，不去水雲瀑布了)<br> →水雲吊橋(發現一處細長瀑布)<br> →停車場</p><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>碎石路上仰望虎山英姿<br><img src="https://1013399.github.io/image-6/368/123116835_l.jpg" alt="虎山" loading="lazy"></p><p>水雲吊橋<br><img src="https://1013399.github.io/image-6/368/123116840_l.jpg" alt="水雲吊橋" loading="lazy"></p><p>木棧道<br><img src="https://1013399.github.io/image-6/368/123116851_l.jpg" alt="虎山 木棧道" loading="lazy"></p><p>水雲瀑布叉路口<br><img src="https://1013399.github.io/image-6/368/123116910_l.jpg" alt="水雲瀑布叉路口" loading="lazy"></p><p>竹叢<br><img src="https://1013399.github.io/image-6/368/123116913_l.jpg" alt="虎山" loading="lazy"></p><p>遠眺上島山<br><img src="https://1013399.github.io/image-6/368/123116919_l.jpg" alt="上島山" loading="lazy"></p><p>溪谷<br><img src="https://1013399.github.io/image-6/368/123116983_l.jpg" alt="虎山 溪谷" loading="lazy"></p><p>滿地油桐花<br><img src="https://1013399.github.io/image-6/368/123117018_l.jpg" alt="虎山 油桐花" loading="lazy"></p><p>陡上<br><img src="https://1013399.github.io/image-6/368/123117119_l.jpg" alt="虎山" loading="lazy"></p><p>遠眺上島山<br><img src="https://1013399.github.io/image-6/368/123117128_l.jpg" alt="上島山" loading="lazy"></p><p>稜線叉路<br><img src="https://1013399.github.io/image-6/368/123117136_l.jpg" alt="虎山" loading="lazy"></p><p>虎山三等三角點<br><img src="https://1013399.github.io/image-6/368/123117140_l.jpg" alt="虎山三等三角點" loading="lazy"></p><p>虎山三等三角點#6634<br><img src="https://1013399.github.io/image-6/368/123117145_l.jpg" alt="虎山三等三角點#6634" loading="lazy"></p><p>仰望虎山側面<br><img src="https://1013399.github.io/image-6/368/123117152_l.jpg" alt="虎山" loading="lazy"></p><p>回程在水雲吊橋上發現細長瀑布<br><img src="https://1013399.github.io/image-6/368/123117232_l.jpg" alt="虎山 瀑布" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',20),y={href:"http://www.tonyhuang39.com/tony0662/tony0662.html",target:"_blank",rel:"noopener noreferrer"},f=t("br",null,null,-1),j=t("img",{src:"https://1013399.github.io/image-6/368/123117261_l.jpg",alt:"虎山 參考地圖",loading:"lazy"},null,-1);function z(x,k){const l=i("RouterLink"),e=i("ExternalLinkIcon");return g(),n("div",null,[b,p(" more "),d,_,m,t("p",null,[a("(同"),r(l,{to:"/posts/post-376-2009-03-16.html"},{default:s(()=>[a("上島山")]),_:1}),a(")")]),u,t("p",null,[t("a",y,[a("Tony的自然人文旅記"),r(e)]),f,j])])}const V=o(c,[["render",z],["__file","post-368-2009-05-11.html.vue"]]);export{V as default};
