import{_ as o}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as r,c as s,a as h,b as i,d as a,w as e,f as t,e as n}from"./app-7f62a6ff.js";const d={},c=i("p",null,"今日天候不如預期的艷陽天，一直都是雲霧深鎖，甚至飄起霧雨，到達北大武山頂，一片霧茫茫，沒能欣賞到層層山巒美景，更遑論遠眺太平洋、巴士海峽和台灣海峽，但是連續二天在3.8K的喜多麗斷崖，欣賞夕陽、雲海、雲瀑美景，算是值回票價。",-1),m=n('<h2 id="實際行程" tabindex="-1"><a class="header-anchor" href="#實際行程" aria-hidden="true">#</a> 實際行程</h2><ul><li>凌晨04:30起床</li><li>05:00早餐、整理背包(輕裝)</li><li>05:45出發06:20展望點(4.8K)</li><li>06:50大武神木(5.25K)</li><li>07:45最後水源(6.3K)</li><li>08:35南大武山叉路(7.3K)</li><li>09:20高砂義勇軍紀念碑</li><li>09:25大武祠(8K)</li><li>10:35北大武山(9K)</li><li>11:20原路下山</li><li>15:15回到檜谷山莊</li></ul><p>走走日期：2016/12/03</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→05:45，輕裝出發，約5分鐘行至步道4K叉路，取左往北大武山<br><img src="https://1013399.github.io/image-1/37/1147347135_l.jpg" alt="IMGP0247.JPG - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→依舊陡上，來到步道4.5K<br><img src="https://1013399.github.io/image-1/37/1147346865_l.jpg" alt="IMGP0248-52.JPG - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→拉繩陡上<br><img src="https://1013399.github.io/image-1/37/1147347726_l.jpg" alt="IMGP0253-54.JPG - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→展望點，約在步道4.8K處<br><img src="https://1013399.github.io/image-1/37/1147346671_l.jpg" alt="IMGP0255.JPG - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→穿過繩網眺望南大武山，但被雲霧遮蔽<br><img src="https://1013399.github.io/image-1/37/1147345980_l.jpg" alt="IMGP0256.JPG - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→過了步道5K，經過疊石、鐵梯，繼續陡上<br><img src="https://1013399.github.io/image-1/37/1147347423_l.jpg" alt="IMGP0257-63.JPG - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→大武神木(紅檜，樹齡約800至1000年)，約在步道5.25K處<br><img src="https://1013399.github.io/image-1/37/1147347424_l.jpg" alt="IMGP0265A.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→陸續經過步道5.5K、6K<br><img src="https://1013399.github.io/image-1/37/1147347530_l.jpg" alt="IMGP0267-70.JPG - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→途中展望山景的朦朧美<br><img src="https://1013399.github.io/image-1/37/1147347727_l.jpg" alt="IMGP0270A.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→水盆水源<br><img src="https://1013399.github.io/image-1/37/1147346672_l.jpg" alt="IMGP0272.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→進入鐵杉林與箭竹的針葉純林帶<br><img src="https://1013399.github.io/image-1/37/1147346866_l.jpg" alt="IMGP0272A.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→石壁鐵缸水源，為最後水源，約在步道6.3K處(往回拍攝)<br><img src="https://1013399.github.io/image-1/37/1147348218_l.jpg" alt="IMGP0273.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→繼續陡上，經過步道6.5K、7K<br><img src="https://1013399.github.io/image-1/37/1147345981_l.jpg" alt="IMGP0278-81.JPG - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→樹型優美的鐵杉，好似盆景<br><img src="https://1013399.github.io/image-1/37/1147348220_l.jpg" alt="IMGP0284.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→踩鐵環橫渡岩壁<br><img src="https://1013399.github.io/image-1/37/1147345982_l.jpg" alt="IMGP0285.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→步道7.3K，上稜線，叉路，右往南大武山，取左往北大武山<br><img src="https://1013399.github.io/image-1/37/1147346796_l.jpg" alt="IMGP0287.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→二度橫渡岩壁(往回拍攝)<br><img src="https://1013399.github.io/image-1/37/1147347137_l.jpg" alt="IMGP0289.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→到達高砂義勇軍紀念碑<br><img src="https://1013399.github.io/image-1/37/1147347425_l.jpg" alt="IMGP0298.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→紀念碑為日本政府所立，以紀念當年徵召出征南洋的原住民<br><img src="https://1013399.github.io/image-1/37/1147346797_l.jpg" alt="IMGP0299.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→再往前行即到達大武遺址(大武祠)，位於步道8K，大武祠是日本興建的神社<br>   鳥居已倒，排灣族人在此祭祀祖靈，每年的大武山成年禮也在此舉行<br><img src="https://1013399.github.io/image-1/37/1147347532_l.jpg" alt="IMGP0300-02.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→續往下行往北大武山，沿途枯木各具特色<br><img src="https://1013399.github.io/image-1/37/1147346868_l.jpg" alt="IMGP0304-09.JPG - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→上上下下經過數個山頭，到達北大武山，拍張攻頂合照<br><img src="https://1013399.github.io/image-1/37/1147347533_l.jpg" alt="IMGP0312.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→北大武山，一等三角點<br><img src="https://1013399.github.io/image-1/37/1147345983_l.jpg" alt="IMGP0313.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→山頂一片霧茫茫，無景可賞，在此休息、午餐<br><img src="https://1013399.github.io/image-1/37/1147348124_l.jpg" alt="IMGP0314.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→原路下山，經過大武祠後，回到步道7.3K叉路，取右下<br><img src="https://1013399.github.io/image-1/37/1147347534_l.jpg" alt="IMGP0699.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→回到步道6.3K水源處，取水帶回送人<br><img src="https://1013399.github.io/image-1/37/1147347140_l.jpg" alt="IMGP0703.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→回到大武神木<br><img src="https://1013399.github.io/image-1/37/1147348126_l.jpg" alt="IMGP0704.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→回到步道4.8K展望點<br><img src="https://1013399.github.io/image-1/37/1147348127_l.jpg" alt="IMGP0712.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→再穿過繩網賞景，南大武山依舊躲在雲層中<br><img src="https://1013399.github.io/image-1/37/1147348128_l.jpg" alt="IMGP0713A.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→回到步道4K叉路，取右回檜谷山莊<br><img src="https://1013399.github.io/image-1/37/1147347142_l.jpg" alt="IMGP0715.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→15:15，回到檜谷山莊<br><img src="https://1013399.github.io/image-1/37/1147347248_l.jpg" alt="IMGP0716.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→休息、整理裝備後，循廁所旁小徑下溪床到瀑布一遊<br><img src="https://1013399.github.io/image-1/37/1147348020_l.jpg" alt="IMGP0719.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><p>→16:10，再回步道3.8K喜多麗斷崖欣賞落日<br><img src="https://1013399.github.io/image-1/37/1147347144_l.jpg" alt="IMGP0720-23.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"><br><img src="https://1013399.github.io/image-1/37/1147348130_l.jpg" alt="IMGP0725-28.jpg - 屏東泰武北大武山(DAY-2)" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約9.5小時(含休息、午餐)</p><p>高低落差：約960公尺(不含上下起伏)</p></div><h2 id="上一篇相關文章" tabindex="-1"><a class="header-anchor" href="#上一篇相關文章" aria-hidden="true">#</a> 上一篇相關文章</h2>',40),b=i("h2",{id:"下一篇相關文章",tabindex:"-1"},[i("a",{class:"header-anchor",href:"#下一篇相關文章","aria-hidden":"true"},"#"),t(" 下一篇相關文章")],-1),_=i("h2",{id:"參考地圖",tabindex:"-1"},[i("a",{class:"header-anchor",href:"#參考地圖","aria-hidden":"true"},"#"),t(" 參考地圖")],-1),u={href:"http://recreation.forest.gov.tw/rt/RT_2_1.aspx?TR_ID=119",target:"_blank",rel:"noopener noreferrer"},j=i("br",null,null,-1),G=i("img",{src:"https://1013399.github.io/image-1/37/1147347732_l.jpg",alt:"北大武山-台灣山林悠遊網 - 屏東泰武北大武山(DAY-2)",loading:"lazy"},null,-1),P=i("h2",{id:"落差圖",tabindex:"-1"},[i("a",{class:"header-anchor",href:"#落差圖","aria-hidden":"true"},"#"),t(" 落差圖")],-1),A={href:"http://tw.hiking.biji.co/index.php?q=news&act=info&id=2642",target:"_blank",rel:"noopener noreferrer"},D=i("br",null,null,-1),Y=i("img",{src:"https://1013399.github.io/image-1/37/1147347434_l.jpg",alt:"北大武山落差圖-健行筆記 - 屏東泰武北大武山(DAY-2)",loading:"lazy"},null,-1);function I(y,z){const l=p("RouterLink"),g=p("ExternalLinkIcon");return r(),s("div",null,[c,h(" more "),m,i("ul",null,[i("li",null,[a(l,{to:"/posts/post-38-2016-12-12.html"},{default:e(()=>[t("北大武山DAY-1(北大武山國家步道→檜谷山莊))")]),_:1})])]),b,i("ul",null,[i("li",null,[a(l,{to:"/posts/post-36-2016-12-16.html"},{default:e(()=>[t("北大武山DAY-3(檜谷山莊→登山口→登山故事館)")]),_:1})])]),_,i("p",null,[t("原圖連結→"),i("a",u,[t("台灣山林悠遊網"),a(g)]),j,G]),P,i("p",null,[t("原圖連結→"),i("a",A,[t("健行筆記"),a(g)]),D,Y])])}const K=o(d,[["render",I],["__file","post-37-2016-12-13.html.vue"]]);export{K as default};
