import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as e,c as g,a as o,b as t,e as a,d as p,f as s}from"./app-4a074c75.js";const n={},h=t("p",null,[t("strong",null,"草嶺山"),a("，海拔347公尺，三等三角點#1010，位於桃園縣大溪鎮，緊鄰慈湖，是一座鹼性玄武岩的盾狀火山，約在十餘萬年前噴發流出地表，山上的白榴基玄武岩，內有黃綠色玻璃質的橄欖石。")],-1),b=t("p",null,[t("strong",null,"石厝坑山"),a("，海拔573公尺，殖產局圖根三角補點，地形圖上稱之為白石山。")],-1),c=s('<p><strong>白石山</strong>，海拔625公尺，無基點，以石壁側面苔痕成白色而得名，山稜與金面山曲彎相連，山頂由巨石盤踞，綿延達百餘公尺，由前方石壁下觀之，威壯無比，故素有「台灣小黃山」之稱。而整條岩稜線上，視野絕佳，每年三、四月份盛開金毛杜鵑，是時粉紅色的花朵會將山稜上粧扮的繽紛有緻。</p><p>登山日期：2009/04/04</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>北二高下大溪交流道，沿台3線往大溪方向，於台3線與台4線交叉口左轉往大溪，過大漢溪武嶺橋後續往三峽方向行駛，至大溪牌坊前左轉往三峽，至中華路右轉往復興、巴陵方向行駛，至復興路左轉台7線前進，至慈湖停車場停車(2009/03/01起收費50元)。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→慈湖停車場<br> →蔣公銅像公園<br> →同福宮<br> →<strong>草嶺山<br><strong>→雙木電桿<br> →廢崗哨(左往打鐵寮古道三層段，取直往石厝坑山及白石山)<br> →叉路(右下白石山及打鐵寮古道，取直上稜往電塔及石厝坑山)<br> →保線路與山徑交會點<br> →三柱電塔(沿途有多處觀景點)<br> →</strong>石厝坑山<br><strong>→穿過農路<br> →叉路(左往金面山、十三份山，取右往白石山)<br> →</strong>白石山</strong><br> →「山上的透天別墅」(寫有「爬山歌」及「白石詩」)<br> →叉路(右往東興橋、慈湖、三層，取左往白石埤、百吉步道)<br> →白石埤<br> →產業道路<br> →搭便車(感謝好心山友張先生，免踢5公里的馬路)<br> →回慈湖停車場</p><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>慈湖橋<br><img src="https://1013399.github.io/image-6/373/122642895_l.jpg" alt="慈湖橋" loading="lazy"></p><p>蔣公銅像公園<br><img src="https://1013399.github.io/image-6/373/122642901_l.jpg" alt="蔣公銅像公園" loading="lazy"></p><p>蔣公銅像公園<br><img src="https://1013399.github.io/image-6/373/122642908_l.jpg" alt="蔣公銅像公園" loading="lazy"></p><p>蔣公銅像公園<br><img src="https://1013399.github.io/image-6/373/122642915_l.jpg" alt="蔣公銅像公園" loading="lazy"></p><p>蔣公銅像公園<br><img src="https://1013399.github.io/image-6/373/122642924_l.jpg" alt="蔣公銅像公園" loading="lazy"></p><p>草嶺山<br><img src="https://1013399.github.io/image-6/373/122642139_l.jpg" alt="草嶺山" loading="lazy"></p><p>草嶺山三等三角點<br><img src="https://1013399.github.io/image-6/373/122642183_l.jpg" alt="草嶺山三等三角點" loading="lazy"></p><p>草嶺山三等三角點<br><img src="https://1013399.github.io/image-6/373/122642184_l.jpg" alt="草嶺山三等三角點" loading="lazy"></p><p>廢崗哨<br><img src="https://1013399.github.io/image-6/373/122642339_l.jpg" alt="草嶺山" loading="lazy"></p><p>石厝坑山<br><img src="https://1013399.github.io/image-6/373/122642364_l.jpg" alt="草嶺山、石厝坑山、白石山" loading="lazy"></p><p>石厝坑山殖產局圖根三角補點<br><img src="https://1013399.github.io/image-6/373/122642450_l.jpg" alt="石厝坑山殖產局圖根三角補點" loading="lazy"></p><p>奇石：像個沙發椅<br><img src="https://1013399.github.io/image-6/373/122642563_l.jpg" alt="草嶺山、石厝坑山、白石山" loading="lazy"></p><p>白石山<br><img src="https://1013399.github.io/image-6/373/122642629_l.jpg" alt="白石山" loading="lazy"></p><p>白石山<br><img src="https://1013399.github.io/image-6/373/122642639_l.jpg" alt="白石山" loading="lazy"></p><p>白石山<br><img src="https://1013399.github.io/image-6/373/122642711_l.jpg" alt="白石山" loading="lazy"></p><p>白石埤<br><img src="https://1013399.github.io/image-6/373/122642718_l.jpg" alt="白石埤" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',24),d={href:"http://tw.myblog.yahoo.com/designteng-go/article?mid=3658&prev=4129&next=3620&l=f&fid=5",target:"_blank",rel:"noopener noreferrer"},m=t("br",null,null,-1),_=t("img",{src:"https://1013399.github.io/image-6/373/122643045_l.jpg",alt:"草嶺山、石厝坑山、白石山 參考地圖",loading:"lazy"},null,-1);function u(y,f){const i=l("ExternalLinkIcon");return e(),g("div",null,[h,b,o(" more "),c,t("p",null,[t("a",d,[a("小鄧的登山札記"),p(i)]),m,_])])}const x=r(n,[["render",u],["__file","post-373-2009-04-06.html.vue"]]);export{x as default};
