import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{r as n,o,c as s,a as l,b as r,f as e,d as i,e as h}from"./app-afda0674.js";const c={},d=r("p",null,[r("strong",null,"大同山"),e("，海拔230公尺，位於台北縣樹林市南寮福德宮後方，山頂有台灣省地政處精密導線點基石、雙涼亭及土地公廟，視野佳。")],-1),b=r("p",null,[r("strong",null,"青龍嶺"),e("，海拔270公尺，位於台北縣樹林市青德宮廣場攤販區後方，山頂有台灣省政府#253004三角點基石，旁有酒甕堆成之字牌，視野極佳。")],-1),p=h('<p><strong>大棟山</strong>，海拔405公尺，位於台北縣樹林市與桃園縣龜山鄉交界處，又稱龜崙山或橫坑子山，為樹林最高峰，山頂有一等三角點基石及土地調查局圖根點基石各一顆，展望極佳，是台北盆地西緣的一等三角點，與七星山、土庫尖、獅仔頭山並列台北四大郊山，該山名列台灣小百岳#015。</p><p>登山日期：2009/02/15</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>北二高下三鶯交流道，循110縣道往鶯歌方向，過鶯歌火車站及平交道後接114縣道(中正一路)，經山佳火車站行至中華路Y型叉路左轉過地下道接大安路，經樹人家商後遇保安街(北74鄉道)左轉，於保安街二段45巷左轉到達「南寮福德宮」，於附近停車。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→南寮福德宮(右側石階上登)<br> →青龍嶺產業道路(右回南寮福德宮大門，取左往青德宮方向)<br> →大同山登山口(左側有高大的土地公雕像)<br> →<strong>大同山</strong>(有土地公廟及雙涼亭)<br> →圓桌休息區<br> →青龍嶺觀景台<br> →青德宮廣場(攤販雲集，卡拉OK熱鬧)<br> →<strong>青龍嶺</strong>(需從青德宮廟後方，淡水河廣播電台發射站旁邊小徑進入)<br> →回青德宮(取左柏油路往大棟山)<br> →大棟山第一登山口(不由此上，續行柏油路)<br> →大棟山第二登山口(開始為墳墓區)<br> →青龍亭(取左)<br> →叉路(續直行)<br> →樹登山莊涼亭休息區(不進入，取右彎續行)<br> →叉路(右往三仙台，續直行)<br> →秋壇山尖石<br> →**大棟山<br> **→原路折返<br> →大棟山登山口(不想再走階梯，改走柏油路下山)<br> →香菇涼亭<br> →南寮福德宮</p><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>大棟山一等三角點及土地調查局圖根點<br><img src="https://1013399.github.io/image-6/379/122815562_l.jpg" alt="大同山、青龍嶺、大棟山" loading="lazy"></p><p>大棟山<br><img src="https://1013399.github.io/image-6/379/122815564_l.jpg" alt="大同山、青龍嶺、大棟山" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',10),_={href:"http://www.tonyhuang39.com/tony0474/tony0474.html",target:"_blank",rel:"noopener noreferrer"},g=r("br",null,null,-1),m=r("img",{src:"https://1013399.github.io/image-6/379/122815584_l.jpg",alt:"大同山、青龍嶺、大棟山",loading:"lazy"},null,-1);function u(f,x){const t=n("ExternalLinkIcon");return o(),s("div",null,[d,b,l(" more "),p,r("p",null,[r("a",_,[e("Tony的自然人文旅記"),i(t)]),g,m])])}const N=a(c,[["render",u],["__file","post-379-2009-02-16.html.vue"]]);export{N as default};
