import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{r as o,o as n,c as i,a as s,b as t,f as a,d as l,e as p}from"./app-6513a715.js";const g={},h=t("p",null,[t("strong",null,"七星山"),a("，海拔1120公尺，一等三角點，也是一等衛星控制點，位於台北盆地北方的陽明山國家公園內，為大屯山火山群中的最高峰，也是台北轄區內的最高點，該山名列台灣小百岳#002。")],-1),c=t("p",null,[t("strong",null,"七星山東峰"),a("，海拔1107公尺，三等三角點#1104，位於七星山主峰的東南方，七星山東峰的山坡上，有極負盛名的夢幻湖。")],-1),d=p('<p>七星山為錐狀火山，係由多次火山噴發而成，頂部並沒有噴火口，主要組成物質是火山熔岩；由於山頂是由大小7座山頭所組成，七星山之名故因此而來，七星山有三個主要的登山口，分別是<strong>小油坑</strong>、<strong>苗圃</strong>及<strong>冷水坑</strong>，這次由冷水坑上山再下小油坑。</p><p>登山日期：2009/01/31</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>(避開仰德大道假日交通管制)</p><p>中山高下環河北路交流道往北，至中正路右轉過百齡橋(台2乙線)，接上福林路(台2甲線)在雙溪公園右轉接至善路，到至善路三段71巷左轉接平菁街，經平等國小續開接上菁山路，再經過「台北奧萬大」、中華電信的雷達站、菁山自然中心，便可到達冷水坑遊客服務站停車場。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→冷水坑遊客服務站<br> →七星山登山口(停車場出口對面)<br> →涼亭叉路(左轉或直行皆可)<br> →叉路(右往夢幻湖，走了一半，風勢強勁難行，折返往七星公園)<br> →教育廣播電台<br> →叉路(左往七星公園，取右上山)<br> →<strong>七星山東峰</strong>(溫度9度)<br> →<strong>七星山主峰</strong>(一片霧茫茫，溫度也是9度)<br> →小油坑地熱景觀<br> →小油坑的公車站(搭遊園公車108線)<br> →冷水坑<br> →免費公共溫泉<br> →冷水坑遊客服務站</p><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>七星山東峰<br><img src="https://1013399.github.io/image-6/381/122853820_l.jpg" alt="七星山東峰" loading="lazy"></p><p>七星山主峰<br><img src="https://1013399.github.io/image-6/381/122853828_l.jpg" alt="七星山主峰" loading="lazy"></p><p>七星山主峰<br><img src="https://1013399.github.io/image-6/381/122853831_l.jpg" alt="七星山主峰" loading="lazy"></p><p>一等衛星控制點<br><img src="https://1013399.github.io/image-6/381/122853844_l.jpg" alt="七星山 一等衛星控制點" loading="lazy"></p><p>小油坑地熱景觀<br><img src="https://1013399.github.io/image-6/381/122853848_l.jpg" alt="七星山 小油坑地熱景觀" loading="lazy"></p><p>小油坑地熱景觀<br><img src="https://1013399.github.io/image-6/381/122853853_l.jpg" alt="七星山 小油坑地熱景觀" loading="lazy"></p><p>小油坑溫泉露頭基石<br><img src="https://1013399.github.io/image-6/381/122853856_l.jpg" alt="七星山 小油坑溫泉露頭基石" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',16),_={href:"http://www.ymsnp.gov.tw/web/travel2c_B.aspx",target:"_blank",rel:"noopener noreferrer"},b=t("br",null,null,-1),m=t("img",{src:"https://1013399.github.io/image-6/381/122853872_l.jpg",alt:"七星山、七星山東峰 參考地圖",loading:"lazy"},null,-1);function u(f,x){const r=o("ExternalLinkIcon");return n(),i("div",null,[h,c,s(" more "),d,t("p",null,[t("a",_,[a("陽明山國家公園網站"),l(r)]),b,m])])}const z=e(g,[["render",u],["__file","post-381-2009-02-02.html.vue"]]);export{z as default};
