import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o as r,c as g,a as o,b as a,f as t,d as p,e as n}from"./app-a9847bbb.js";const s={},h=a("p",null,[a("strong",null,"李棟山"),t("，或稱李崠山，海拔1914公尺，一等三角點，屬雪山山脈，山稜北連鳥嘴山、那結山，東稜延伸至泰平山、馬望曾僧侶山，南稜連鳥來山，西稜接大混山，山容壯闊。日治時期(西元1910)日軍於山頂建一座要塞城堡，重兵駐守，以監控附近的三光、秀巒、玉峰等泰雅族部落。泰雅族人曾在此地與日本軍警發生過激烈的戰役，與苗栗馬拉邦山同為台灣近代史赫赫有名的抗日古戰場。")],-1),c=n('<p>登山日期：2008/12/21</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>120縣道往尖石、內灣方向，過尖石鄉公所，右轉過尖石大橋接竹60鄉道，至羅那溪與錦屏溪交會的三叉路口時，左轉過錦屏大橋，穿過「泰雅勇士塑像」的明隧道，至竹60鄉道約19K附近T型叉路口取左行，續行約1公里多左轉「馬美道路」，遇叉路依指標取左行，即可抵達李棟山莊(每人收20元)。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→李棟山莊<br> →登山口(李棟山莊後方)<br> →叉路取左<br> →穿越產業道路(總共會穿越七次，產業道路也可到達山頂)<br> →李棟古堡城牆<br> →<strong>李棟山</strong>(門口旁有往大混山叉路)<br> →下山(改走產業道路，坡度較緩，景觀也較佳)<br> →第一次山徑與產道交會處(續行產道往馬美道路，改原山路回李棟山莊)<br> →李棟山莊(莊主提供高山七葉膽茶試喝)</p><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>李棟山莊<br><img src="https://1013399.github.io/image-6/384/122856298_l.jpg" alt="李棟山莊" loading="lazy"></p><p>李棟山莊入口<br><img src="https://1013399.github.io/image-6/384/122856341_l.jpg" alt="李棟山莊入口" loading="lazy"></p><p>李棟山莊<br><img src="https://1013399.github.io/image-6/384/122856349_l.jpg" alt="李棟山莊" loading="lazy"></p><p>李棟山莊<br><img src="https://1013399.github.io/image-6/384/122856354_l.jpg" alt="李棟山莊" loading="lazy"></p><p>李棟山莊旁導覽圖<br><img src="https://1013399.github.io/image-6/384/122856359_l.jpg" alt="李棟山莊旁導覽圖" loading="lazy"></p><p>山路與產業道路交會指示牌<br><img src="https://1013399.github.io/image-6/384/122856384_l.jpg" alt="李棟山 山路與產業道路交會指示牌" loading="lazy"></p><p>解說牌<br><img src="https://1013399.github.io/image-6/384/122856401_l.jpg" alt="李棟山 解說牌" loading="lazy"></p><p>古堡解說牌<br><img src="https://1013399.github.io/image-6/384/122856403_l.jpg" alt="李棟山 古堡解說牌" loading="lazy"></p><p>往大混山叉路<br><img src="https://1013399.github.io/image-6/384/122856412_l.jpg" alt="李棟山" loading="lazy"></p><p>一等三角點<br><img src="https://1013399.github.io/image-6/384/122856432_l.jpg" alt="李棟山一等三角點" loading="lazy"></p><p>頭前溪水源點<br><img src="https://1013399.github.io/image-6/384/122856447_l.jpg" alt="李棟山 頭前溪水源點" loading="lazy"></p><p>雨量測報站<br><img src="https://1013399.github.io/image-6/384/122856465_l.jpg" alt="李棟山 雨量測報站" loading="lazy"></p><p>李棟山六路會師紀念基點<br><img src="https://1013399.github.io/image-6/384/122856472_l.jpg" alt="李棟山 李棟山六路會師紀念基點" loading="lazy"></p><p>古堡城牆<br><img src="https://1013399.github.io/image-6/384/122856476_l.jpg" alt="李棟山 古堡城牆" loading="lazy"></p><p>六路會師人馬<br><img src="https://1013399.github.io/image-6/384/122856518_l.jpg" alt="李棟山 六路會師人馬" loading="lazy"></p><p>還有楓紅可欣賞<br><img src="https://1013399.github.io/image-6/384/122856522_l.jpg" alt="李棟山 楓紅" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',23),d={href:"http://www.tonyhuang39.com/tony0606/tony0606.html",target:"_blank",rel:"noopener noreferrer"},m=a("br",null,null,-1),b=a("img",{src:"https://1013399.github.io/image-6/384/122856608_l.jpg",alt:"李棟山 參考地圖",loading:"lazy"},null,-1);function _(u,y){const i=e("ExternalLinkIcon");return r(),g("div",null,[h,o(" more "),c,a("p",null,[a("a",d,[t("Tony的自然人文旅記"),p(i)]),m,b])])}const f=l(s,[["render",_],["__file","post-384-2008-12-22.html.vue"]]);export{f as default};
