import{_ as i}from"./plugin-vue_export-helper-c27b6911.js";import{r as o,o as e,c as l,a as g,b as t,f as a,d as n,e as s}from"./app-a39bec1a.js";const p={},h=t("p",null,[t("strong",null,"五指山"),a("坐落於新竹縣北埔鄉、竹東鎮和五峰鄉的交界處，其形似手掌的五指並列，因而得名，分別為拇指峰(H980m)、食指峰(H1041m)、中指峰(1061m)、無名指峰(H1047m)及小指峰(H1021m)，五指山腳下寺廟林立，較具規模者有灶君堂、盤古廟、玉皇宮、觀音禪寺等寺廟，終年香煙鼎盛。")],-1),b=t("p",null,[t("strong",null,"五指山"),a("的主峰為中指峰，海拔1061公尺，山頂有二等三角點#1116基石及省政府頭前溪水源保護區基石，視野佳，五指山為臺灣十二名勝景之一，名列台灣小百岳#031。")],-1),c=s('<p>登山日期：2008/12/20</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>開車經竹東，於下公館轉122縣道往五峰及清泉，行至30.1k處，右轉上行五指山公路，途經北埔冷泉及觀音禪寺叉路，即可達五指山山麓的玉皇宮停車場停車。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→玉皇宮(右側水泥路進入)<br> →盤古廟<br> →養氣亭登山口(盤古廟右側)<br> →十字叉路(五指山橫向步道)<br> →叉路(右至石壁，續直走山腰路，稍後接上急陡坡)<br> →稜線小平台(右轉續上陡坡)<br> →稜線鞍部叉路(有大石塊，旁有指示牌，取左往中指)<br> →<strong>中指峰</strong>(續直走陡下進入桂竹林，再陡上)<br> →<strong>無名指峰</strong><br> →<strong>小指峰</strong>(直走往上坪山及上坪國小，遙遠且難走)<br> →原路返回<br> →無名指峰<br> →中指峰<br> →稜線鞍部叉路(右下為剛才上山之路，取直行木棧道往食指)<br> →<strong>食指峰</strong><br> →叉路(休息椅，右下為木棧道新路，取左舊山路)<br> →<strong>拇指峰</strong>(新、舊路交會，取左下山)<br> →叉路(左往大隘登山口，取右往竹林禪苑)<br> →木橋→叉路(右木棧道為「五指山橫向步道」，續直走杉木林)<br> →竹林禪苑<br> →叉路(左往仙洞，直走柏油路)<br> →第二停車場<br> →山訓場遊樂區<br> →玉皇宮</p><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>中指峰<br><img src="https://1013399.github.io/image-6/385/122890523_l.jpg" alt="五指山 中指峰" loading="lazy"></p><p>中指峰二等三角點#1116<br><img src="https://1013399.github.io/image-6/385/122890525_l.jpg" alt="五指山 中指峰二等三角點#1116" loading="lazy"></p><p>中指峰二等三角點<br><img src="https://1013399.github.io/image-6/385/122890528_l.jpg" alt="五指山 中指峰二等三角點#1116" loading="lazy"></p><p>眺望鵝公髻山<br><img src="https://1013399.github.io/image-6/385/122890544_l.jpg" alt="鵝公髻山" loading="lazy"></p><p>遠眺群山<br><img src="https://1013399.github.io/image-6/385/122890546_l.jpg" alt="五指山" loading="lazy"></p><p>無名指峰<br><img src="https://1013399.github.io/image-6/385/122890548_l.jpg" alt="五指山 無名指峰" loading="lazy"></p><p>小指峰<br><img src="https://1013399.github.io/image-6/385/122890569_l.jpg" alt="五指山 小指峰" loading="lazy"></p><p>無名指與中指間有顆山字基石<br><img src="https://1013399.github.io/image-6/385/122890590_l.jpg" alt="五指山 山字基石" loading="lazy"></p><p>食指中指指示牌<br><img src="https://1013399.github.io/image-6/385/122890592_l.jpg" alt="五指山 食指中指指示牌" loading="lazy"></p><p>拇指食指指示牌<br><img src="https://1013399.github.io/image-6/385/122890595_l.jpg" alt="五指山 拇指食指指示牌" loading="lazy"></p><p>拇指峰<br><img src="https://1013399.github.io/image-6/385/122890596_l.jpg" alt="五指山 拇指峰" loading="lazy"></p><p>拇指峰<br><img src="https://1013399.github.io/image-6/385/122890598_l.jpg" alt="五指山 拇指峰" loading="lazy"></p><p>竹林禪苑指示牌<br><img src="https://1013399.github.io/image-6/385/122890601_l.jpg" alt="五指山 竹林禪苑" loading="lazy"></p><p>還有楓紅可欣賞<br><img src="https://1013399.github.io/image-6/385/122890647_l.jpg" alt="五指山 楓紅" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',21),d={href:"http://www.tonyhuang39.com/tony0422/tony0422.html",target:"_blank",rel:"noopener noreferrer"},m=t("br",null,null,-1),_=t("img",{src:"https://1013399.github.io/image-6/385/122890669_l.jpg",alt:"五指山 參考地圖",loading:"lazy"},null,-1);function u(y,f){const r=o("ExternalLinkIcon");return e(),l("div",null,[h,b,g(" more "),c,t("p",null,[t("a",d,[a("Tony的自然人文旅記"),n(r)]),m,_])])}const x=i(p,[["render",u],["__file","post-385-2008-12-22.html.vue"]]);export{x as default};
