import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{r as o,o as i,c as n,a as s,b as t,f as r,d as l,e as g}from"./app-2f66486a.js";const p={},h=t("p",null,[t("strong",null,"打牛崎古道"),r("，位於新竹縣關西鎮東山里四寮，早年的竹28鄉道沿線有陡坡難行，當時農民運送農產品及民生必需品，皆靠牛車運送，牛車行到此陡坡，連牛都懶的走，農民必須鞭打牛隻，才能勉強上坡，故稱打牛崎。")],-1),c=t("p",null,[t("strong",null,"眺望崠"),r("，海拔375公尺，大石頭樹幹上有標示牌，無基石，林中無視野。")],-1),d=g('<p><strong>老虎山</strong>，海拔395公尺，無基石，無標示牌，林中無視野。</p><p><strong>胡房崠</strong>，無標高，無標示，難以確認。</p><p><strong>石人崠</strong>，海拔428公尺，無基石，山頂有小蓄水池。</p><p>登山日期：2008/12/07</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>北二高下關西交流道後，沿118縣道往關西方向，接台3線取左往龍潭，至台3線59.5K關西高中，右轉竹28鄉道往立益球場，至0.2k叉路取左往藥師佛禪寺(右往立益球場)，過藥師佛禪寺後，續行駛約50公尺即可達打牛崎登山口，旁有「湖肚高幹94」電力桿。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→<strong>打牛崎古道</strong>登山口(有牌樓及登山路線圖)<br> →攀岩場(取右陡上)<br> →好漢坡(旁有「好漢坡300公尺，有膽接受挑戰嗎？」牌子)<br> →「加油！快要到山頂了」牌子<br> →<strong>眺望崠</strong><br> →<strong>老虎山</strong>(叉路，右寬廣山路往大鼓亭，取左往石人崠)<br> →<strong>胡房崠</strong>(小山頭，無標示，不確定是否為胡房崠)<br> →果園(一時找不到路，原來沿稜線走到底便可看到小拉繩)<br> →<strong>石人崠</strong>(亦無標示，有個蓄水池)<br> →泥土產業道路(似乎剛開發不久，已無山徑，不知東畚箕窩山、十寮山到了沒，也沒看到傳說中的關西庄龍潭庄大界柱)<br> →小山頭(視野不錯，遠眺童話世界)<br> →十(八)寮古道最高點(指示牌釘在土路旁樹上，由此下山)<br> →<strong>十寮古道</strong>(雜草淹沒，巧遇中壢山友廖先生在開闢登山路徑、綁登山條)<br> →廢棄紅磚屋<br> →竹28鄉道<br> →金廣成文化館<br> →登山口</p><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>登山口旁登山路線圖<br><img src="https://1013399.github.io/image-6/386/123110239_l.jpg" alt="打牛崎古道 登山口旁登山路線圖" loading="lazy"></p><p>攀岩場<br><img src="https://1013399.github.io/image-6/386/123110089_l.jpg" alt="攀岩場 打牛崎古道、眺望崠、老虎山、石人崠" loading="lazy"></p><p>好漢坡<br><img src="https://1013399.github.io/image-6/386/123110092_l.jpg" alt="打牛崎古道、眺望崠、老虎山、石人崠" loading="lazy"></p><p>眺望崠<br><img src="https://1013399.github.io/image-6/386/123110098_l.jpg" alt="打牛崎古道、眺望崠、老虎山、石人崠" loading="lazy"></p><p>老虎山<br><img src="https://1013399.github.io/image-6/386/123110103_l.jpg" alt="老虎山" loading="lazy"></p><p>小山頭視野不錯<br><img src="https://1013399.github.io/image-6/386/123110137_l.jpg" alt="打牛崎古道、眺望崠、老虎山、石人崠" loading="lazy"></p><p>保安林界石<br><img src="https://1013399.github.io/image-6/386/123110172_l.jpg" alt="保安林界石 打牛崎古道、眺望崠、老虎山、石人崠" loading="lazy"></p><p>電塔林立，遠眺童話世界<br><img src="https://1013399.github.io/image-6/386/123110184_l.jpg" alt="打牛崎古道、眺望崠、老虎山、石人崠" loading="lazy"></p><p>十(八)寮古道最高點<br><img src="https://1013399.github.io/image-6/386/123110174_l.jpg" alt="十(八)寮古道最高點" loading="lazy"></p><p>金廣成文化館<br><img src="https://1013399.github.io/image-6/386/123110235_l.jpg" alt="金廣成文化館" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',20),b={href:"http://www.keepon.com.tw/ActiveSite/Article/One.asp?ArticleID=6460",target:"_blank",rel:"noopener noreferrer"},_=t("br",null,null,-1),m=t("img",{src:"https://1013399.github.io/image-6/386/123110285_l.jpg",alt:"打牛崎古道、眺望崠、老虎山、石人崠 參考地圖",loading:"lazy"},null,-1);function u(f,j){const a=o("ExternalLinkIcon");return i(),n("div",null,[h,c,s(" more "),d,t("p",null,[t("a",b,[r("藍天圖集"),l(a)]),_,m])])}const x=e(p,[["render",u],["__file","post-386-2008-12-08.html.vue"]]);export{x as default};
