import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r,o,c as e,a as g,b as t,e as a,d as p,f as n}from"./app-4a074c75.js";const s={},h=t("p",null,[t("strong",null,"五寮尖"),a("，又名鳳髻尖、湊合山，海拔639公尺，總督府圖根補點，位於三峽鎮湊合正西方，屬於插天山脈東眼山支系，其稜脈上的岩質非常堅硬，其地表有些岩石呈現出如海中的蜂巢岩，與石碇皇帝殿、筆架山並列北部三大岩場。")],-1),b=t("p",null,[t("strong",null,"鳳冠山"),a("，海拔422公尺，為一巨岩，從第一或第二登山口起登便會經過。")],-1),c=n('<p>五寮尖登山口有三處：</p><ol><li>第一登山口，在合作橋南端玉里商店旁，有一小段水泥石階，可循此登山口直上五寮尖東稜。</li><li>第二登山口，在合作橋前數十公尺有一右上水泥叉道及牌坊往濟公廟(濟玄堂)，廟前有片停車場。</li><li>第三登山口，在第二登山口前產道岔路右行到合作溪溪畔觀音寺，通常當下山路線。</li></ol><p>這次與友人同行，選擇由第二登山口起登。</p><p>登山日期：2008/10/25</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>北二高三鶯交流道下往三峽方向走台3線至大埔(25.5K)後左轉開往三民方向(台7乙線)，過互助橋後注意右邊有叉路往濟公廟，車停於廟前停車場。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→濟公廟登山口(竹林陡上)<br> →稜線(與第一登山口路線會合)<br> →<strong>鳳冠山</strong>(展望佳)<br> →石壁公(兩個垂直山壁中間的凹洞)<br> →牛背岩(瘦窄稜線)<br> →<strong>峭壁雄風</strong>(大石壁垂直下降)<br> →小空地(旁有一座陡峭獨立山峰，決定一試)<br> →<strong>獨立峰</strong>頂(視野極佳，眺望五寮尖、俯瞰峭壁雄風)<br> →小空地<br> →鞍部<br> →木梯<br> →大茄苳樹(叉路，指示牌寫著左去右回)<br> →<strong>五寮尖</strong>(空地不大，展望很棒)<br> →下山(走龍山脈泉路往玉觀音，踩著紅色鐵條往下，泥土路，走來輕鬆)<br> →叉路(直往玉觀音，取右往濟玄堂)<br> →濟公廟停車場</p><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>第一段峭壁<br><img src="https://1013399.github.io/image-7/390/123242261_l.jpg" alt="五寮尖" loading="lazy"></p><p>拉繩陡下岩壁<br><img src="https://1013399.github.io/image-7/390/123242267_l.jpg" alt="五寮尖" loading="lazy"></p><p>牛背岩<br><img src="https://1013399.github.io/image-7/390/123242269_l.jpg" alt="五寮尖 牛背岩" loading="lazy"></p><p>牛背岩<br><img src="https://1013399.github.io/image-7/390/123242308_l.jpg" alt="五寮尖 牛背岩" loading="lazy"></p><p>牛背岩<br><img src="https://1013399.github.io/image-7/390/123242343_l.jpg" alt="五寮尖 牛背岩" loading="lazy"></p><p>陡下岩壁<br><img src="https://1013399.github.io/image-7/390/123242347_l.jpg" alt="五寮尖" loading="lazy"></p><p>牛背岩<br><img src="https://1013399.github.io/image-7/390/123242353_l.jpg" alt="五寮尖 牛背岩" loading="lazy"></p><p>遠眺五寮尖<br><img src="https://1013399.github.io/image-7/390/123242392_l.jpg" alt="五寮尖" loading="lazy"></p><p>峭壁雄風<br><img src="https://1013399.github.io/image-7/390/123242397_l.jpg" alt="五寮尖" loading="lazy"></p><p>獨立峰頂遠眺峭壁雄風<br><img src="https://1013399.github.io/image-7/390/123242424_l.jpg" alt="五寮尖" loading="lazy"></p><p>獨立峰頂遠眺峭壁雄風<br><img src="https://1013399.github.io/image-7/390/123242439_l.jpg" alt="五寮尖" loading="lazy"></p><p>獨立峰頂遠眺五寮尖<br><img src="https://1013399.github.io/image-7/390/123242443_l.jpg" alt="五寮尖" loading="lazy"></p><p>遠眺岩稜上攀爬的山友<br><img src="https://1013399.github.io/image-7/390/123242451_l.jpg" alt="五寮尖" loading="lazy"></p><p>漂亮的黑松<br><img src="https://1013399.github.io/image-7/390/123242462_l.jpg" alt="五寮尖 黑松" loading="lazy"></p><p>大茄苳樹<br><img src="https://1013399.github.io/image-7/390/123242479_l.jpg" alt="五寮尖 大茄苳樹" loading="lazy"></p><p>五寮尖總督府圖根補點<br><img src="https://1013399.github.io/image-7/390/123242493_l.jpg" alt="五寮尖總督府圖根補點" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',26),d={href:"http://www.tonyhuang39.com/tony0334.html",target:"_blank",rel:"noopener noreferrer"},m=t("br",null,null,-1),_=t("img",{src:"https://1013399.github.io/image-7/390/123242546_l.jpg",alt:"五寮尖 參考地圖",loading:"lazy"},null,-1);function u(y,j){const i=r("ExternalLinkIcon");return o(),e("div",null,[h,b,g(" more "),c,t("p",null,[t("a",d,[a("Tony的自然人文旅記"),p(i)]),m,_])])}const x=l(s,[["render",u],["__file","post-390-2008-10-27.html.vue"]]);export{x as default};
