import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r,o as l,c as o,a as p,b as t,e as a,d as s,f as e}from"./app-39f3f472.js";const n={},h=t("p",null,[t("strong",null,"北得拉曼"),a("巨木群位於北得拉曼山(內鳥嘴山)上，在海拔900公尺到1500公尺間，是罕見的低海拔檜木群，被新樂村的原住民公諸於世，這個充滿傳奇的神木群，曾是當地泰雅族長老保密五十多年的秘密，公佈後也引起諸多爭議，並一度引發村民封山，以避免大批遊客湧入。")],-1),b=t("p",null,[a("後來林務局修建"),t("strong",null,"北得拉曼國家步道"),a("，通往神木群，步道長約2.6公里，路程適中，每隔100公尺設置里程路樁，指標清楚，而步道海拔落差約三、四百公尺，遊客也得以親近北得拉曼神木。")],-1),m=e('<p>登山日期：2008/10/18</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>北二高下竹林交流道，循120縣道往內灣、尖石方向，過內灣之後，續往尖石鄉，至120縣道35.4K時，左轉新樂大橋，進入水田道路(竹58鄉道)，前行至約竹58鄉道1.1K附近(有新竹客運「<strong>田中站</strong>」站牌)，再右後轉水田林道支線，路口不太明顯，須特別注意。進入水田林道支線，沿途有不少叉路，都取直行，馬路愈走愈窄，由柏油路變成水泥路，路面有不少坑洞，經過最後一家雜貨店後，路面轉壞，最後3公里為崎嶇土石路，四驅車可開抵登山口，小車不宜，於路旁較寬處停車。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→停車處(走了約一小時的崎嶇土石林道)<br> →北得拉曼登山口<br> →北得拉曼步道(每100公尺就有木樁里程碑)<br> →0.3K木樁(開始陡上)<br> →0.4K木樁(坡度趨緩)<br> →0.6K木樁(<strong>好漢坡</strong>，Z字型陡上)<br> →0.95K(有木椅可休息)<br> →1.2K木樁(叉路，右往拉庫山(水田山)，取左往內鳥嘴山)<br> →<strong>回音谷</strong>(穿越巨石、岩稜、木梯、棧道等，約1.4K後為鬆軟的地毯路)→1442峰(展望不錯，有人在岩石上刻上「<strong>田中山</strong>」字樣)<br> →1.6K木樁(<strong>環狀叉路口</strong>，設有導覽圖，直上往內鳥嘴山，取右往神木)<br> →<strong>四號神木</strong><br> →<strong>三號神木</strong><br> →<strong>二號神木</strong><br> →<strong>一號神木</strong><br> →瀑布<br> →未編號巨木(1.8K木樁處，也有叉路往內鳥嘴山)<br> →環狀叉路口<br> →1442峰<br> →回音谷<br> →好漢坡<br> →登山口(約踢50分鐘林道下山)<br> →停車處</p><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>北得拉曼登山口<br><img src="https://1013399.github.io/image-7/391/123301130_l.jpg" alt="北得拉曼登山口" loading="lazy"></p><p>北得拉曼步道<br><img src="https://1013399.github.io/image-7/391/123301136_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>0.95K處視野不錯<br><img src="https://1013399.github.io/image-7/391/123301139_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>簡易木梯(回音谷附近)<br><img src="https://1013399.github.io/image-7/391/123301158_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>簡易木梯(回音谷附近)<br><img src="https://1013399.github.io/image-7/391/123301181_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>回音谷展望<br><img src="https://1013399.github.io/image-7/391/123301206_l.jpg" alt="北得拉曼步道 回音谷展望" loading="lazy"></p><p>田中山<br><img src="https://1013399.github.io/image-7/391/123301208_l.jpg" alt="北得拉曼步道 田中山" loading="lazy"></p><p>田中山展望<br><img src="https://1013399.github.io/image-7/391/123301212_l.jpg" alt="北得拉曼步道 田中山" loading="lazy"></p><p>田中山展望<br><img src="https://1013399.github.io/image-7/391/123301224_l.jpg" alt="北得拉曼步道 田中山" loading="lazy"></p><p>環狀叉路口巨木區路線圖(1.6K木樁)<br><img src="https://1013399.github.io/image-7/391/123301227_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>三號神木<br><img src="https://1013399.github.io/image-7/391/123301228_l.jpg" alt="北得拉曼步道 三號神木" loading="lazy"></p><p>三號神木<br><img src="https://1013399.github.io/image-7/391/123301230_l.jpg" alt="北得拉曼步道 三號神木" loading="lazy"></p><p>二號神木<br><img src="https://1013399.github.io/image-7/391/123301298_l.jpg" alt="北得拉曼步道 二號神木" loading="lazy"></p><p>二號神木<br><img src="https://1013399.github.io/image-7/391/123301307_l.jpg" alt="北得拉曼步道 二號神木" loading="lazy"></p><p>四號神木<br><img src="https://1013399.github.io/image-7/391/123301313_l.jpg" alt="北得拉曼步道 四號神木" loading="lazy"></p><p>四號神木<br><img src="https://1013399.github.io/image-7/391/123301395_l.jpg" alt="北得拉曼步道 四號神木" loading="lazy"></p><p>一號神木<br><img src="https://1013399.github.io/image-7/391/123301398_l.jpg" alt="北得拉曼步道 一號神木" loading="lazy"></p><p>一號神木<br><img src="https://1013399.github.io/image-7/391/123301404_l.jpg" alt="北得拉曼步道 一號神木" loading="lazy"></p><p>小瀑布<br><img src="https://1013399.github.io/image-7/391/123301440_l.jpg" alt="北得拉曼步道 小瀑布" loading="lazy"></p><p>1.8K木樁處，也有叉路往內鳥嘴山<br><img src="https://1013399.github.io/image-7/391/123301577_l.jpg" alt="北得拉曼步道 1.8K木樁處" loading="lazy"></p><p>未編號巨木<br><img src="https://1013399.github.io/image-7/391/123301632_l.jpg" alt="北得拉曼步道 巨木" loading="lazy"></p><p>未編號巨木<br><img src="https://1013399.github.io/image-7/391/123301633_l.jpg" alt="北得拉曼步道 巨木" loading="lazy"></p><p>大岩塊<br><img src="https://1013399.github.io/image-7/391/123301634_l.jpg" alt="北得拉曼步道 大岩塊" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',30),c={href:"http://www.tonyhuang39.com/tony0605/tony0605.html",target:"_blank",rel:"noopener noreferrer"},d=t("br",null,null,-1),_=t("img",{src:"https://1013399.github.io/image-7/391/123301650_l.jpg",alt:"北得拉曼步道 參考地圖",loading:"lazy"},null,-1);function u(y,j){const i=r("ExternalLinkIcon");return l(),o("div",null,[h,b,p(" more "),m,t("p",null,[t("a",c,[a("Tony的自然人文旅記"),s(i)]),d,_])])}const K=g(n,[["render",u],["__file","post-391-2008-10-20.html.vue"]]);export{K as default};
