import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{r,o,c as l,a as n,b as a,f as t,d as g,e as p}from"./app-63889d57.js";const s={},h=a("p",null,[a("strong",null,"石牛山"),t("，海拔671公尺，位於關西鎮錦山里羅馬公路旁，山頂有三等三角點#6247基石，山頂平台寬大有巨石，具360度的視野，可以遠眺石門水庫、大溪、龍潭、三峽等地區，反方向也可看見鳥嘴山以及蜿蜒山道，所有馬武督風光一覽無疑，該山名列台灣小百岳#025。")],-1),c=p('<p>石牛山登山口石福宮，由左由右皆可上石牛山，左平緩右較陡，這次選擇右上左下繞行一圈。</p><p>登山日期：2008/10/10</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>北二高下關西交流道後，取左沿118縣道往關西方向行駛，進入市區過台三線後續往羅孚行駛，最後過「李樹下」站牌(118縣道34.5K)後，約10公尺取左進入狹窄產業道路往石牛山，約100公尺遇水泥橋叉路，取右依「石牛山或石牛山民宿」指標沿柏油路前進，接水泥路後遇叉路口續直走(左往石牛山民宿)，抵達石福宮私人停車場(收費100元，主人會提供登山路線圖)。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→石福宮<br> →戴宅<br> →叉路(右較緩、較遠山路也可上石牛山，取左上)<br> →溪谷(山徑貼緊溪岸岩壁，拉繩來回越過)<br> →巨石窄洞<br> →稜線<br> →<strong>石牛山</strong><br> →叉路(直走往彩和山，取左陡下)<br> →呂宅(錦山72號)<br> →石福宮</p><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>石牛山三等三角點#6247基石<br><img src="https://1013399.github.io/image-7/392/123315454_l.jpg" alt="石牛山三等三角點#6247基石" loading="lazy"></p><p>石牛山<br><img src="https://1013399.github.io/image-7/392/123315456_l.jpg" alt="石牛山" loading="lazy"></p><p>石牛山<br><img src="https://1013399.github.io/image-7/392/123315458_l.jpg" alt="石牛山" loading="lazy"></p><p>山頂巨石(向陽石)<br><img src="https://1013399.github.io/image-7/392/123315460_l.jpg" alt="石牛山 山頂巨石" loading="lazy"></p><p>石牛山頂好視野<br><img src="https://1013399.github.io/image-7/392/123315462_l.jpg" alt="石牛山 山頂好視野" loading="lazy"></p><p>石牛山頂好視野<br><img src="https://1013399.github.io/image-7/392/123315467_l.jpg" alt="石牛山 山頂好視野" loading="lazy"></p><p>石牛山頂好視野<br><img src="https://1013399.github.io/image-7/392/123315523_l.jpg" alt="石牛山 山頂好視野" loading="lazy"></p><hr><p>補上兩張於2011/11/27拍攝到的「雲瀑」及一張組合過的群山美景<br><img src="https://1013399.github.io/image-7/392/204183175_l.jpg" alt="石牛山 雲瀑" loading="lazy"></p><figure><img src="https://1013399.github.io/image-7/392/204183188_l.jpg" alt="石牛山 雲瀑" tabindex="0" loading="lazy"><figcaption>石牛山 雲瀑</figcaption></figure><figure><img src="https://1013399.github.io/image-7/392/204183251_l.jpg" alt="石牛山 組合過的群山美景" tabindex="0" loading="lazy"><figcaption>石牛山 組合過的群山美景</figcaption></figure><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',19),d={href:"http://www.tonyhuang39.com/tony0444/tony0444.html",target:"_blank",rel:"noopener noreferrer"},b=a("br",null,null,-1),m=a("img",{src:"https://1013399.github.io/image-7/392/123315668_l.jpg",alt:"石牛山 參考地圖",loading:"lazy"},null,-1);function _(u,f){const i=r("ExternalLinkIcon");return o(),l("div",null,[h,n(" more "),c,a("p",null,[a("a",d,[t("Tony的自然人文旅記"),g(i)]),b,m])])}const j=e(s,[["render",_],["__file","post-392-2008-10-13.html.vue"]]);export{j as default};
