import{_ as n}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o as a,c as s,a as g,b as t,f as r,d as i,e as l}from"./app-7f62a6ff.js";const h={},p=t("p",null,[t("strong",null,"獅頭山風景區"),r("隸屬於參山國家風景區，分為前山(獅頭)及後山(獅尾)兩部分，前山獅頭位於苗栗縣南庄鄉，勸化堂停車場為大眾化路線及石階步道，遊客眾多；後山獅尾位於新竹縣峨眉鄉七星村六寮地區，自然景觀豐富，自然步道較多，獅山遊客服務中心設於峨眉鄉。")],-1),c=l('<p>獅頭山風景區步道在前山獅頭有<strong>獅山龍門口登山步道</strong>；後山獅尾則有<strong>水濂洞步道</strong>、<strong>水濂橋古道</strong>、<strong>獅山古道</strong>、<strong>六寮古道</strong>、<strong>茶亭步道</strong>、<strong>猿山步道</strong>及<strong>藤坪步道</strong>等步道。</p><p><strong>猿山</strong>，海拔463公尺，又名猴子山，位於新竹縣峨眉鄉猿山步道頂端，山頂有三等三角點一二二號基石，也是苗栗縣與新竹縣之界山。<br>     由於已走過獅頭，此次走獅尾部分，先走水濂洞步道、水濂橋古道，再走藤坪步道、猿山步道登猿山，最後接茶亭步道，從六寮古道下山。</p><p>登山日期：2008/09/06</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>台3線往南行駛，經北埔、峨眉後，至台3線86.5k處，左轉沿竹41鄉道往獅頭山，行駛至竹41鄉道4.5k進入獅頭山牌樓，左前方為水濂橋古道的出口，右前方為獅山古道的入口，續行駛約100公尺，右轉上獅山遊客服務中心停車。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→遊客中心<br> →<strong>水濂洞步道</strong>(步道口在遊客中心前右轉竹41鄉道數十公尺處下切，天然大岩洞內設有梵音寺)<br> →<strong>水濂橋古道</strong>(沿溪而行，有座百年糯米橋，欣賞壺穴、峽谷景觀)<br> →遊客中心<br> →<strong>藤坪步道</strong>(登山口在遊客中心入口旁，沿途擁有多種藤類植物)<br> →賞鳥平台<br> →休憩平台(叉路，右下往六寮古道，取直走往猿山)<br> →猿山步道(蚊子超多)<br> →<strong>猿山</strong><br> →<strong>茶亭步道</strong><br> →伯公廟<br> →七星神木<br> →<strong>六寮古道</strong><br> →遊客中心</p><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>水濂洞步道壺穴、峽谷景觀<br><img src="https://1013399.github.io/image-7/393/123376348_l.jpg" alt="水濂洞步道壺穴、峽谷景觀" loading="lazy"></p><p>水濂橋古道糯米橋下「一線天」景觀<br><img src="https://1013399.github.io/image-7/393/123376416_l.jpg" alt="水濂橋古道糯米橋下「一線天」景觀" loading="lazy"></p><p>猿山三等三角點<br><img src="https://1013399.github.io/image-7/393/123376459_l.jpg" alt="猿山三等三角點" loading="lazy"></p><p>猿山三等三角點一二二號<br><img src="https://1013399.github.io/image-7/393/123376539_l.jpg" alt="猿山三等三角點一二二號" loading="lazy"></p><p>七星神木<br><img src="https://1013399.github.io/image-7/393/123376540_l.jpg" alt="七星神木" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',14),d={href:"http://www.tonyhuang39.com/tony0393.html",target:"_blank",rel:"noopener noreferrer"},b=t("br",null,null,-1),_=t("img",{src:"https://1013399.github.io/image-7/393/123376563_l.jpg",alt:"水濂洞步道、藤坪步道、猿山、六寮古道 參考地圖",loading:"lazy"},null,-1);function m(u,f){const o=e("ExternalLinkIcon");return a(),s("div",null,[p,g(" more "),c,t("p",null,[t("a",d,[r("Tony的自然人文旅記"),i(o)]),b,_])])}const k=n(h,[["render",m],["__file","post-393-2008-09-08.html.vue"]]);export{k as default};
