import{_ as i}from"./plugin-vue_export-helper-c27b6911.js";import{r,o as h,c as n,a as o,b as a,f as e,d as l,e as s}from"./app-afda0674.js";const d={},g=a("p",null,[a("strong",null,"光天高山"),e("，海拔1123公尺，無基石，位於苗栗縣南庄鄉，為加里山脈的一條支稜，東側受大東河的切割侵蝕，峭崖壁立；西側則山坡平緩，稜脈較寬廣，山頂可俯瞰整個向天湖。")],-1),p=a("p",null,[a("strong",null,"向天湖山"),e("，海拔1225公尺，森林三角點，亦位於苗栗縣南庄鄉，山頂為小空地，有一角落可眺望五峰鵝公髻山，該山名列小百岳#032。")],-1),c=s('<p>光天高山與向天湖山自登山口起登，形成Ｏ形路線，繞一圈約二至三小時，海拔落差約400公尺，而全程幾乎走在柳杉森林裡，可說是一條絕佳的森林浴路線。</p><p><strong>神仙谷</strong>，是風美溪及鹿湖溪匯流在三個斷層區上，形成雙瀑布，落差30公尺，由六、七層的岩階組成水流從上層傾瀉而下，形成多道奔騰的水瀑，在瀑底形成深潭，岩石因流水迴溢流竄形成優美紋路。</p><p>登山日期：2008/08/23</p><h2 id="光天高山、向天湖山" tabindex="-1"><a class="header-anchor" href="#光天高山、向天湖山" aria-hidden="true">#</a> 光天高山、向天湖山</h2><h3 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h3><p>國道一號下頭份交流道，接上縣道124線往三灣、南庄方向行駛，經過三灣、南庄後，接苗21鄉道往鹿場道路行，經東河部落百媚橋後至叉路，右下過加拉彎大橋後蜿蜒上行，穿過向天湖牌樓，經向天湖停車場續往前行，沿著狹小的環湖產業道路前進約1公里，至「向天湖14號」民宅附近空地停車，前行約50公尺叉路，往右上行約100公尺即達登山口，旁有三個大型綠色水塔。</p><h3 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h3><p>→登山口<br> →叉路(左往向天湖山，取直行往光天高山，沿著水管路前進，山路濕滑)<br> →稜線叉路(直行往加里山，左往向天湖山，取右往光天高山)<br> →<strong>光天高山</strong>(折返)<br> →回到稜線叉路(直行往向天湖山)<br> →叉路(右往南大龜山、加里山，續直行往向天湖山)<br> →杉木林叉路(左下往登山口、向天湖部落，右上往向天湖山)<br> →**向天湖山<br> **→回到杉木林叉路(直行下山)<br> →叉路(右往三角湖山、瞭望台，續直行)<br> →平山界石柱<br> →巨石<br> →登山口</p><h3 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h3><p>光天高山<br><img src="https://1013399.github.io/image-7/394/123382120_l.jpg" alt="光天高山" loading="lazy"></p><p>光天高山<br><img src="https://1013399.github.io/image-7/394/123382127_l.jpg" alt="光天高山" loading="lazy"></p><p>陡上岩壁，登光天高山(回程拍攝)<br><img src="https://1013399.github.io/image-7/394/123382184_l.jpg" alt="光天高山 陡上岩壁" loading="lazy"></p><p>向天湖山<br><img src="https://1013399.github.io/image-7/394/123382186_l.jpg" alt="向天湖山" loading="lazy"></p><p>向天湖山森林三角點<br><img src="https://1013399.github.io/image-7/394/123382187_l.jpg" alt="向天湖山森林三角點" loading="lazy"></p><p>優美柳杉林(起霧了)<br><img src="https://1013399.github.io/image-7/394/123382188_l.jpg" alt="向天湖山 柳杉林" loading="lazy"></p><p>巨石<br><img src="https://1013399.github.io/image-7/394/123382225_l.jpg" alt="向天湖山 巨石" loading="lazy"></p><p>漂亮的白色玫瑰花<br><img src="https://1013399.github.io/image-7/394/123382226_l.jpg" alt="向天湖山 白色玫瑰花" loading="lazy"></p><h2 id="神仙谷" tabindex="-1"><a class="header-anchor" href="#神仙谷" aria-hidden="true">#</a> 神仙谷</h2><h3 id="行車路線-1" tabindex="-1"><a class="header-anchor" href="#行車路線-1" aria-hidden="true">#</a> 行車路線</h3><p>登完光天高山與向天湖山後，往回接苗21鄉道右轉往鹿場道路行，沿途經過幾個部落，於石壁橋可欣賞附近石壁峽谷的峭壁奇景，過石壁橋不久又可見一處更巍峨的峭壁雄風崖壁，經過峭壁雄風，即可抵達風美著名景點神仙谷，入口設有小型停車場，沿著石階往溪谷方向走，約三分鐘路程就可抵達神仙谷。</p><h3 id="走走相片-1" tabindex="-1"><a class="header-anchor" href="#走走相片-1" aria-hidden="true">#</a> 走走相片</h3><p>神仙谷<br><img src="https://1013399.github.io/image-7/394/123382228_l.jpg" alt="神仙谷" loading="lazy"></p><p>神仙谷<br><img src="https://1013399.github.io/image-7/394/123382229_l.jpg" alt="神仙谷" loading="lazy"></p><p>神仙谷吊橋<br><img src="https://1013399.github.io/image-7/394/123382259_l.jpg" alt="神仙谷吊橋" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><h3 id="南庄交通路線圖" tabindex="-1"><a class="header-anchor" href="#南庄交通路線圖" aria-hidden="true">#</a> 南庄交通路線圖</h3><figure><img src="https://1013399.github.io/image-7/394/123382347_l.jpg" alt="南庄交通路線圖" tabindex="0" loading="lazy"><figcaption>南庄交通路線圖</figcaption></figure><h3 id="光天高山、向天湖山地圖" tabindex="-1"><a class="header-anchor" href="#光天高山、向天湖山地圖" aria-hidden="true">#</a> 光天高山、向天湖山地圖</h3>',28),b={href:"http://www.tonyhuang39.com/tony0598/tony0598.html",target:"_blank",rel:"noopener noreferrer"},m=a("br",null,null,-1),_=a("img",{src:"https://1013399.github.io/image-7/394/123382357_l.jpg",alt:"光天高山、向天湖山 參考地圖",loading:"lazy"},null,-1);function u(f,x){const t=r("ExternalLinkIcon");return h(),n("div",null,[g,p,o(" more "),c,a("p",null,[a("a",b,[e("Tony的自然人文旅記"),l(t)]),m,_])])}const z=i(d,[["render",u],["__file","post-394-2008-08-25.html.vue"]]);export{z as default};
