import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{r as n,o,c as s,a as l,b as e,f as r,d as i,e as h}from"./app-19acc492.js";const c={},d=e("p",null,[e("strong",null,"仙山"),r("，海拔967公尺，又稱紅毛館山，位於苗栗縣獅潭鄉，山頂原有三等三角點#4196基石，但已遺失，仙山的登山口位於124縣道43K處的靈洞宮旁，該山名列台灣小百岳#033。")],-1),p=e("p",null,[e("strong",null,"八仙古道"),r("，為獅潭仙山與南庄八卦力之間的古道，出口在124縣道41.7K處，亦是仙山的第二登山口。")],-1),_=h('<p>登山日期：2008/08/16</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>中山高頭份交流道下往三灣方向，至珊珠湖轉台三線南下，經過三灣抵達獅潭，然後由獅潭走124縣道，至124縣道43K，於仙山靈洞宮立體停車場停車。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→靈洞宮<br> →叉路(右經水仙亭往協靈宮，取左)<br> →叉路(右經仙靈亭往協靈宮，取左)<br> →叉路(左為新舖設花崗石步道回124縣道，取右舊石階上行)<br> →仙洞門(由岩縫中穿過，開始進入多岩石的陡山徑)<br> →稜線平台空地<br> →叉路(右小石階步道可往協靈宮，續直行)<br> →望陽台涼亭(右山徑亦可往協靈宮，休息後續行，開始急陡的岩壁)<br> →稜線平台(陡峭岩壁結束)<br> →叉路(右往神桌山，取左)<br> →<strong>仙山</strong>(折返)<br> →回到叉路<br> →山頭叉路(有指標，左為神仙縱走路線往神桌山，取右沿八仙古道下山)<br> →叉路(旁有「山」字水泥柱，左雜草山徑往四十二分山，取右)<br> →124縣道(41.7K，旁有仙山幹#148電力桿，取右沿公路往靈洞宮)<br> →涼亭<br> →靈洞宮</p><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>仙洞門<br><img src="https://1013399.github.io/image-7/395/123388265_l.jpg" alt="仙洞門 仙山、八仙古道" loading="lazy"></p><p>望陽台涼亭<br><img src="https://1013399.github.io/image-7/395/123388271_l.jpg" alt="望陽台涼亭 仙山、八仙古道" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',9),b={href:"http://www.tonyhuang39.com/tony0471/tony0471.html",target:"_blank",rel:"noopener noreferrer"},g=e("br",null,null,-1),m=e("img",{src:"https://1013399.github.io/image-7/395/123388306_l.jpg",alt:"仙山、八仙古道 參考地圖",loading:"lazy"},null,-1);function u(f,x){const t=n("ExternalLinkIcon");return o(),s("div",null,[d,p,l(" more "),_,e("p",null,[e("a",b,[r("Tony的自然人文旅記"),i(t)]),g,m])])}const N=a(c,[["render",u],["__file","post-395-2008-08-18.html.vue"]]);export{N as default};
