import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{r as i,o,c as n,a as s,b as a,e as g,d as l,f as t}from"./app-4a074c75.js";const h={},p=t("<p><strong>大凍山</strong>，海拔1976公尺，位於嘉義縣竹崎鄉中和村奮起湖，又稱糞箕山，山頂有觀日亭，山頂雖無基石，但視野極佳，名列台灣小百岳#060。</p><p>大凍山國家步道是一環狀的步道，全長約5公里，除了大凍山觀日亭外，尚有<strong>樹石盟</strong>、<strong>霹靂山</strong>、<strong>石獅象</strong>、<strong>十八羅漢洞</strong>、<strong>流星崖</strong>、<strong>明月窟</strong>等景點。</p>",2),d=t('<p>大凍山與阿里山祝山為觀日勝地，祝山之日出只有4秒，而大凍山之日出長達3分20秒，這是中南部觀日出最美的山峰，阿里山、玉山、大小塔山、太和、草嶺等勝景近收眼底。</p><p>登山日期：2008/07/23</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號下中埔交流道往阿里山方向，沿台18線至石卓左轉往奮起湖，至奮起湖後接嘉155鄉道往來吉，便可抵達多林檢查哨，於檢查哨停車場停車，大凍山登山口就在多林檢查哨斜對面。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→大凍山登山口(有大凍山國家步道標示牌，右往流星崖，取左往大凍山)<br> →叉路(右往石獅象，取左往大凍山)<br> →叉路(左右皆可上大凍山，取左經樹石盟上大凍山)<br> →樹石盟(大樹盤繞於巨石塊上)<br> →叉路(右往石獅象，取左上行)<br> →<strong>大凍山</strong>(山頂有超大的觀日平台，視野極佳，賞景後原路下山)<br> →回到叉路(右為剛才上山路線，取左下山)<br> →涼亭(叉路，左往一探七星石)<br> →七星石(直走可往石獅象，小朋友喊累了，於是折返不再續行)<br> →回涼亭(取左下山)<br> →大凍山登山口</p><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>大凍山登山口<br><img src="https://1013399.github.io/image-7/397/123466395_l.jpg" alt="大凍山登山口 (多林檢查站路線)" loading="lazy"></p><p>樹石盟<br><img src="https://1013399.github.io/image-7/397/123466376_l.jpg" alt="大凍山 樹石盟 (多林檢查站路線)" loading="lazy"></p><p>大凍山觀景平台<br><img src="https://1013399.github.io/image-7/397/123466377_l.jpg" alt="大凍山觀景平台" loading="lazy"></p><p>平台視野極佳<br><img src="https://1013399.github.io/image-7/397/123466378_l.jpg" alt="大凍山 (多林檢查站路線)" loading="lazy"></p><p>沿路都是杉木林<br><img src="https://1013399.github.io/image-7/397/123466381_l.jpg" alt="大凍山 杉木林 (多林檢查站路線)" loading="lazy"></p><p>杉木林<br><img src="https://1013399.github.io/image-7/397/123466389_l.jpg" alt="大凍山 杉木林 (多林檢查站路線)" loading="lazy"></p><p>杉木林<br><img src="https://1013399.github.io/image-7/397/123466393_l.jpg" alt="大凍山 杉木林 (多林檢查站路線)" loading="lazy"></p><p>涼亭(左叉路往七星石)<br><img src="https://1013399.github.io/image-7/397/123466394_l.jpg" alt="大凍山 涼亭 (多林檢查站路線)" loading="lazy"></p><p>迷霧杉木林<br><img src="https://1013399.github.io/image-7/397/123466398_l.jpg" alt="大凍山 杉木林 (多林檢查站路線)" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><h3 id="阿里山公路圖" tabindex="-1"><a class="header-anchor" href="#阿里山公路圖" aria-hidden="true">#</a> 阿里山公路圖</h3><figure><img src="https://1013399.github.io/image-7/397/123466411_l.jpg" alt="阿里山公路圖" tabindex="0" loading="lazy"><figcaption>阿里山公路圖</figcaption></figure><h3 id="大凍山登山地圖" tabindex="-1"><a class="header-anchor" href="#大凍山登山地圖" aria-hidden="true">#</a> 大凍山登山地圖</h3>',20),c={href:"http://www.thetea.com.tw/",target:"_blank",rel:"noopener noreferrer"},b=a("br",null,null,-1),m=a("img",{src:"https://1013399.github.io/image-7/397/123466417_l.jpg",alt:"大凍山 (多林檢查站路線) 參考地圖",loading:"lazy"},null,-1);function _(u,f){const r=i("ExternalLinkIcon");return o(),n("div",null,[p,s(" more "),d,a("p",null,[a("a",c,[g("大凍山休閒渡假中心"),l(r)]),b,m])])}const y=e(h,[["render",_],["__file","post-397-2008-07-25.html.vue"]]);export{y as default};
