import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r,o,c as g,a as e,b as t,f as a,d as n,e as p}from"./app-2f66486a.js";const s={},h=t("p",null,[t("strong",null,"飛鳳山"),a("，海拔423公尺，省府地政處測量局三等控制點#S151基石，因山形似飛鳳伸頸行將斂翼下降之勢而得名，為新竹八景之一，名列台灣小百岳#027。")],-1),b=t("p",null,[t("strong",null,"石碧潭山"),a("，海拔402公尺，位於豐柑步道途中，三等三角點#100。")],-1),c=t("p",null,[t("strong",null,"中坑山"),a("，海拔462公尺，又稱王爺坑山，位於觀日亭附近，二等三角點#1050。")],-1),d=p('<hr><p>飛鳳山步道系統：</p><ul><li><strong>梅嶺步道</strong>：以代勸堂為起點，經飛鳳夕照涼亭及相思崖至觀日亭，為水泥石階步道。</li><li><strong>紫藤步道</strong>：以修心宮為起點，一路陡坡至相思崖，為最陡的一條步道，長1800公尺。</li><li><strong>豐柑步道</strong>：以修心宮為起點，途經石壁潭山，為泥土坡路，長2800公尺。</li><li><strong>綠穹步道(森林步道)</strong>：以第二停車場為起點，大都為石板步道，為較平緩的步道。</li></ul><p>飛鳳山為新竹熱門郊山，由於離家近，已經爬過不下百回，所有步道也都嘗試走過，最喜歡的路線是由豐柑步道(簡稱2800)起登，至觀日亭後由森林步道(簡稱1300)下至代勸堂，再沿柏油路回停車處，此O型路線雖不經飛鳳山基石(基石在第二涼亭旁小山丘上)，但沿途景緻較佳，又可避開較多傷膝的石階，走完全程約2至3小時。</p><p>路線簡述如下：</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→修心宮<br> →小橋<br> →鐵柵門<br> →登山口<br> →緩坡(4、5月可賞油桐花)<br> →陡坡<br> →陡坡結束休息平台(可俯瞰頭前溪、竹林大橋，眺望竹東市區)<br> →石碧潭山(基石不在路旁，需上切果園稜線)<br> →風情萬景咖啡廳<br> →山頂平台(與梅嶺步道交會)<br> →觀日亭(風和日麗時可看到台北101)<br> →森林步道(常可看到五色鳥)<br> →第一涼亭(飛鳳夕照)<br> →代勸堂(1月賞櫻、3月賞杜鵑、12月賞梅，可遠眺鵝公髻山、加里山)<br> →雲谷寺<br> →第三停車場<br> →第二停車場<br> →感應橋<br> →修心宮</p><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>飛鳳山<br><img src="https://1013399.github.io/image-7/408/121400308_l.jpg" alt="飛鳳山" loading="lazy"></p><p>油桐花<br><img src="https://1013399.github.io/image-7/408/121400010_l.jpg" alt="飛鳳山 油桐花" loading="lazy"></p><p>休息平台，俯瞰頭前溪、竹林大橋，眺望竹東市區<br><img src="https://1013399.github.io/image-7/408/121400046_l.jpg" alt="頭前溪、竹林大橋，眺望竹東市區" loading="lazy"></p><p>石碧潭山<br><img src="https://1013399.github.io/image-7/408/121400049_l.jpg" alt="石碧潭山" loading="lazy"></p><p>石碧潭山三等三角點#100基石<br><img src="https://1013399.github.io/image-7/408/121400111_l.jpg" alt="石碧潭山三等三角點#100基石" loading="lazy"></p><p>風情萬景咖啡廳<br><img src="https://1013399.github.io/image-7/408/121400132_l.jpg" alt="飛鳳山 風情萬景咖啡廳" loading="lazy"></p><p>相思崖<br><img src="https://1013399.github.io/image-7/408/121400273_l.jpg" alt="相思崖 飛鳳山、石碧潭山、中坑山" loading="lazy"></p><p>相思崖<br><img src="https://1013399.github.io/image-7/408/121400289_l.jpg" alt="相思崖 飛鳳山、石碧潭山、中坑山" loading="lazy"></p><p>飛鳳山省府地政處測量局三等控制點<br><img src="https://1013399.github.io/image-7/408/121400321_l.jpg" alt="飛鳳山省府地政處測量局三等控制點" loading="lazy"></p><p>觀日亭<br><img src="https://1013399.github.io/image-7/408/121400336_l.jpg" alt="觀日亭 飛鳳山、石碧潭山、中坑山" loading="lazy"></p><p>眺望台北101的方向<br><img src="https://1013399.github.io/image-7/408/121400352_l.jpg" alt="飛鳳山、石碧潭山、中坑山" loading="lazy"></p><p>補一張2009/09/26於觀日亭拍攝的<strong>台北101</strong>相片<br><img src="https://1013399.github.io/image-7/408/135549778_l.jpg" alt="飛鳳山 台北101" loading="lazy"></p><p>中坑山二等三角點#1050基石<br><img src="https://1013399.github.io/image-7/408/121400367_l.jpg" alt="中坑山二等三角點#1050基石" loading="lazy"></p><p>中坑山二等三角點#1050基石<br><img src="https://1013399.github.io/image-7/408/121400383_l.jpg" alt="中坑山二等三角點#1050基石" loading="lazy"></p><p>森林步道<br><img src="https://1013399.github.io/image-7/408/121400402_l.jpg" alt="飛鳳山、石碧潭山、中坑山 森林步道" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',24),m={href:"http://www.tonyhuang39.com/tony0442/tony0442.html",target:"_blank",rel:"noopener noreferrer"},_=t("br",null,null,-1),u=t("img",{src:"https://1013399.github.io/image-7/408/121410049_l.jpg",alt:"飛鳳山、石碧潭山、中坑山 參考地圖",loading:"lazy"},null,-1);function y(j,z){const i=r("ExternalLinkIcon");return o(),g("div",null,[h,b,c,e(" more "),d,t("p",null,[t("a",m,[a("Tony的自然人文旅記"),n(i)]),_,u])])}const k=l(s,[["render",y],["__file","post-408-2007-05-19.html.vue"]]);export{k as default};
