import{_ as o}from"./plugin-vue_export-helper-c27b6911.js";import{r as t,o as r,c as e,a as h,b as i,d as g,w as s,f as a,e as n}from"./app-44b14332.js";const m={},d=i("p",null,[a("台灣第二高峰-"),i("strong",null,"雪山"),a("，曾被名為次高山、西魯維亞山、興隆山，海拔3886公尺，位於台中市和平區與苗栗縣泰安鄉交界，山頂設有石碑與一等三角點，特色為高山峻嶺且能鎮護地方之山，台灣百岳中具大山之勢，山勢為百岳五嶽之一，名列台灣百岳No.5(舊序)。")],-1),c=n('<p>下了一整晚的雨，雨勢未見停歇，凌晨1點起床，01:30享用布農族協作準備的早餐(稀飯)後，再回寢室睡回籠覺，3點多雨勢稍緩，猶豫之間，決定出發往雪山主峰，04:20從三六九山莊輕裝出發，雨中行走，又濕又冷，來回5小時完成。</p><p>走走日期：2016/08/13</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><h3 id="往雪山主峰" tabindex="-1"><a class="header-anchor" href="#往雪山主峰" aria-hidden="true">#</a> 往雪山主峰</h3><p>→輕裝出發往雪山主峰<br><img src="https://1013399.github.io/image-1/42/1136344355_l.jpg" alt="IMGP9874.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→叉路，右為黑森林水源路起點，可往凱蘭特昆山、雪山北峰，取左往雪山主峰<br><img src="https://1013399.github.io/image-1/42/1136342099_l.jpg" alt="IMGP9875.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→7.8K里程樁，進入黑森林<br><img src="https://1013399.github.io/image-1/42/1136342359_l.jpg" alt="IMGP9876.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→進入黑森林後，跟著樹上的反光標誌走，就不會迷路<br><img src="https://1013399.github.io/image-1/42/1136344709_l.jpg" alt="IMGP9877.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→8K里程樁<br><img src="https://1013399.github.io/image-1/42/1136344711_l.jpg" alt="IMGP9878.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→經過石瀑區，約在8.5~8.6K處<br><img src="https://1013399.github.io/image-1/42/1136343939_l.jpg" alt="IMGP9879.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→黑森林中陡上，沿途是優美的冷杉純林<br><img src="https://1013399.github.io/image-1/42/1136344521_l.jpg" alt="IMGP9882-83.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→大片倒下的杉木<br><img src="https://1013399.github.io/image-1/42/1136341894_l.jpg" alt="IMGP9885A.jpg - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→休息空地，約在8.9K處<br><img src="https://1013399.github.io/image-1/42/1136342363_l.jpg" alt="IMGP9886.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→9K里程樁<br><img src="https://1013399.github.io/image-1/42/1136343942_l.jpg" alt="IMGP9887.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→9.2K里程樁，連續長陡上<br><img src="https://1013399.github.io/image-1/42/1136342364_l.jpg" alt="IMGP9889A.jpg - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→千年古柏-玉山圓柏，約在9.4K處<br><img src="https://1013399.github.io/image-1/42/1136341393_l.jpg" alt="IMGP9890.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→9.7K，走出黑森林，前方山頭(北稜角)隱沒於雲霧中<br><img src="https://1013399.github.io/image-1/42/1136344360_l.jpg" alt="IMGP9891.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→9.8K，進入雪山一號圈谷，霧濛濛無法一窺全貌<br><img src="https://1013399.github.io/image-1/42/1136341395_l.jpg" alt="IMGP9892-95.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→10K里程樁<br><img src="https://1013399.github.io/image-1/42/1136342367_l.jpg" alt="IMGP9896.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→雪山主峰下，大片碎石坡<br><img src="https://1013399.github.io/image-1/42/1136344861_l.jpg" alt="IMGP9897.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→之字型陡上，經過10.6K<br><img src="https://1013399.github.io/image-1/42/1136345101_l.jpg" alt="IMGP9898-99.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→到達雪山主峰，旁有10.9K里程樁<br><img src="https://1013399.github.io/image-1/42/1136341155_l.jpg" alt="IMGP9902.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→雪山主峰，山頂解說牌與一等三角點<br><img src="https://1013399.github.io/image-1/42/1136343945_l.jpg" alt="IMGP9902A-07.jpg - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→右稜線為聖稜線，可往北稜角、凱蘭特崑山、雪山北峰<br><img src="https://1013399.github.io/image-1/42/1136342370_l.jpg" alt="IMGP9906.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→山頂風大溫低，拍張供頂照後，速速下山<br><img src="https://1013399.github.io/image-1/42/1136345104_l.jpg" alt="IMGP9907E.jpg - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→回至雪山圈谷，圈谷地形仍看不清<br><img src="https://1013399.github.io/image-1/42/1136342372_l.jpg" alt="IMGP9910.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→回至9.7K，進入黑森林<br><img src="https://1013399.github.io/image-1/42/1136341399_l.jpg" alt="IMGP9911.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→回至拉繩處<br><img src="https://1013399.github.io/image-1/42/1136344440_l.jpg" alt="IMGP9912.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→回至石瀑區<br><img src="https://1013399.github.io/image-1/42/1136344443_l.jpg" alt="IMGP9913.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→回至7.8K，出黑森林<br><img src="https://1013399.github.io/image-1/42/1136344912_l.jpg" alt="IMGP9914.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→走出黑森林後，雨停了，視野豁然開朗，眺望武陵四秀<br><img src="https://1013399.github.io/image-1/42/1136341567_l.jpg" alt="IMGP9915A.jpg - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→中央山脈群山依舊未露臉<br><img src="https://1013399.github.io/image-1/42/1136344368_l.jpg" alt="IMGP9915B.jpg - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→三六九山莊後面，曾經失火的地方<br><img src="https://1013399.github.io/image-1/42/1136342663_l.jpg" alt="IMGP9917.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→回到三六九山莊，等候布農協作準備點心時，拍張合照<br><img src="https://1013399.github.io/image-1/42/1136344460_l.jpg" alt="IMGP9918A.JPG - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><h3 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h3><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約5小時(含休息)</p><p>高低落差：約730公尺(不含上下起伏)</p></div><h3 id="下山-離開三六九山莊下山" tabindex="-1"><a class="header-anchor" href="#下山-離開三六九山莊下山" aria-hidden="true">#</a> 下山，離開三六九山莊下山</h3><p>→用完點心，整裝完畢，拍張合照後，離開三六九山莊下山<br><img src="https://1013399.github.io/image-1/42/1136344447_l.jpg" alt="IMGP9919A.jpg - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→回到停機坪叉路，右上往東峰，灰濛濛無視野，就不上去了，取左腰繞<br><img src="https://1013399.github.io/image-1/42/1136342665_l.jpg" alt="IMGP9920A.jpg - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→回到陡下再陡上路段<br><img src="https://1013399.github.io/image-1/42/1136341166_l.jpg" alt="IMGP9921A.jpg - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→回到4.4K哭坡頂<br><img src="https://1013399.github.io/image-1/42/1136341169_l.jpg" alt="IMGP9922A.jpg - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→回到4K哭坡觀景台<br><img src="https://1013399.github.io/image-1/42/1136344917_l.jpg" alt="IMGP9922D.jpg - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→回到3.4K木椅休息區<br><img src="https://1013399.github.io/image-1/42/1136344533_l.jpg" alt="IMGP9923A.jpg - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→回到2K七卡山莊<br><img src="https://1013399.github.io/image-1/42/1136344918_l.jpg" alt="IMGP9924A.jpg - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→在七卡山莊休息、午餐<br><img src="https://1013399.github.io/image-1/42/1136344452_l.jpg" alt="IMGP9925A.jpg - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><p>→回到雪山登山口<br><img src="https://1013399.github.io/image-1/42/1136345211_l.jpg" alt="IMGP9926A.jpg - 台中和平雪山主東(DAY2－雪山主峰)" loading="lazy"></p><h3 id="走走資訊-1" tabindex="-1"><a class="header-anchor" href="#走走資訊-1" aria-hidden="true">#</a> 走走資訊</h3><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約4小時(含休息、午餐)</p><p>高低落差：約1030公尺(不含上下起伏)</p></div><h2 id="上一篇相關文章" tabindex="-1"><a class="header-anchor" href="#上一篇相關文章" aria-hidden="true">#</a> 上一篇相關文章</h2>',49),b=i("h2",{id:"參考地圖",tabindex:"-1"},[i("a",{class:"header-anchor",href:"#參考地圖","aria-hidden":"true"},"#"),a(" 參考地圖")],-1),_={href:"http://www.tonyhuang39.com/tony0425/tony0425.html",target:"_blank",rel:"noopener noreferrer"},G=i("br",null,null,-1),u=i("img",{src:"https://1013399.github.io/image-1/42/1136345404_l.jpg",alt:"雪山主東-tony.GIF - 台中和平雪山主東(DAY2－雪山主峰)",loading:"lazy"},null,-1);function P(j,A){const p=t("RouterLink"),l=t("ExternalLinkIcon");return r(),e("div",null,[d,h(" more "),c,i("p",null,[g(p,{to:"/posts/post-43-2016-08-23.html"},{default:s(()=>[a("【台中和平】雪山主東(DAY1－雪山東峰)")]),_:1})]),b,i("p",null,[a("原圖連結→"),i("a",_,[a("Tony的自然人文旅記"),g(l)]),G,u])])}const I=o(m,[["render",P],["__file","post-42-2016-08-24.html.vue"]]);export{I as default};
