import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as e,c as r,a as g,b as a,e as i,d as h,f as o}from"./app-3fad0725.js";const s={},n=a("p",null,[a("strong",null,"志良節山"),i("，海拔1976公尺，山頂有顆三等三角點No.6328及氣象局地殼形變觀測站、一等衛星控制點、中央研究院S031衛星點，登山口位於台7甲線56.25Km，山頂狹窄，但視野頗佳。")],-1),d=a("p",null,[a("strong",null,"平岩山"),i("，海拔1922公尺，山頂有顆三等三角點No.6301，登山口位於台7甲線57.85Km，山頂為果園高點，腹地狹窄、視野不錯，此山與「平岩縱走多加屯」的平岩山(海拔2827公尺，二等三角點No.1466)不同。")],-1),c=o('<p>志良節山與平岩山，皆位於台中市和平區，此行為「馬武霸山」活動中第一天路程順撿的兩座山頭，免揹背包原路來回輕鬆行，第二天卻因天候不佳，馬武霸山未能成行。</p><p>走走日期：2019/03/23</p><h2 id="志良節山行車路線" tabindex="-1"><a class="header-anchor" href="#志良節山行車路線" aria-hidden="true">#</a> 志良節山行車路線</h2><p>新竹科學園區管理局搭乘中巴，走國道1號轉國道3號再轉國道5號，下頭城交流道往礁溪鄉方向，經過礁溪續行台9線往宜蘭市，過宜蘭橋後右轉環河路接台7線往大同鄉，於台7線85.7K續直行台7甲線，於台7甲線56.25Km附近下車。</p><h2 id="志良節山走走路線" tabindex="-1"><a class="header-anchor" href="#志良節山走走路線" aria-hidden="true">#</a> 志良節山走走路線</h2><p>→台7甲線56.25Km，依「欣欣果園」指標進入產道<br><img src="https://1013399.github.io/image-1/5/1209590388_l.jpg" alt="IMG_2709.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→約行200公尺見基地台及民宅<br><img src="https://1013399.github.io/image-1/5/1209590193_l.jpg" alt="IMG_2711.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→從民宅後面往高處走<br><img src="https://1013399.github.io/image-1/5/1209591366_l.jpg" alt="IMG_2712.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→一小段上坡<br><img src="https://1013399.github.io/image-1/5/1209590488_l.jpg" alt="IMG_2713-14.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→志良節山山頂<br><img src="https://1013399.github.io/image-1/5/1209591465_l.jpg" alt="IMG_2715.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→志良節山，山頂設有氣象局地殼形變觀測站、一等衛星控制點<br><img src="https://1013399.github.io/image-1/5/1209591665_l.jpg" alt="IMG_2716-17.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→志良節山，三等三角點No.6328<br><img src="https://1013399.github.io/image-1/5/1209590968_l.jpg" alt="IMG_2719-20.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→山頂展望<br><img src="https://1013399.github.io/image-1/5/1209590195_l.jpg" alt="IMG_2721.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→拍照後原路回，回到台7甲線上車</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約20分鐘</p><p>高低落差：約20公尺</p></div><h2 id="平岩山行車路線" tabindex="-1"><a class="header-anchor" href="#平岩山行車路線" aria-hidden="true">#</a> 平岩山行車路線</h2><p>回到台7甲線56.25Km上車，往梨山方向續行，於台7甲線57.85Km處下車。</p><h2 id="平岩山走走路線" tabindex="-1"><a class="header-anchor" href="#平岩山走走路線" aria-hidden="true">#</a> 平岩山走走路線</h2><p>→台7甲線57.85Km路口，旁有「大里區梨產銷班第一班班場所」招牌<br><img src="https://1013399.github.io/image-1/5/1209589902_l.jpg" alt="IMG_2722.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→先在此午餐(台中市和平區中興路三段16之1號)<br><img src="https://1013399.github.io/image-1/5/1209590196_l.jpg" alt="IMG_2725.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→餐畢，出發<br><img src="https://1013399.github.io/image-1/5/1209590492_l.jpg" alt="IMG_2726.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→叉路，取左上<br><img src="https://1013399.github.io/image-1/5/1209590670_l.jpg" alt="IMG_2727.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→見二個圓形大水塔，取左離開產道，往高處走<br><img src="https://1013399.github.io/image-1/5/1209590197_l.jpg" alt="IMG_2728.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→穿越果園<br><img src="https://1013399.github.io/image-1/5/1209590198_l.jpg" alt="IMG_2729.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→到達基點處<br><img src="https://1013399.github.io/image-1/5/1209590076_l.jpg" alt="IMG_2730.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→平岩山，三等三角點No.6301，拍照後原路回<br><img src="https://1013399.github.io/image-1/5/1209590077_l.jpg" alt="IMG_2731.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→回程途中，眺望志佳陽大山(雲層遮住山頭)、馬武霸山<br><img src="https://1013399.github.io/image-1/5/1209591866_l.jpg" alt="IMG_2737.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→回到台7甲線上車<br><img src="https://1013399.github.io/image-1/5/1209591266_l.jpg" alt="IMG_2739.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><h2 id="走走資訊-1" tabindex="-1"><a class="header-anchor" href="#走走資訊-1" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約35分鐘</p><p>高低落差：約40公尺</p></div><h2 id="希瑪農莊行車路線" tabindex="-1"><a class="header-anchor" href="#希瑪農莊行車路線" aria-hidden="true">#</a> 希瑪農莊行車路線</h2><p>回到台7甲線57.85Km上車，往梨山方向續行，於台7甲線61.8Km處(中興路三段環山一巷)右下環山部落，約行駛800公尺遇第一叉路口下車。</p><h2 id="希瑪農莊走走路線" tabindex="-1"><a class="header-anchor" href="#希瑪農莊走走路線" aria-hidden="true">#</a> 希瑪農莊走走路線</h2><p>→往志佳陽大山登山口方向，前往今晚住宿處「希瑪農莊」，環山部落處處是盛開的紫藤花，非常漂亮<br><img src="https://1013399.github.io/image-1/5/1209591267_l.jpg" alt="IMG_2740-57.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→約步行20分鐘後(1.1Km)，到達希瑪農莊<br><img src="https://1013399.github.io/image-1/5/1209591268_l.jpg" alt="IMG_2742.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→希瑪農莊，位於四季蘭溪吊橋旁<br><img src="https://1013399.github.io/image-1/5/1209591568_l.jpg" alt="IMG_2743-44.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→分配房間後，過吊橋閒逛<br><img src="https://1013399.github.io/image-1/5/1209591368_l.jpg" alt="IMG_2745.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→吊橋下司界蘭溪，原名四季郎溪，也稱蘇七蘭溪，發源於雪山<br><img src="https://1013399.github.io/image-1/5/1209590583_l.jpg" alt="IMG_2746.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→過吊橋後，即為志佳陽大山登山口<br><img src="https://1013399.github.io/image-1/5/1209590671_l.jpg" alt="IMG_2747.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>→逛完原路回，享受希瑪農莊準備的豐盛晚餐<br><img src="https://1013399.github.io/image-1/5/1209590672_l.jpg" alt="IMG_2748-49.JPG - 台中和平志良節山、平岩山" loading="lazy"></p><p>後記：<br>     晚上開始下起雨來，隔日清晨起床，雨勢依舊未歇，用完早餐後，大家決定取消今日馬武霸山行程，於探訪馬武霸山登山口後，就此賦歸。</p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',43),_={href:"https://map.happyman.idv.tw/",target:"_blank",rel:"noopener noreferrer"},m=a("br",null,null,-1),b=a("img",{src:"https://1013399.github.io/image-1/5/1209590584_l.jpg",alt:"志良節山、平岩山-地圖產生器.JPG - 台中和平志良節山、平岩山",loading:"lazy"},null,-1);function G(u,y){const t=p("ExternalLinkIcon");return e(),r("div",null,[n,d,g(" more "),c,a("p",null,[i("取自地圖產生器→"),a("a",_,[i("連結網址"),h(t)]),m,b])])}const z=l(s,[["render",G],["__file","post-5-2019-04-30.html.vue"]]);export{z as default};
