import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as e,a as r,b as i,e as a,d as o,f as s}from"./app-e9e80f6e.js";const h={},n=i("p",null,[i("strong",null,"冬瓜山步道"),a("，位於苗栗縣泰安鄉，步道由枕木階梯及傳統山徑串連，全長1.6公里，途中林縫可眺望虎山、上島山、佳里山等，走在原始森林間，涼爽舒適，步道終點有一棵千年櫸木(冬瓜山神木)，神木樹形高大，依然生氣蓬勃、綠意盎然，該步道為林務局推薦的100條步道之一。")],-1),m=s('<p>走走日期：2016/04/03</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>中山高下苗栗交流道往苗栗方向，左轉台72快速道路往汶水，接台3線後右轉不過汶水橋，立即取左沿溪畔道路往泰安，接上苗62鄉道左轉，經過清安豆腐街續行苗62鄉道，至錦水溫泉飯店叉路往右後轉，過龍安橋後左彎，於騰龍溫泉山莊叉路，取右前水泥路(路口有打比厝溪健行步道指標)續行狹窄產道，經過南大窩橋後到達露營區農場停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→農場整裝<br><img src="https://1013399.github.io/image-1/51/1122060171_l.jpg" alt="IMGP9093.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→出發，續行水泥路往上<br><img src="https://1013399.github.io/image-1/51/1122061872_l.jpg" alt="IMGP9094.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→經過噴水池<br><img src="https://1013399.github.io/image-1/51/1122062069_l.jpg" alt="IMGP9094A.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→冬瓜山露營區<br><img src="https://1013399.github.io/image-1/51/1122061079_l.jpg" alt="IMGP9094B.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→冬瓜山步道口，旁有步道指標及0K里程樁<br><img src="https://1013399.github.io/image-1/51/1122059778_l.jpg" alt="IMGP9095AB.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→進入佈滿野草原始山徑<br><img src="https://1013399.github.io/image-1/51/1122061572_l.jpg" alt="IMGP9095C.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→進入平緩山徑<br><img src="https://1013399.github.io/image-1/51/1122060470_l.jpg" alt="IMGP9096.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→接枕木步道，開始陡上<br><img src="https://1013399.github.io/image-1/51/1122061674_l.jpg" alt="IMGP9097-01.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→路旁有棵大櫸木<br><img src="https://1013399.github.io/image-1/51/1122060975_l.jpg" alt="IMGP9102.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→繼續陡上<br><img src="https://1013399.github.io/image-1/51/1122060173_l.jpg" alt="IMGP9103-04.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→經過大岩壁<br><img src="https://1013399.github.io/image-1/51/1122061875_l.jpg" alt="IMGP9105-06.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→過了大岩壁後崩塌處，拉繩小心通過<br><img src="https://1013399.github.io/image-1/51/1122061378_l.jpg" alt="IMGP9107.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→崩塌處展望<br><img src="https://1013399.github.io/image-1/51/1122060174_l.jpg" alt="IMGP9108.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→陡上山徑<br><img src="https://1013399.github.io/image-1/51/1122060880_l.jpg" alt="IMGP9109.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→眺望上島山<br><img src="https://1013399.github.io/image-1/51/1122061877_l.jpg" alt="IMGP9111.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→0.9K里程樁(未見其他里程樁)<br><img src="https://1013399.github.io/image-1/51/1122062070_l.jpg" alt="IMGP9112.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→再繼續陡上<br><img src="https://1013399.github.io/image-1/51/1122060881_l.jpg" alt="IMGP9113.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→1K指標叉路，右下可回騰龍山莊(舊路)，取左往冬瓜山神木<br><img src="https://1013399.github.io/image-1/51/1122060882_l.jpg" alt="IMGP9114.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→休息木椅平台<br><img src="https://1013399.github.io/image-1/51/1122061878_l.jpg" alt="IMGP9115.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→土階步道續陡上<br><img src="https://1013399.github.io/image-1/51/1122061879_l.jpg" alt="IMGP9116-7.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→1.25K指標，右彎續行<br><img src="https://1013399.github.io/image-1/51/1122061381_l.jpg" alt="IMGP9118.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→坍塌碎石坡，路幅較窄，拉繩小心通過<br><img src="https://1013399.github.io/image-1/51/1122060978_l.jpg" alt="IMGP9119.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→最後陡上<br><img src="https://1013399.github.io/image-1/51/1122060473_l.jpg" alt="IMGP9120.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→到達步道終點，冬瓜山神木<br><img src="https://1013399.github.io/image-1/51/1122061175_l.jpg" alt="IMGP9121.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→冬瓜山神木，據說是台灣最大棵的櫸木，依然生氣蓬勃<br><img src="https://1013399.github.io/image-1/51/1122061574_l.jpg" alt="IMGP9122.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→續行可往洗水山(冬瓜山)約150~180分鐘，今日不去，在此休息<br><img src="https://1013399.github.io/image-1/51/1122061575_l.jpg" alt="IMGP9123.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→原路折返，回到休息木椅平台<br><img src="https://1013399.github.io/image-1/51/1122062072_l.jpg" alt="IMGP9125.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→回到1K指標叉路，取右回<br><img src="https://1013399.github.io/image-1/51/1122059977_l.jpg" alt="IMGP9126.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→回到大岩壁崩塌處<br><img src="https://1013399.github.io/image-1/51/1122060272_l.jpg" alt="IMGP9127.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→回到步道口<br><img src="https://1013399.github.io/image-1/51/1122059780_l.jpg" alt="IMGP9130.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><p>→回到停車處<br><img src="https://1013399.github.io/image-1/51/1122061880_l.jpg" alt="IMGP9135.JPG - 苗栗泰安冬瓜山步道" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約3.5小時(含休息)</p><p>高低落差：約450公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',38),b={href:"http://blog.xuite.net/laoweiz/blog/32623262",target:"_blank",rel:"noopener noreferrer"},c=i("figure",null,[i("img",{src:"https://1013399.github.io/image-1/51/1122061883_l.jpg",alt:"冬瓜山步道千年櫸木-老尉子.jpg - 苗栗泰安冬瓜山步道",tabindex:"0",loading:"lazy"}),i("figcaption",null,"冬瓜山步道千年櫸木-老尉子.jpg - 苗栗泰安冬瓜山步道")],-1);function d(G,P){const t=l("ExternalLinkIcon");return p(),e("div",null,[n,r(" more "),m,i("p",null,[a("原圖連結→"),i("a",b,[a("老尉子部落格"),o(t)])]),c])}const j=g(h,[["render",d],["__file","post-51-2016-04-13.html.vue"]]);export{j as default};
