import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as p,c as r,a as o,b as i,e as a,d as e,f as s}from"./app-272e1b70.js";const h={},n=i("p",null,[i("strong",null,"奮瑞古道"),a("，位於嘉義縣竹崎鄉和梅山鄉間，連結奮起湖和瑞里風景區，古道由石塊步道、枕木步道及產業道路銜接而成，全長約7公里，為早期先民挑擔往來通商、拓墾之路，沿途穿梭在原始林及孟宗竹林間，景緻清幽動人，為值得一遊的懷舊之旅。")],-1),m=i("p",null,[i("strong",null,"糕仔崁古道"),a("，位於嘉義縣竹崎鄉，為早年連結奮起湖與太和、瑞里間的主要通道，古道皆為砂岩打造的長方形石階，目前僅存一公里長，由於走來汗水淋漓，亦有人稱之為「汗路古道」。")],-1),c=s('<p>今日參加新竹縣健行協會活動，七部遊覽車共200餘人，原本預計由166縣道的瑞里縱走奮瑞古道至奮起湖，但領隊說瑞里地區目前禁行大型車輛，改由奮起湖(南側入口)，走到3K附近的觀落日平台後折返，回到1.5K越嶺點涼亭(陰陽廟)，再改走糕仔崁古道回奮起湖，所以今日奮瑞古道只走了南端一段，未走完全程。</p><p>走走日期：2016/02/28</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號下中埔交流道往阿里山方向，沿台18線經觸口至石棹後左轉169縣道，到達奮起湖第二停車場(台電變電所旁)下車，旁邊即為奮瑞古道南側入口。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→第二停車場，奮瑞古道南側入口<br><img src="https://1013399.github.io/image-1/52/1118059981_l.jpg" alt="IMGP9040.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→入口狹窄，排隊耗時，續行公路往上行<br><img src="https://1013399.github.io/image-1/52/1118059825_l.jpg" alt="IMGP9041.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→由此缺口上切<br><img src="https://1013399.github.io/image-1/52/1118058747_l.jpg" alt="IMGP9042.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→接回古道，取右行<br><img src="https://1013399.github.io/image-1/52/1118057661_l.jpg" alt="IMGP9043.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→叉路，右為奮起步道，取左續行<br><img src="https://1013399.github.io/image-1/52/1118058749_l.jpg" alt="IMGP9044.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→開始陡上，杉木林後轉竹林，經過0.5K里程柱<br><img src="https://1013399.github.io/image-1/52/1118057452_l.jpg" alt="IMGP9045-48.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→轉之字型陡上，經過1K里程柱<br><img src="https://1013399.github.io/image-1/52/1118059832_l.jpg" alt="IMGP9050-53.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→下切後進入幽雅的竹林<br><img src="https://1013399.github.io/image-1/52/1118059540_l.jpg" alt="IMGP9054-58.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→越嶺點，為竹崎鄉與梅山鄉交界，有座涼亭及祀奉土地公和地基主的陰陽廟<br>   此處也是叉路，右下為糕仔崁古道，可回奮起湖<br>   左後可往光崙山，涼亭後方可往大凍山西峰，在此休息、午餐<br><img src="https://1013399.github.io/image-1/52/1118057454_l.jpg" alt="IMGP9059.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→涼亭旁設有奮瑞、瑞太、長山步道路線圖及糕仔崁古道解說牌<br><img src="https://1013399.github.io/image-1/52/1118148444_l.jpg" alt="IMGP9081-82.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→餐畢續行，接上水泥路<br><img src="https://1013399.github.io/image-1/52/1118056646_l.jpg" alt="IMGP9060.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→往前幾步，便經過1.5K里程柱<br><img src="https://1013399.github.io/image-1/52/1118057807_l.jpg" alt="IMGP9061.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→經過塔山眺望點，今日灰濛濛，看不清楚<br><img src="https://1013399.github.io/image-1/52/1118060282_l.jpg" alt="IMGP9062.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→途中展望<br><img src="https://1013399.github.io/image-1/52/1118060283_l.jpg" alt="IMGP9064.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→叉路，直行產道往公路，取左上行，離開產道，經過2K里程柱<br><img src="https://1013399.github.io/image-1/52/1118060585_l.jpg" alt="IMGP9065-68.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→再接上產道，為四叉路，往前續行<br><img src="https://1013399.github.io/image-1/52/1118061069_l.jpg" alt="IMGP9069.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→經過2.5K里程柱<br><img src="https://1013399.github.io/image-1/52/1118059895_l.jpg" alt="IMGP9071.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→經過鐵皮工寮<br><img src="https://1013399.github.io/image-1/52/1118060680_l.jpg" alt="IMGP9072.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→經過大凍山眺望點<br><img src="https://1013399.github.io/image-1/52/1118059562_l.jpg" alt="IMGP9073.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→俯瞰瑞里地區<br><img src="https://1013399.github.io/image-1/52/1118060872_l.jpg" alt="IMGP9075.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→叉路，約在3K附近(未見里程柱)，直行為奮瑞古道往瑞里，取左往觀日落平台<br><img src="https://1013399.github.io/image-1/52/1118060774_l.jpg" alt="IMGP9077.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→觀日落平台，此行終點，一片霧茫茫，無視野，休息後原路折返<br><img src="https://1013399.github.io/image-1/52/1118059650_l.jpg" alt="IMGP9078.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→回到越嶺點涼亭<br><img src="https://1013399.github.io/image-1/52/1118059345_l.jpg" alt="IMGP9080.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→休息後，取直行進入糕仔崁古道<br><img src="https://1013399.github.io/image-1/52/1118059125_l.jpg" alt="IMGP9083.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→糕仔崁古道全程石階，步道濕滑，一路陡下<br><img src="https://1013399.github.io/image-1/52/1118060683_l.jpg" alt="IMGP9084-87.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→接上奮起步道，取右續行<br><img src="https://1013399.github.io/image-1/52/1118060589_l.jpg" alt="IMGP9088.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→奮起步道沿著公路平行而建<br><img src="https://1013399.github.io/image-1/52/1118060686_l.jpg" alt="IMGP9089.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→悶材窯遺址<br><img src="https://1013399.github.io/image-1/52/1118060387_l.jpg" alt="IMGP9090.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><p>→不再走奮起步道，下切公路回奮起湖逛老街<br><img src="https://1013399.github.io/image-1/52/1118060780_l.jpg" alt="IMGP9092.JPG - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：2小時50分鐘(含休息、午餐)</p><p>高低落差：約200公尺(不含上下起伏)</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',37),b={href:"http://recreation.forest.gov.tw/RT/RT_2_1.aspx?TR_ID=099",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-1/52/1118059135_l.jpg",alt:"奮瑞古道-台灣山林悠遊網.jpg - 嘉義竹崎奮瑞古道(南段)、糕仔崁古道",loading:"lazy"},null,-1);function G(P,u){const t=g("ExternalLinkIcon");return p(),r("div",null,[n,m,o(" more "),c,i("p",null,[a("原圖連結→"),i("a",b,[a("台灣山林悠遊網"),e(t)]),d,_])])}const y=l(h,[["render",G],["__file","post-52-2016-03-09.html.vue"]]);export{y as default};
