import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as p,c as e,a as o,b as i,f as a,d as r,e as s}from"./app-a7b0d29a.js";const h={},n=i("p",null,[i("strong",null,"忘憂森林"),a("，位於南投縣鹿谷鄉，名為「草坔」的地方，靠近杉林溪，原為海拔約兩千公尺的一座原始森林，因921大地震後而形成的淤積沼澤，因杉木林長期泡水而枯死，形成特異景觀，終日霧氣迷濛，又稱為迷霧森林。")],-1),m=i("p",null,"忘憂森林曾經造訪過二次，都是由仁淵茶行走水泥路上山，今日參加公司活動，由留龍頭起登，再下至仁淵茶行，體驗不同路線，惟今日的忘憂森林，已乾涸見底，似乎缺了一份迷濛之美。",-1),c=s('<p>走走日期：2015/12/05</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>搭乘遊覽車從新竹出發，上國道1號轉國道3號，國道3號下竹山交流道，接集山路二段右轉(台3線)往竹山，於竹山轉151縣道往鹿谷、溪頭方向，經過溪頭續行杉林溪公路(0Km)往杉林溪，經過12生肖彎、龍鳳峽後，續行至杉林溪公路13.2km留龍頭下車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→留龍頭，下車，整裝後出發<br><img src="https://1013399.github.io/image-1/56/1108622965_l.jpg" alt="IMGP8867.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→旁有觀景平台，天氣晴朗時，可眺望鳳凰山稜線<br><img src="https://1013399.github.io/image-1/56/1108619631_l.jpg" alt="IMGP8868.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→登山口，進入山徑<br><img src="https://1013399.github.io/image-1/56/1108619021_l.jpg" alt="IMGP8869.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→約行十公尺，經過護管所<br><img src="https://1013399.github.io/image-1/56/1108620697_l.jpg" alt="IMGP8870.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→在樹林間一路陡上<br><img src="https://1013399.github.io/image-1/56/1108622861_l.jpg" alt="IMGP8872-75.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→走出樹林至茶園，旁有江家茶園立牌<br><img src="https://1013399.github.io/image-1/56/1108620501_l.jpg" alt="IMGP8876.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→沿茶園旁山徑陡上，再進入樹林<br><img src="https://1013399.github.io/image-1/56/1108619633_l.jpg" alt="IMGP8877-80.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→再度走出樹林至茶園<br><img src="https://1013399.github.io/image-1/56/1108621477_l.jpg" alt="IMGP8881.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→仍沿著茶園旁山徑陡上<br><img src="https://1013399.github.io/image-1/56/1108621777_l.jpg" alt="IMGP8882.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→茶園山徑，沿途展望佳<br><img src="https://1013399.github.io/image-1/56/1108620439_l.jpg" alt="IMGP8884.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→經過工寮<br><img src="https://1013399.github.io/image-1/56/1108622567_l.jpg" alt="IMGP8886.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→拉繩陡上<br><img src="https://1013399.github.io/image-1/56/1108620440_l.jpg" alt="IMGP8887.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→接上產業道路，右往產道底茶園(可賞景)，休息等候山友<br><img src="https://1013399.github.io/image-1/56/1108621779_l.jpg" alt="IMGP8888.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→續行，取左下坡<br><img src="https://1013399.github.io/image-1/56/1108619024_l.jpg" alt="IMGP8889.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→叉路，左往嶺頭山，取右行<br><img src="https://1013399.github.io/image-1/56/1108621479_l.jpg" alt="IMGP8890.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→叉路，續直行<br><img src="https://1013399.github.io/image-1/56/1108623148_l.jpg" alt="IMGP8891.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→叉路，右下往杉林溪公路、仁淵茶行，取左續行<br><img src="https://1013399.github.io/image-1/56/1108621780_l.jpg" alt="IMGP8892.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→忘憂森林咖啡屋，取左茶園山徑<br><img src="https://1013399.github.io/image-1/56/1108622568_l.jpg" alt="IMGP8893.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→忘憂森林入口，左側有小水塘<br><img src="https://1013399.github.io/image-1/56/1108620698_l.jpg" alt="IMGP8894.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→上鐵梯<br><img src="https://1013399.github.io/image-1/56/1108622863_l.jpg" alt="IMGP8895.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→到達忘憂森林，乾涸見底，在此休息、午餐<br><img src="https://1013399.github.io/image-1/56/1108619228_l.jpg" alt="IMGP8896.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"><br><img src="https://1013399.github.io/image-1/56/1108622864_l.jpg" alt="IMGP8897.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→餐畢，原路回，回到忘憂森林咖啡屋，取右回<br><img src="https://1013399.github.io/image-1/56/1108622769_l.jpg" alt="IMGP8898.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→回到前一叉路，取左產業道路往仁淵茶行<br> →沿途有不少鳳仙花<br><img src="https://1013399.github.io/image-1/56/1108621781_l.jpg" alt="IMGP8901.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→沿途展望也不錯<br><img src="https://1013399.github.io/image-1/56/1108623150_l.jpg" alt="IMGP8902.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→到達杉林溪公路(仁淵茶行)<br><img src="https://1013399.github.io/image-1/56/1108621201_l.jpg" alt="IMGP8903.JPG - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)" loading="lazy"></p><p>→搭乘遊覽車前往羊彎，再去走走銀杏森林</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2小時(含休息、午餐)</p><p>高低落差：約240公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',33),d={href:"http://blog.xuite.net/laoweiz/blog/61744927",target:"_blank",rel:"noopener noreferrer"},b=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-1/56/1108621870_l.jpg",alt:"留龍頭、嶺頭山、忘憂森林-老尉子(路線).jpg - 南投鹿谷忘憂森林(留龍頭上、下仁淵茶行)",loading:"lazy"},null,-1);function G(P,u){const t=g("ExternalLinkIcon");return p(),e("div",null,[n,m,o(" more "),c,i("p",null,[a("原圖連結→"),i("a",d,[a("老尉子的部落格"),r(t)]),b,_])])}const I=l(h,[["render",G],["__file","post-56-2015-12-10.html.vue"]]);export{I as default};
