import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as p,c as r,a as e,b as i,e as a,d as o,f as s}from"./app-272e1b70.js";const h={},n=i("p",null,[i("strong",null,"馬武督探索森林"),a("，位於新竹縣關西鎮，區裡面遍佈柳杉林、林相原始，充滿清新自然的芬多精，主要景點有綠光小學、虹橋瀑布、馬武督瀑布、舞楓池、楊梅阿嬤、楊梅阿公等，園區設有元氣步道、楊梅步道、竹林步道及桐花步道串聯各景點，另外設有烤肉區，提供適合閤家大小同樂的遊憩空間。")],-1),m=s('<p>今日參加公司烤肉活動，員工及眷屬共約400人，加上當天遊客眾多，讓馬武督探索森林顯得熱鬧非凡，趁時間尚早，先走走森林步道，繞一圈僅需一個多小時。</p><p>走走日期：2015/10/04</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號下關西交流道，取左沿118縣道往關西方向行駛，進入市區過台3線後續往羅孚行駛(羅馬公路)，至118縣道37.6Km右轉進入，約行1.2Km至收費站，再續行1.9Km至第一停車場停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→第一停車場，出發往遊客中心方向<br><img src="https://1013399.github.io/image-2/62/1100715373_l.jpg" alt="IMGP8378.JPG - 馬武督探索森林" loading="lazy"></p><p>→遊客中心，前方為元氣步道入口，道路右側為烤肉區<br><img src="https://1013399.github.io/image-2/62/1100715946_l.jpg" alt="IMGP8379.JPG - 馬武督探索森林" loading="lazy"></p><p>→元氣步道入口<br><img src="https://1013399.github.io/image-2/62/1100713692_l.jpg" alt="IMGP8380.JPG - 馬武督探索森林" loading="lazy"></p><p>→綠光小學，三立電視台曾在此拍攝連續劇「綠光．森林」<br><img src="https://1013399.github.io/image-2/62/1100713197_l.jpg" alt="IMGP8383.JPG - 馬武督探索森林" loading="lazy"></p><p>→進入教室參觀<br><img src="https://1013399.github.io/image-2/62/1100715845_l.jpg" alt="IMGP8384-85.JPG - 馬武督探索森林" loading="lazy"></p><p>→續行元氣步道，平緩好走<br><img src="https://1013399.github.io/image-2/62/1100714474_l.jpg" alt="IMGP8386-89.JPG - 馬武督探索森林" loading="lazy"></p><p>→虹橋瀑布<br><img src="https://1013399.github.io/image-2/62/1100714475_l.jpg" alt="IMGP8390.JPG - 馬武督探索森林" loading="lazy"></p><p>→瀑布是由大水管引水形成的<br><img src="https://1013399.github.io/image-2/62/1100713895_l.jpg" alt="IMGP8391.JPG - 馬武督探索森林" loading="lazy"></p><p>→叉路，直行不知去向且封閉中，取左行<br><img src="https://1013399.github.io/image-2/62/1100713311_l.jpg" alt="IMGP8392.JPG - 馬武督探索森林" loading="lazy"></p><p>→之字型陡上<br><img src="https://1013399.github.io/image-2/62/1100714664_l.jpg" alt="IMGP8393-94.JPG - 馬武督探索森林" loading="lazy"></p><p>→叉路，左往舞楓池，取右往馬武督瀑布<br><img src="https://1013399.github.io/image-2/62/1100713199_l.jpg" alt="IMGP8395.JPG - 馬武督探索森林" loading="lazy"></p><p>→休息涼亭，後有廁所<br><img src="https://1013399.github.io/image-2/62/1100715654_l.jpg" alt="IMGP8396.JPG - 馬武督探索森林" loading="lazy"></p><p>→馬武督瀑布，右側有山徑可往外鳥嘴山，取左過橋<br><img src="https://1013399.github.io/image-2/62/1100713027_l.jpg" alt="IMGP8397.JPG - 馬武督探索森林" loading="lazy"></p><p>→上至高點後，開始陡下<br><img src="https://1013399.github.io/image-2/62/1100713029_l.jpg" alt="IMGP8400.JPG - 馬武督探索森林" loading="lazy"></p><p>→賞鳥亭<br><img src="https://1013399.github.io/image-2/62/1100713411_l.jpg" alt="IMGP8402.JPG - 馬武督探索森林" loading="lazy"></p><p>→舞楓池，因秋冬紅色楓葉落入池中隨波舞動而得名<br>   左後可回遊客中心，取右下<br><img src="https://1013399.github.io/image-2/62/1100714759_l.jpg" alt="IMGP8403-04.JPG - 馬武督探索森林" loading="lazy"></p><p>→叉路，右上接竹林步道(不經過神木)，續直行往楊梅神木<br><img src="https://1013399.github.io/image-2/62/1100716046_l.jpg" alt="IMGP8405.JPG - 馬武督探索森林" loading="lazy"></p><p>→前方觀景台，旁邊即為楊梅阿嬤<br><img src="https://1013399.github.io/image-2/62/1100714189_l.jpg" alt="IMGP8406.JPG - 馬武督探索森林" loading="lazy"></p><p>→楊梅阿嬤，樹齡在500年以上，林務局列管第23號<br><img src="https://1013399.github.io/image-2/62/1100714190_l.jpg" alt="IMGP8407.JPG - 馬武督探索森林" loading="lazy"></p><p>→觀景台展望佳，俯瞰第一停車場，休息後續行<br><img src="https://1013399.github.io/image-2/62/1100714287_l.jpg" alt="IMGP8409.JPG - 馬武督探索森林" loading="lazy"></p><p>→楊梅阿公，樹齡近300年，林務局列管第22號<br><img src="https://1013399.github.io/image-2/62/1100715952_l.jpg" alt="IMGP8411.JPG - 馬武督探索森林" loading="lazy"></p><p>→樹抱石<br><img src="https://1013399.github.io/image-2/62/1100714080_l.jpg" alt="IMGP8412.JPG - 馬武督探索森林" loading="lazy"></p><p>→接上竹林步道，右可回楓舞池，取左回停車場<br><img src="https://1013399.github.io/image-2/62/1100715377_l.jpg" alt="IMGP8413.JPG - 馬武督探索森林" loading="lazy"></p><p>→竹林步道為寬大的土石產道<br><img src="https://1013399.github.io/image-2/62/1100714478_l.jpg" alt="IMGP8414.JPG - 馬武督探索森林" loading="lazy"></p><p>→叉路，直行往第二停車場，左下為桐花步道<br><img src="https://1013399.github.io/image-2/62/1100715953_l.jpg" alt="IMGP8415.JPG - 馬武督探索森林" loading="lazy"></p><p>→取左桐花步道回第一停車場<br><img src="https://1013399.github.io/image-2/62/1100714082_l.jpg" alt="IMGP8416.JPG - 馬武督探索森林" loading="lazy"></p><p>→叉路，取右下<br><img src="https://1013399.github.io/image-2/62/1100715167_l.jpg" alt="IMGP8417.JPG - 馬武督探索森林" loading="lazy"></p><p>→接柏油路，第一停車場就在左邊，右往第二停車場，取左<br><img src="https://1013399.github.io/image-2/62/1100713125_l.jpg" alt="IMGP8418.JPG - 馬武督探索森林" loading="lazy"></p><p>→回到第一停車場，更衣後前往烤肉區參加烤肉活動<br><img src="https://1013399.github.io/image-2/62/1100714191_l.jpg" alt="IMGP8419.JPG - 馬武督探索森林" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約80分鐘(含休息)</p><p>高低落差：約120公尺(不含上下起伏)</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',37),c={href:"http://www.discovery-forest.com.tw/discovery/equipment.htm",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),b=i("img",{src:"https://1013399.github.io/image-2/62/1100716243_l.jpg",alt:"馬武督探索森林",loading:"lazy"},null,-1);function G(P,_){const t=g("ExternalLinkIcon");return p(),r("div",null,[n,e(" more "),m,i("p",null,[a("原圖連結→"),i("a",c,[a("馬武督探索森林"),o(t)]),d,b])])}const I=l(h,[["render",G],["__file","post-62-2015-10-07.html.vue"]]);export{I as default};
