import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as r,c as o,a as e,b as i,f as a,d as g,e as s}from"./app-7f62a6ff.js";const h={},n=i("p",null,[i("strong",null,"阿塱壹古道"),a("，或稱阿朗壹古道，位於台東縣達仁鄉南田村與屏東縣牡丹鄉旭海村之間，全長約8.4Km，屬於「瑯嶠卑南古道」中的一段，為昔日各族群往來恆春半島及台東間重要通道，沿途沒有消波塊、防波堤等人工建築，景觀自然原始，在環保團體的保護下，台26線省道「安朔」至「旭海」段在此中斷，與「港仔」至「佳樂水」段成為台灣環島公路網的缺口，目前列為「旭海觀音鼻自然保留區」。")],-1),m=i("p",null,"進入阿塱壹古道(旭海觀音鼻自然保留區)，需先申請核可，並由屏東縣政府培訓之環境解說員帶領進入，每20人(及以下)需配置1位解說員(每日/每次費用3,000元)，申請辦法說明：",-1),b={href:"http://175.99.86.233/syuhai/index.php?page=apply",target:"_blank",rel:"noopener noreferrer"},G=s('<p>走走日期：2015/09/07</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>台東市出發走台9線南下，先在台9線443.2Km達仁鄉圖書館(台9線與台26線會合處)接解說員上車，再轉台26線南下，於台26線88.6Km柏油路終點下車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→出發，進入往阿塱壹古道的土石路，今日行程是由北往南走<br><img src="https://1013399.github.io/image-2/63/1097437781_l.jpg" alt="IMGP8312.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→路旁有塊座標牌，此處屬台東縣達仁鄉南田村<br><img src="https://1013399.github.io/image-2/63/1097438948_l.jpg" alt="IMGP8313.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→塔瓦溪出海口<br><img src="https://1013399.github.io/image-2/63/1097436815_l.jpg" alt="IMGP8314.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→踩著漂流木過溪<br><img src="https://1013399.github.io/image-2/63/1097440952_l.jpg" alt="IMGP8315.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→過溪後也有塊座標牌，這裡是台東縣與屏東縣交界<br><img src="https://1013399.github.io/image-2/63/1097435423_l.jpg" alt="IMGP8316.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→叉路，左右均可，取左續行<br><img src="https://1013399.github.io/image-2/63/1097439242_l.jpg" alt="IMGP8317.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→進入椰子林，經過幾戶民宅，未見檢查哨<br><img src="https://1013399.github.io/image-2/63/1097440745_l.jpg" alt="IMGP8318.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→進入扁平圓石(南田石)海灘<br><img src="https://1013399.github.io/image-2/63/1097438451_l.jpg" alt="IMGP8319-23.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→觀音鼻北口前亂石堆<br><img src="https://1013399.github.io/image-2/63/1097437194_l.jpg" alt="IMGP8324.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→古道高遶觀音鼻，拉繩陡上<br><img src="https://1013399.github.io/image-2/63/1097437876_l.jpg" alt="IMGP8325-31.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→進入小樹林<br><img src="https://1013399.github.io/image-2/63/1097439754_l.jpg" alt="IMGP8333.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→繼續陡上<br><img src="https://1013399.github.io/image-2/63/1097438766_l.jpg" alt="IMGP8334.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→到達觀音鼻最高點<br><img src="https://1013399.github.io/image-2/63/1097436415_l.jpg" alt="IMGP8336.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→旁邊座標牌寫著觀音鼻山南田段<br><img src="https://1013399.github.io/image-2/63/1097438768_l.jpg" alt="IMGP8337.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→回首俯瞰海灘及太平洋<br><img src="https://1013399.github.io/image-2/63/1097441143_l.jpg" alt="IMGP8337A.jpg - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→續行，拉繩下陡坡<br><img src="https://1013399.github.io/image-2/63/1097436589_l.jpg" alt="IMGP8338.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→進入雜草區，坡度趨緩<br><img src="https://1013399.github.io/image-2/63/1097438567_l.jpg" alt="IMGP8339.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→進入樹林<br><img src="https://1013399.github.io/image-2/63/1097438081_l.jpg" alt="IMGP8340.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→於林中大空地休息<br><img src="https://1013399.github.io/image-2/63/1097436590_l.jpg" alt="IMGP8341.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→此處座標牌寫著觀音山平台<br><img src="https://1013399.github.io/image-2/63/1097440450_l.jpg" alt="IMGP8342.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→休息後續行<br><img src="https://1013399.github.io/image-2/63/1097435425_l.jpg" alt="IMGP8344.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→陡下觀音鼻南口<br><img src="https://1013399.github.io/image-2/63/1097436593_l.jpg" alt="IMGP8345-46.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→下至沙灘海岸<br><img src="https://1013399.github.io/image-2/63/1097438454_l.jpg" alt="IMGP8347.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→旁邊座標牌寫著觀音鼻山旭海段(好漢坡)<br><img src="https://1013399.github.io/image-2/63/1097438455_l.jpg" alt="IMGP8348.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→在此休息，拍張大合照<br><img src="https://1013399.github.io/image-2/63/1097441144_l.jpg" alt="IMGP8349.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→再出發<br><img src="https://1013399.github.io/image-2/63/1097437964_l.jpg" alt="IMGP8350.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→軍艦岩<br><img src="https://1013399.github.io/image-2/63/1097437965_l.jpg" alt="IMGP8352.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→再度行走在扁平圓石上，旁邊許多漂流木<br><img src="https://1013399.github.io/image-2/63/1097439244_l.jpg" alt="IMGP8354.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→如來掌礁岩<br><img src="https://1013399.github.io/image-2/63/1097438949_l.jpg" alt="IMGP8355-56.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→拳頭石<br><img src="https://1013399.github.io/image-2/63/1097437196_l.jpg" alt="IMGP8358.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→越過巨石堆<br><img src="https://1013399.github.io/image-2/63/1097435426_l.jpg" alt="IMGP8359.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→進入細沙沙灘，今日也有許多遊客由南往北走<br><img src="https://1013399.github.io/image-2/63/1097439245_l.jpg" alt="IMGP8360.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→取右離開沙灘接上土路<br><img src="https://1013399.github.io/image-2/63/1097441146_l.jpg" alt="IMGP8362.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→沿途都是林投樹<br><img src="https://1013399.github.io/image-2/63/1097440552_l.jpg" alt="IMGP8363.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→經過廢棄紅磚屋<br><img src="https://1013399.github.io/image-2/63/1097438082_l.jpg" alt="IMGP8364.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→過小溪<br><img src="https://1013399.github.io/image-2/63/1097439346_l.jpg" alt="IMGP8365.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→又再回到海灘<br><img src="https://1013399.github.io/image-2/63/1097436937_l.jpg" alt="IMGP8367.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→經過大石壁<br><img src="https://1013399.github.io/image-2/63/1097439349_l.jpg" alt="IMGP8368.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→旭海檢查哨，檢查員核對人數後放行<br><img src="https://1013399.github.io/image-2/63/1097440954_l.jpg" alt="IMGP8369.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→接上水泥路<br><img src="https://1013399.github.io/image-2/63/1097437199_l.jpg" alt="IMGP8370.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→到達旭海安檢所，阿塱壹古道挑戰成功，讚<br><img src="https://1013399.github.io/image-2/63/1097436595_l.jpg" alt="IMGP8371.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→旭海漁港安檢所，休息後續行<br><img src="https://1013399.github.io/image-2/63/1097435815_l.jpg" alt="IMGP8372.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→叉路，右往旭海大草原入口，取左續行<br><img src="https://1013399.github.io/image-2/63/1097436596_l.jpg" alt="IMGP8373.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→叉路，直行可接199甲縣道與26線，取左花崗石步道往魚港停車場<br><img src="https://1013399.github.io/image-2/63/1097435427_l.jpg" alt="IMGP8375.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→經過圖騰木橋<br><img src="https://1013399.github.io/image-2/63/1097441056_l.jpg" alt="IMGP8376.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><p>→到達旭海魚港停車場，約在台26線79.8Km處，梳洗更衣後上車<br>   (今日從台26線88.6Km走到台26線79.8Km，約走了8.8Km)<br><img src="https://1013399.github.io/image-2/63/1097441058_l.jpg" alt="IMGP8377.JPG - 屏東牡丹阿塱壹古道" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約4小時(含休息)</p><p>高低落差：約120公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',53),P={href:"http://blog.xuite.net/laoweiz/blog/68958169",target:"_blank",rel:"noopener noreferrer"},c=i("br",null,null,-1),d=i("img",{src:"https://1013399.github.io/image-2/63/1097440262_l.jpg",alt:"阿朗壹.jpg - 屏東牡丹阿塱壹古道",loading:"lazy"},null,-1);function _(u,j){const t=p("ExternalLinkIcon");return r(),o("div",null,[n,e(" more "),m,i("p",null,[i("a",b,[a("屏東縣政府-觀光旅遊"),g(t)]),a("。")]),G,i("p",null,[a("原圖連結→"),i("a",P,[a("老尉子的部落格"),g(t)]),c,d])])}const I=l(h,[["render",_],["__file","post-63-2015-09-15.html.vue"]]);export{I as default};
